public void jumpRef()
{
    Args            args;
    MenuFunction            menuFunction;
    OMOperatingUnit omOperatingUnitLocal;

    //super();

    switch (projFundingSource.FundingType)
    {
        case ProjFundingType::Grant :
            projFundingSource_ds.object(fieldNum(ProjFundingSource, ProjGrant)).jumpRef();
            break;

        case ProjFundingType::Customer :
            projFundingSource_ds.object(fieldNum(ProjFundingSource, CustAccount)).jumpRef();
            break;

        case ProjFundingType::Organization :
        case ProjFundingType::OnHoldFundingSource :
            args = new Args();
            args.caller(element);
            omOperatingUnitLocal = OMOperatingUnit::findName(editProjFunder.text(), OMOperatingUnitType::OMBusinessUnit);
            menuFunction = new MenuFunction(menuitemDisplayStr(OMBusinessUnit), MenuItemType::Display);
            menuFunction.run(args);
    }
}

