/// <summary>
/// Sets the editable property of various datasource fields for the form.
/// </summary>
void enableFieldsActive()
{
    ProjTable           projTable;
    ProjHourCostPrice   projHourCostPrice;

    projTable = ProjTable::find(projJournalTrans.ProjId);

    if (ProjInvoiceTable::isSalesCurrencyLocked(projTable.ProjInvoiceProjId))
    {
        projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).allowEdit(false);
        projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).skip(true);
    }
    else
    {
        projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).allowEdit(true);
        projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).skip(false);
    }
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CostPrice)).enabled(true);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CostPrice)).skip(false);

    if (projTable                                                           &&
        ResourceFacade::useResourceRate(ProjJournalTrans.Resource))
    {
        projHourCostPrice = ProjHourCostPrice::findResourceRate(ProjJournalTrans.Resource, ProjJournalTrans.TransDate);
        if (projHourCostPrice.IsResourceRate)
        {
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CostPrice)).enabled(false);
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CostPrice)).skip(true);
        }
    }

    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ReverseDate)).allowEdit(ProjJournalTrans.ReverseEntry);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ReverseDate)).skip(ProjJournalTrans.ReverseEntry);
}

void init()
{
    // Registration of timesheet/qty according to from & to time
    ProjTimeRegistration    timeRegistrationVisible;
    ProjTimeRegistration    timeRegistrationEnable;
    ProjJournalNameId       journalNameId;
    // Registration of timesheet/qty according to from & to time

    journalFormTrans    = new ProjJournalFormTrans_Hour(element);

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        isIndiaIndirectTaxParameterMarked   = TaxParameters::checkTaxParameters_IN();
        //isIndiaWHTMarked                    = TaxWithholdParameters_IN::checkTaxParameters();
        isProjJournalTrans_INEnabled        = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(ProjJournalTransTaxExtensionIN));
    }
    // </GIN>

    super();

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    cdsLockingOptionHourJournals = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::HourJournals);

    journalFormTrans.parmCtrlCheckJournal(checkJournal);
    journalFormTrans.parmCtrlPostJournal(postJournal);
    journalFormTrans.parmCtrlInfolog(showInfolog);
    journalFormTrans.parmCtrlVoucherGrid(projJournalTrans_Voucher);
    journalFormTrans.parmCtrlVoucherTab(voucher);
    journalFormTrans.parmCtrlLineLogImage(lineLogImage);

    journalFormTrans.formInitPost();

    // Registration of timesheet/qty according to from & to time
    journalNameId               = journalFormTrans.getJournalFormTable().getJournalFormTable().
                                                   journalTableData().journalTable().JournalNameId;

    timeRegistrationVisible     = ProjParameters::find().TimeRegistration;
    timeRegistrationEnable      = ProjJournalName::find(journalNameId).TimeRegistration;

    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, FromTime)).visible(timeRegistrationVisible);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ToTime)).visible(timeRegistrationVisible);

    timeSheet.visible(timeRegistrationVisible);
    timeSheet_FromTime.visible(timeRegistrationVisible);
    timeSheet_ToTime.visible(timeRegistrationVisible);

    timeSheet_FromTime.allowEdit(timeRegistrationEnable);
    timeSheet_ToTime.allowEdit(timeRegistrationEnable);

    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, FromTime)).allowEdit(timeRegistrationEnable);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, FromTime)).skip(!timeRegistrationEnable);

    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ToTime)).allowEdit(timeRegistrationEnable);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ToTime)).skip(!timeRegistrationEnable);

   // Registration of timesheet/qty according to from & to time
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, Qty)).allowEdit(! timeRegistrationEnable || ! timeRegistrationVisible);
    projJournalTrans_ds.object(fieldNum(ProjJournalTrans, Qty)).skip(timeRegistrationEnable && timeRegistrationVisible);

    projValParameter = ProjParameters::find().ValidationEmplCategory;

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        Tax.visible(false);
        Tax_TaxGroupId.visible(false);
        Tax_TaxItemGroupId.visible(false);
    }
    // </GTE>

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
    }
    // </GIN>

    // Get the number of lines and project quantity from ProjJournalTable.
    journalTableNumOfLines = journalFormTrans.displayJournalNumOfLines(ProjJournalTrans);
    journalTableQty = journalFormTrans.displayJournalprojQty(ProjJournalTrans);

    if (element.args().dataset() == tableNum(ProjJournalTable))
    {
        ProjJournalTable projJournalTable = element.args().record();
        if (!projJournalTable.isEditAllowedByJournalApproval())
        {
            isReadOnly = true;
        }
    }
}

/// <summary>
/// If the total transaction number of lines or sum of quantity is not the same as the prevously stored value,
/// then update ProjJournalTable.
/// </summary>
private void updateJournalTable()
{
    ProjJournalTable projJournalTable = ProjJournalTable::find(journalId);

    if (projJournalTable
        && (projJournalTable.NumOfLines != journalTransNumOfLines || projJournalTable.ProjQty != journalTransQty))
    {
        projJournalTable.selectForUpdate(true);

        ttsbegin;
        ProjJournalTable::initTotal(projJournalTable);
        projJournalTable.update();
        ttscommit;

        journalTableNumOfLines = projJournalTable.NumOfLines;
        journalTableQty = projJournalTable.ProjQty;
    }
}

private container getJournalTransNumberOfLinesAndSumOfQty(ProjJournalId _journalId)
{
    ProjJournalTrans journalTrans;

    if (_journalId)
    {
        select count(RecId), sum(Qty) from journalTrans
            where journalTrans.JournalId == _journalId;
    }

    return [int642int(journalTrans.RecId), journalTrans.Qty];
}

