public boolean validateWrite()
{
    boolean ret;

    ret = super();

    //
    // Set the voucher number
    //
    // Normally this would have been done in the _ds.InitValue or TransDate field.
    //
    // Setting of the voucher number has been delayed as the there are number of
    // ways to set the TransDate/EmpId values which affects the voucher number returned.
    //
    journalFormTrans.fieldModifiedTransDatePost();

    ret = journalFormTrans.datasourceValidateWritePost(ret);

    return ret;
}

int active()
{
    int ret;

    ret = super();

    journalFormTrans.datasourceActivePost();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (projJournalTrans.ProjPriceGroupID &&
           (projJournalTrans.ProjPriceGroupID == ProjInvoiceTable::find(ProjTable::find(projJournalTrans.ProjId).ProjInvoiceProjId).ProjPriceGroup))
        {
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ProjPriceGroupID)).allowEdit(false);
        }
        else
        {
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ProjPriceGroupID)).allowEdit(true);
        }
    }

    ProjJournalTrans journalTrans = this.cursor();
    boolean existingRecord = journalTrans.RecId;
    boolean isRecordExternallyManaged = Project::isProjectExternallyManaged(journalTrans.ProjId);
    boolean lockFieldsForHourJournals = cdsLockingOptionHourJournals && isRecordExternallyManaged;

    if (!ProjJournalTable::find(journalTrans.JournalId).Posted)
    {
        projJournalTrans_ds.allowDelete(existingRecord ? !lockFieldsForHourJournals : true);
        projJournalTrans_ds.allowEdit(existingRecord ? !lockFieldsForHourJournals : true);
    }

    if (isReadOnly)
    {
        projJournalTrans_ds.allowEdit(false);
        projJournalTransTaxExtensionIN_ds.allowEdit(false);
        taxServiceTariff_ds.allowEdit(false);

        #SysSystemDefinedButtons
        FormCommandButtonControl newButton = element.control(element.controlId(#SystemDefinedNewButton)) as FormCommandButtonControl;
        FormCommandButtonControl editButton = element.control(element.controlId(#SystemDefinedViewEditButton)) as FormCommandButtonControl;
        FormCommandButtonControl deleteButton = element.control(element.controlId(#SystemDefinedDeleteButton)) as FormCommandButtonControl;
        newButton.enabled(false);
        editButton.enabled(false);
        deleteButton.enabled(false);
    }

    return ret;
}

