public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    ResourceView ret;
    ResourceLookup resourceLookup;
    ProjParameters  projParameters = ProjParameters::find();

    if (projParameters.ShowInactiveEmployees == NoYes::No)
    {
        resourceLookup = ResourceLookup::newOnlyActiveResourcesWithinCompany();
    }
    else
    {
        resourceLookup = ResourceLookup::newAllResourcesWithinCompany();
    }

    ret = super(_formReferenceControl);

    if (!ret)
    {
        List filterValues = _formReferenceControl.filterValues();

        if (filterValues)
        {
            ListEnumerator filterValuesEnum = filterValues.getEnumerator();

            if (filterValuesEnum)
            {
                Query q = new Query();
                QueryBuildDataSource qbds = q.addDataSource(tableNum(ResourceView));

                while (filterValuesEnum.moveNext())
                {
                    ControlFilterValue filterValue = filterValuesEnum.current();

                    if (strLen(filterValue.value()) > 0)
                    {
                        FieldBinding fieldBinding = filterValue.fieldBinding();
                        QueryBuildRange qbr = qbds.addRange(fieldBinding.fieldId());

                        qbr.value(SysQuery::valueLikeAfter(filterValue.valueForQueryRange()));
                    }
                }

                if (qbds.rangeCount() > 0)
                {
                    // add one more range, on current legal entity
                    SysQuery::findOrCreateRange(qbds, fieldNum(ResourceView, ResourceCompanyId)).value(curExt());

                    q.firstOnly10(true);
                    QueryRun qr = new QueryRun(q);

                    // assume we only fetched one record
                    if (qr.next())
                    {
                        ret.data(qr.getNo(1));

                        // but if we fetched more than one, then ignore the result
                        if (qr.next())
                        {
                            ret = null;
                        }
                    }
                }
            }
        }
    }

    if (projParameters.ValidationEmplProj == ProjValParameter::Mandatory)
    {
        if (!ResourceLookup::validateResource(ret.RecId, projJournalTrans))
        {
            ret = null;
        }
    }
    else
    {
        if (!resourceLookup.validate(ret.RecId))
        {
            ret = null;
        }
    }

    return ret;
}

