/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityIterator.more())
    {
        dataEntityIterator.delete();
    }

    DocuTemplate docuTemplate = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, resourceStr(FeeJournalTransEntryTemplate));

    if (docuTemplate)
    {
        OfficeTemplateExportMenuItem menuItem = OfficeTemplateExportMenuItem::constructWithDocuTemplate(docuTemplate, docuTemplate.TemplateID);
        _menuOptions.customMenuItems().addEnd(menuItem);
    }
}

/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <param name = "_menuItem">
/// The menu item for the template to apply the filters to.
/// </param>
/// <returns>
/// The Map of filters to apply keyed by AOT entity name.
/// </returns>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    const str templateName = resourceStr(FeeJournalTransEntryTemplate);

    DocuTemplate template = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, templateName);
    Map filtersToApply = new Map(Types::String, Types::Class);
    if (template && template.TemplateID == templateName)
    {
        ProjJournalTable journalTable = journalFormTrans.getJournalFormTable().getJournalFormTable().
                                                   journalTableData().journalTable();
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(ProjJournalTransEntity));

        Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode lineFilter = lineFilterBuilder.and(
            lineFilterBuilder.areEqual(fieldStr(ProjJournalTransEntity,JournalId), journalTable.JournalId),
            lineFilterBuilder.areEqual(fieldStr(ProjJournalTransEntity,DataAreaId), curExt()));

        filtersToApply.insert(tableStr(ProjJournalTransEntity), lineFilter);

        ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(ProjJournalTableEntity));

        Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode headerFilter = headerFilterBuilder.and(
            headerFilterBuilder.areEqual(fieldStr(ProjJournalTableEntity,JournalBatchNumber), journalTable.JournalId),
            headerFilterBuilder.areEqual(fieldStr(ProjJournalTableEntity,DataAreaId), journalTable.DataAreaId));

        filtersToApply.insert(tableStr(ProjJournalTableEntity), headerFilter);
    }
    return filtersToApply;
}

 /// <summary>
 /// Sets the editable property of various datasource fields for the form.
 /// </summary>
 void enableFieldsActive()
 {
     ProjTable projTable = ProjTable::find(projJournalTrans.ProjId);

     if (ProjInvoiceTable::isSalesCurrencyLocked(projTable.ProjInvoiceProjId))

     {
         projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).allowEdit(false);
         projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).skip(true);
     }
     else
     {
         projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).allowEdit(true);
         projJournalTrans_ds.object(fieldNum(ProjJournalTrans, CurrencyId)).skip(false);
     }
}

void init()
{
    journalFormTrans    = new ProjJournalFormTrans_Revenue(element);

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        isIndiaIndirectTaxParameterMarked   = TaxParameters::checkTaxParameters_IN();
        isIndiaWHTMarked                    = TaxWithholdParameters_IN::checkTaxParameters();
        isProjJournalTrans_INEnabled        = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(ProjJournalTransTaxExtensionIN));
    }
    // </GIN>

    super();

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    journalFormTrans.parmCtrlCheckJournal(checkJournal);
    journalFormTrans.parmCtrlPostJournal(postJournal);
    journalFormTrans.parmCtrlVoucherGrid(projJournalTrans_Voucher);
    journalFormTrans.parmCtrlInfolog(showInfolog);
    journalFormTrans.parmCtrlVoucherTab(voucher);
    journalFormTrans.parmCtrlLineLogImage(lineLogImage);

    journalFormTrans.formInitPost();

    cdsLockingOptionFeeJournals = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::FeeJournals);

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxGroup.visible(false);
        Group2_TaxGroupId.visible(false);
        Group2_TaxItemGroupId.visible(false);
    }
    // </GTE>

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
    }
    // </GIN>

    // Get the number of lines and sales price from ProjJournalTable.
    journalTableNumOfLines = journalFormTrans.displayJournalNumOfLines(ProjJournalTrans);
    journalTableSalesPrice = journalFormTrans.displayJournalSalesPrice(ProjJournalTrans);
}

/// <summary>
///    Performs a lookup on the string control that is passed in as a parameter.
/// </summary>
/// <param name="_ctrl">
///    The string control on which to perform the lookup.
/// </param>
/// <param name="_buffer">
///    The data to pass to the lookup form.
/// </param>
/// <remarks>
///    The <c>ProjtableLookup</c> form is used to display the lookup list for this string control.
/// </remarks>
private void lookupProjId(FormControl _ctrl, Common _buffer)
{
    FormStringControl   formStringControl;
    Args                args = new Args();
    FormRun             formRun;

    args.name(formStr(ProjTableLookup));
    args.caller(_ctrl);
    args.record(_buffer);
    args.parmEnum(NoYes::No);
    args.lookupValue(projJournalTrans.ProjId);

    formRun = classfactory.formRunClass(args);
    formRun.byProjType(ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled() ? ProjType::None : ProjType::TimeMaterial);
    formRun.init();

    if (cdsLockingOptionFeeJournals)
    {
        FormDataSource formDS = formRun.dataSource(1);
        QueryBuildDataSource qbds1 = formDS.queryBuildDataSource();
        QueryBuildDataSource qbds2 = qbds1.addDataSource(tableNum(ProjTable));
        qbds2.addLink(fieldNum(ProjProjectLookup, ProjId), fieldNum(ProjTable, ProjId));
        qbds2.joinMode(JoinMode::InnerJoin);
        qbds2.addRange(fieldNum(ProjTable, ProjDataSource)).value(SysQuery::value(0));
    }

    formStringControl = _ctrl as FormStringControl;
    formStringControl.performFormLookup(formRun);
}

private boolean validateProjectExternallyManaged(ProjId _projId)
{
    boolean ret = true;

    if (cdsLockingOptionFeeJournals)
    {
        if (Project::isProjectExternallyManaged(_projId))
        {
            ret = checkFailed(strfmt("@Proj:ProjCDSValueInvalidDueToExternal", "@Proj:ProjCDSFeeJournals"));
        }
    }
    return ret;
}

/// <summary>
/// If the total transaction number of lines or sum of transaction sales price is not the same as the prevously stored value,
/// then update ProjJournalTable.
/// </summary>
private void updateJournalTable()
{
    ProjJournalTable projJournalTable = ProjJournalTable::find(journalId);

    if (projJournalTable
        && (projJournalTable.NumOfLines != journalTransNumOfLines || projJournalTable.SalesPrice != journalTransSalesPriceSum))
    {
        projJournalTable.selectForUpdate(true);

        ttsbegin;
        ProjJournalTable::initTotal(projJournalTable);
        projJournalTable.update();
        ttscommit;

        journalTableNumOfLines = projJournalTable.NumOfLines;
        journalTableSalesPrice = projJournalTable.SalesPrice;
    }
}

private container getJournalTransNumberOfLinesAndSumOfSalesPrice(ProjJournalId _journalId)
{
    ProjJournalTrans journalTrans;

    if (_journalId)
    {
        select count(RecId), sum(SalesPrice) from journalTrans
            where journalTrans.JournalId == _journalId;
    }

    return [int642int(journalTrans.RecId), journalTrans.SalesPrice];
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    //
    // Set the voucher number
    //
    // Normally this would have been done in the _ds.InitValue or TransDate field.
    //
    // Setting of the voucher number has been delayed as the there are number of
    // ways to set the TransDate/EmpId values which affects the voucher number returned.
    //
    journalFormTrans.fieldModifiedTransDatePost();

    ret = journalFormTrans.datasourceValidateWritePost(ret);

    return ret;
}

int active()
{
    int ret;

    ret = super();

    journalFormTrans.datasourceActivePost();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (projJournalTrans.ProjPriceGroupID &&
           (projJournalTrans.ProjPriceGroupID == ProjInvoiceTable::find(ProjTable::find(projJournalTrans.ProjId).ProjInvoiceProjId).ProjPriceGroup))
        {
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ProjPriceGroupID)).allowEdit(false);
        }
        else
        {
            projJournalTrans_ds.object(fieldNum(ProjJournalTrans, ProjPriceGroupID)).allowEdit(true);
        }
    }

    ProjJournalTrans journalTrans = this.cursor();
    boolean existingRecord = journalTrans.RecId;
    boolean isRecordExternallyManaged = Project::isProjectExternallyManaged(journalTrans.ProjId);
    boolean lockFieldsForFeeJournals = cdsLockingOptionFeeJournals && isRecordExternallyManaged;

    if (!ProjJournalTable::find(journalTrans.JournalId).Posted)
    {
        projJournalTrans_ds.allowDelete(existingRecord ? !lockFieldsForFeeJournals : true);
        projJournalTrans_ds.allowEdit(existingRecord ? !lockFieldsForFeeJournals : true);
    }

    return ret;
}

void initValue()
{
    journalFormTrans.datasourceInitValuePre();

    if (cdsLockingOptionFeeJournals)
    {
        if (Project::isProjectExternallyManaged(ProjJournalTrans.ProjId))
        {
            projJournalTrans.ProjId = '';
        }
    }

    super();

    journalFormTrans.datasourceInitValuePost();
    // <GIN>
    if (isIndiaIndirectTaxParameterMarked)
    {
        projJournalTrans.Qty = 1;

        ProjJournalTransTaxExtensionIN projJourTransTaxExtensionIN = projJournalTrans.projJournalTransTaxExtensionIN();
        projJourTransTaxExtensionIN.AssessableValueTransactionCurrency = projJournalTrans.SalesPrice * projJournalTrans.Qty;
        projJournalTrans.packProjJournalTrans_IN(projJourTransTaxExtensionIN);
    }
    // </GIN>
}

void delete()
{
    ttsbegin;

    journalFormTrans.datasourceDeletePre();

    super();

    journalFormTrans.datasourceDeletePost();

    ttscommit;

    // Get the number of lines and sales price from ProjJournalTable.
    journalTableNumOfLines = journalFormTrans.displayJournalNumOfLines(ProjJournalTrans);
    journalTableSalesPrice = journalFormTrans.displayJournalSalesPrice(ProjJournalTrans);
}

void write()
{
    ttsbegin;

    journalFormTrans.datasourceWritePre();

    super();

    journalFormTrans.datasourceWritePost();

    // <GIN>
    if (isProjJournalTrans_INEnabled)
    {
        projJournalTransTaxExtensionIN.SysExtensionSerializerExtensionMap::insertAfterBaseTable(projJournalTrans);
    }
    // </GIN>

    ttscommit;

    // Get the number of lines and sales price from ProjJournalTable.
    journalTableNumOfLines = journalFormTrans.displayJournalNumOfLines(ProjJournalTrans);
    journalTableSalesPrice = journalFormTrans.displayJournalSalesPrice(ProjJournalTrans);
}

void linkActive()
{
    if (element.args().dataset() == tableNum(ProjJournalTable))
    {
        ProjJournalTable journalTable = element.args().record();
        journalId = journalTable.JournalId;

        // Store the current number of lines and sum of sales price values from ProjJournalTrans table.
        [journalTransNumOfLines, journalTransSalesPriceSum] = element.getJournalTransNumberOfLinesAndSumOfSalesPrice(journalId);
    }

    journalFormTrans.datasourceLinkActivePre();

    super();
}

