[Form]
public class ProjParameters extends FormRun implements OfficeIMenuCustomizer
{
    NumberSeqReference          numberSeqReference;
    boolean                     runExecuteDirect;
    TmpIdRef                    tmpIdRef;
    FieldId                     tmpFieldId;
    NumberSeqScope              scope;
    NumberSeqApplicationModule  numberSeqApplicationModule;
    container                   numberSequenceModules;
    ProjStageLanguageId         projStageLanguage = LanguageTable::defaultLanguage();
    boolean enableMultipleContractLinesForProject = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    private boolean projOperationsIntegrationFeatureIsEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    private boolean projHoldVendorPaymentIsEnabled = FeatureStateProvider::isFeatureEnabled(ProjHoldVendorPaymentFeature::instance());

    const str UniversalResourceSchedulingFlightName = "EnableUniversalResourceScheduling";

}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    Set entitiesToExclude = element.entitiesToExcludeSet();
    Project::removeEntitiesFromOpenOfficeMenu(_menuOptions, entitiesToExclude);
}

/// <summary>
/// Gets entities excluded set.
/// </summary>
/// <returns>The set of entities to be excluded.</returns>
protected Set entitiesToExcludeSet()
{
    Set entitiesToExclude = new Set(Types::String);

    if (projOperationsIntegrationFeatureIsEnabled)
    {
        entitiesToExclude.add(dataentityviewstr(ProjectParameterEntity));
        entitiesToExclude.add(dataentityviewstr(ProjectIntercompanyParametersEntity));
        entitiesToExclude.add(dataentityviewstr(InventDimSetupGridEntity));
        entitiesToExclude.add(dataentityviewstr(PSAVendPWPTxtEntity));
    }
    else
    {
        entitiesToExclude.add(dataentityviewstr(ProjBillingTypeLinePropertyEntity));
        entitiesToExclude.add(dataentityviewstr(ProjProjectParameterV2Entity));
    }

    return entitiesToExclude;
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret=projParameters_ds.validateWrite();
    }

    return ret;
}

public boolean checkFundingSearchOrder(int    _newPosition)
{
    boolean         retValue = true;
    DictEnum        dictEnum  = new DictEnum(enumNum(ProjFundingSearchCriteria));
    str             selectedTxt = fundingSearchCriteriaListbox.getText(fundingSearchCriteriaListbox.selection());

    // Validate no item placed at top of list when Priority is first.

    // Validate no item is placed at bottom of list when Priority is last.

    // Validate Activity is after Project
    if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Activity)
    {
        if (dictEnum.name2Value(fundingSearchCriteriaListbox.getText(_newPosition)) == ProjFundingSearchCriteria::Project)
        {
            return checkFailed("@SYS132951");
        }
    }

    if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Project)
    {
        if (dictEnum.name2Value(fundingSearchCriteriaListbox.getText(_newPosition)) == ProjFundingSearchCriteria::Activity)
        {
            return checkFailed("@SYS132951");
        }
    }

    return retValue;
}

public void close()
{
    if (Project::Enabled())
    {
        psaParameters_ds.write();
    }
    super();

    this.saveCostReduction();
    this.saveFundingSearchCriteria();
}

public void doApply()
{
    super();

    this.saveCostReduction();
    this.saveFundingSearchCriteria();
}

/// <summary>
/// Enables or disables project budget carry forward fields.
/// </summary>
public void enableDisableCarryForwardFields()
{
    if (projParameters.ProjCarryForwardRemainingBudget == NoYes::No)
    {
        projParameters_ds.object(fieldNum(ProjParameters, ProjCarryForwardNegativeBudget)).allowEdit(false);
    }
    else
    {
        projParameters_ds.object(fieldNum(ProjParameters, ProjCarryForwardNegativeBudget)).allowEdit(true);
    }
}

/// <summary>
/// Ensures that tracking of committed cost on Purchase Req, Purchase Order, and Vendor Invoice is on when tracking
/// committed cost for budget reservation is on and doesn't allow the user to change it. When tracking committed
/// cost for budget reservation is off, allow the editing of Purchase Req committed cost tracking.
/// </summary>
public void enableDisableCommittedCostBudgetReservation()
{
    if (projParameters.TrackCommittedCostBudgetReservation_PSN)
    {
        ProjParameters.TrackCommittedCostPurchaseRequisition = NoYes::Yes;
        projParameters.TrackCommittedCostPurchaseOrder = NoYes::Yes;
        projParameters.TrackCommittedCostVendInvoice = NoYes::Yes;
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostPurchaseRequisition)).allowEdit(false);
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostPurchaseOrder)).allowEdit(false);
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostVendInvoice)).allowEdit(false);
    }
    else
    {
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostPurchaseRequisition)).allowEdit(true);
    }
}

public void enableDisableCommittedCostPurchaseOrder()
{
    if (projParameters.TrackCommittedCostPurchaseRequisition)
    {
        projParameters.TrackCommittedCostPurchaseOrder = NoYes::Yes;
        projParameters.TrackCommittedCostVendInvoice = NoYes::Yes;
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostPurchaseOrder)).allowEdit(false);
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostVendInvoice)).allowEdit(false);
    }
    else
    {
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostPurchaseOrder)).allowEdit(true);
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostVendInvoice)).allowEdit(true);
    }

    if (projParameters.TrackCommittedCostPurchaseOrder)
    {
        projParameters.TrackCommittedCostVendInvoice = NoYes::Yes;
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostVendInvoice)).allowEdit(false);
    }
    else
    {
        projParameters_ds.object(fieldNum(ProjParameters, TrackCommittedCostVendInvoice)).allowEdit(true);
    }
}

public void enableDisableFundingButtons()
{
    DictEnum        dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    str selectedTxt;

    selectedTxt = fundingSearchCriteriaListbox.getText(0);
    if (fundingSearchCriteriaListbox.selection() == 0 || (fundingSearchCriteriaListbox.selection() == 1 && dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority))
    {
        // Top element
        fundingSearchUp.enabled(false);
    }
    else
    {
        fundingSearchUp.enabled(true);
    }

    selectedTxt = fundingSearchCriteriaListbox.getText(dictEnum.values()-1);
    if (fundingSearchCriteriaListbox.selection() ==  dictEnum.values()-1 || (fundingSearchCriteriaListbox.selection() == dictEnum.values()-2 && dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority) )
    {
        // Bottom element
        fundingSearchDown.enabled(false);
    }
    else
    {
        fundingSearchDown.enabled(true);
    }
}

void enableDisableFundingFields()
{
    fundingSearchDown.enabled(true);
    fundingSearchCriteriaListbox.enabled(true);
    fundingSearchUp.enabled(true);

    fundingSearchCriteriaListbox.displayLengthMode(AutoMode::Auto);

    if (projParameters.FundingAllocationSettingsLocked == NoYes::Yes)
    {
        fundingSearchUp.enabled(false);
        fundingSearchDown.enabled(false);
        fundingSearchCriteriaListbox.enabled(false);
    }
    else
    {
        element.enableDisableFundingButtons();
    }
}

public void enableDisableTimeRangeOverlap()
{
    if (projParameters_ds.object(fieldNum(ProjParameters, RequireStartStopTime)).getValue() == NoYes::Yes)
    {
        projParameters_ds.object(fieldNum(ProjParameters, TimeOverlap)).enabled(true);
    }
    else
    {
        projParameters_ds.object(fieldNum(ProjParameters, TimeOverlap)).enabled(false);
    }
}

void enableDisableValidationFields()
{
    projParameters_ds.object(fieldNum(ProjParameters, TimesheetChangeReason))           .allowEdit(projParameters.TimesheetAuditTrail);
    projParameters_ds.object(fieldNum(ProjParameters, TimesheetChangeReasonMandatory))  .allowEdit(projParameters.TimesheetAuditTrail && projParameters.TimesheetChangeReason);
    projParameters_ds.object(fieldNum(ProjParameters, TimesheetAbsenceProjId))          .allowEdit(projParameters.TimesheetValidateAbsence);
    projParameters_ds.object(fieldNum(ProjParameters, TimesheetAuditTrail))             .allowEdit(!projParameters.RequireStartStopTime);
    projParameters_ds.object(fieldNum(ProjParameters, RequireStartStopTime))            .allowEdit(!projParameters.TimesheetAuditTrail && !projParameters.TimesheetBlockFutureEntries);
    projParameters_ds.object(fieldNum(ProjParameters, TimesheetBlockFutureEntries))     .allowEdit(!projParameters.RequireStartStopTime);
}

//goes through the ProjUtilTypes table and seeks out entries for every
//project type controlled by fields in this form. If the entry is absent,
//inserts a default one.
private void ensureProjUtilTypesFull()
{
    if(element.enableMultipleContractLinesForProject)
    {
        if (!ProjUtilTypes::exist(ProjType::None))
        {
            ProjUtilTypes::insertValue(ProjType::None, ProjUtilOption::AccToLineProperty, ProjUtilOption::Utilization);
        }
    }
    else
    {
        if (!ProjUtilTypes::exist(ProjType::Cost))
        {
            ProjUtilTypes::insertValue(ProjType::Cost, ProjUtilOption::NotIncluded, ProjUtilOption::NotIncluded);
        }

        if (!ProjUtilTypes::exist(ProjType::FixedPrice))
        {
            ProjUtilTypes::insertValue(ProjType::FixedPrice, ProjUtilOption::Utilization, ProjUtilOption::Utilization);
        }

        if (!ProjUtilTypes::exist(ProjType::Internal))
        {
            ProjUtilTypes::insertValue(ProjType::Internal, ProjUtilOption::NotIncluded, ProjUtilOption::NotIncluded);
        }

        if (!ProjUtilTypes::exist(ProjType::Investment))
        {
            ProjUtilTypes::insertValue(ProjType::Investment, ProjUtilOption::Burden, ProjUtilOption::Burden);
        }

        if (!ProjUtilTypes::exist(ProjType::Time))
        {
            ProjUtilTypes::insertValue(ProjType::Time, ProjUtilOption::NotIncluded, ProjUtilOption::NotIncluded);
        }

        if (!ProjUtilTypes::exist(ProjType::TimeMaterial))
        {
            ProjUtilTypes::insertValue(ProjType::TimeMaterial, ProjUtilOption::AccToLineProperty, ProjUtilOption::Utilization);
        }
    }
}

