    return "";
}

void fillView()
{
    InventDimMetaDataCache::fillParameterFormView(listView);
    element.setupAllowEdit(false);
}

edit ProjUtilOption NoneTypeProjectsBillableType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    select firstonly t where t.ProjType == ProjType::None;
    if (set)
    {
        ttsbegin;
        t.selectForUpdate(true);
        t.Billable = type;
        t.update();
        ttscommit;
    }

    return t.Billable;
}

edit ProjUtilOption NoneTypeProjectsEfficiencyType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    select firstonly t where t.ProjType == ProjType::None;
    if (set)
    {
        ttsbegin;
        t.selectForUpdate(true);
        t.Efficiency = type;
        t.update();
        ttscommit;
    }

    return t.Efficiency;
}

