//returns true if the user has access rights to the ProjUtilTypes table
//and configuration key Project Basic Hour enabled
private boolean hasRightsToUtilTable(AccessType _permissionLevel = AccessType::View)
{
    return (new DictTable(tableNum(ProjUtilTypes)).rights() >= _permissionLevel) &&
           (Project::Enabled());
}

private void updateHideVendorPaymentElementByFeature()
{
