private void hideCreateInvoiceProposalOptions()
{
    boolean enableInvoiceProposalCreatingPerfEnhanced = FeatureStateProvider::IsFeatureEnabled(ProjInvoiceProposalPerformanceEnhancementFeature::instance());
    CreateInvoiceProposalGroup.visible(!projOperationsIntegrationFeatureIsEnabled && enableInvoiceProposalCreatingPerfEnhanced);

    ProjParameters_InvoiceProposalLineEditPerf.visible(
        !projOperationsIntegrationFeatureIsEnabled &&
        enableInvoiceProposalCreatingPerfEnhanced &&
        ProjFeatureStateHelper::isInvoiceFormsHeaderLineViewFeatureEnabled());
}

private void hideEstimateBatchExecutionOptions()
{
    boolean enableControlPeriodMultipleBatchTasksFeature = FeatureStateProvider::isFeatureEnabled(
        ProjControlPeriodMultipleBatchTasksFeature::instance());
    EstimateBatchExecution.visible(enableControlPeriodMultipleBatchTasksFeature && !projOperationsIntegrationFeatureIsEnabled);
}

public void init()
{
    // Pre initialize number sequence handling
    this.numberSeqPreInit();

    super();

    //make sure there is data in the table for Utilization fields'
    //data methods to work with
    this.ensureProjUtilTypesFull();

    // Get the project paramter tables
    ProjParameters::find();
    WrkCtrParameters::find();

    // Post initialize number sequence handling
    this.numberSeqPostInit();

    // Update the inventory dimension setup grid table
    InventDimSetupGrid::loadSetup();

    // Fill inventory dimensions list
    element.fillView();

    listView.viewType(FormListViewType::List);

    // Fill tmp table for grid display of ProjStatus usage for ProjTypes
    TmpProjStatusSetup::fillTable(tmpProjStatusSetup, projStageLanguage);

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN() || TaxWithholdParameters_IN::checkTaxParameters())
    {
        invoiceSplit_IN.visible(true);
    }
    else
    {
        invoiceSplit_IN.visible(false);
    }
    // </GIN>

    trackCommittedCostBudgetReservation_PSN.visible(PublicSectorUtils::isBudgetReservationEnabled());

    ResourceScheduling_EnableUniversalResourceScheduling.visible(Global::isFlightEnabled(UniversalResourceSchedulingFlightName));

    ProjMutilContractLinesFeatureFormController::updateFormControlVisibity(element,
        [ProjectGeneral_DisplayProjectType.id(),
         Invoice_OnAccountPaymentCriteria.id(),
         TimeAndMaterialGroup.id(), 
         FixedPriceGroup.id(),
         InvestmentGroup.id(),
         InternalCostGroup.id(),
         TimeProjectsGroup.id()],
        [NoneTypeProjectsGroup.id()]);

    this.updateHideVendorPaymentElementByFeature();

    this.hideElementForCEIntegration();

    this.hideCreateInvoiceProposalOptions();

    this.hideEstimateBatchExecutionOptions();

    if (FeatureStateProvider::isFeatureEnabled(ProjItemRequirementMultipleFundingSources::instance()))
    {
        SalesOrder.caption("@Proj:SalesOrdersAndItemRequirements");
    }

    if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
    {
        Estimate.caption("@Proj:RevRec");
        AllowEliminationWithoutEstimate.label("@Proj:WithoutPostingRevRec");
        RemainingBudget_AddCommittedToCTC.label("@Proj:CommittedCostUptoRevRecDate");
        RemainingBudget_AddActualPeriodEndToCTC.label("@Proj:ActualCostAfterRevRecDate");
        RemainingBudget_AddCommittedPeriodEndToCTC.label("@Proj:CommittedCostAfterRevRecDate");
        EstimateBatch_MaxTasksForEstimateBatch.label("@Proj:MaxTasksForEstimateBatchV2");
    }

    // Disable control as contract line based revenue recognition cannot be turned off once turned on
    ProjParameters_ContractLineBasedRevenueRecognition.enabled(!ProjParameters.ContractLineBasedRevenueRecognition);
}

edit ProjUtilOption internalCostBillableType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    if (set)
    {
        ttsbegin;

        //because internal and cost are united into one field, this method
        //updates both, but reads from just one.
        select firstonly forupdate t
        where t.ProjType == ProjType::Internal;
        t.Billable = type;
        t.update();

        select firstonly forupdate t
        where t.ProjType == ProjType::Cost;
        t.Billable = type;
        t.update();

        ttscommit;
    }

    select firstonly t where t.ProjType == ProjType::Cost;

    return t.Billable;
}

edit ProjUtilOption internalCostEfficiencyType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    if (set)
    {
        ttsbegin;

        //because internal and cost are united into one field, this method
        //updates both, but reads from just one.
        select firstonly forupdate t
        where t.ProjType == ProjType::Internal;
        t.Efficiency = type;
        t.update();

        select firstonly forupdate t
        where t.ProjType == ProjType::Cost;
        t.Efficiency = type;
        t.update();

        ttscommit;
    }

    select firstonly t where t.ProjType == ProjType::Cost;

    return t.Efficiency;
}

edit ProjUtilOption investmentBillableType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    select firstonly t where t.ProjType == ProjType::Investment;
    if (set)
    {
        ttsbegin;
        t.selectForUpdate(true);
        t.Billable = type;
        t.update();
        ttscommit;
    }

    return t.Billable;
}

edit ProjUtilOption investmentEfficiencyType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    select firstonly t where t.ProjType == ProjType::Investment;
    if (set)
    {
        ttsbegin;
        t.selectForUpdate(true);
        t.Efficiency = type;
        t.update();
        ttscommit;
    }

    return t.Efficiency;
}

void listSelection()
{
    int idx;

    if (inventDimSetupGrid.RecId)
    {
        inventDimSetupGrid.write();
    }

    // Get the selected inventory dimension
    idx = listView.getNextItem(FormListNext::Selected);

    // i
    if (idx < 0)
    {
        element.setupAllowEdit(false);
        tmpFieldId = 0;
        inventDimSetupGrid_ds.executeQuery();
        inventDimSetupGrid_ds.refresh();

        return;
    }

    element.setupAllowEdit(true);
    tmpFieldId = listView.getItem(idx).data();
    inventDimSetupGrid_ds.executeQuery();
    inventDimSetupGrid_ds.refresh();
}

void loadCostReduction()
{
    DictEnum        dictEnum=new DictEnum(enumNum(ProjReduceCostPriority));
    int             i;
    ProjParameters  projParameter;

    projParameter=ProjParameters::find();

    // Add the selected hour priorities to hourSelected listbox
    hourSelected.clear();
    hourMinRequirement.clear();
    if (projParameter.HourReduceCostPriority1 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceCostPriority1)))
    {
        hourSelected.add(dictEnum.index2Label(projParameter.HourReduceCostPriority1));
        hourMinRequirement.add(dictEnum.index2Label(projParameter.HourReduceCostPriority1));
    }
    if (projParameter.HourReduceCostPriority2 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceCostPriority2)))
    {
        hourSelected.add(dictEnum.index2Label(projParameter.HourReduceCostPriority2));
        hourMinRequirement.add(dictEnum.index2Label(projParameter.HourReduceCostPriority2));
    }
    if (projParameter.HourReduceCostPriority3 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceCostPriority3)))
    {
        hourSelected.add(dictEnum.index2Label(projParameter.HourReduceCostPriority3));
        hourMinRequirement.add(dictEnum.index2Label(projParameter.HourReduceCostPriority3));
    }
    if (projParameter.HourReduceCostPriority4 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceCostPriority4)))
    {
        hourSelected.add(dictEnum.index2Label(projParameter.HourReduceCostPriority4));
        hourMinRequirement.add(dictEnum.index2Label(projParameter.HourReduceCostPriority4));
    }
    if (projParameter.HourReduceCostPriority5 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceCostPriority5)))
    {
        hourSelected.add(dictEnum.index2Label(projParameter.HourReduceCostPriority5));
        hourMinRequirement.add(dictEnum.index2Label(projParameter.HourReduceCostPriority5));
    }
    hourSelected.displayLengthMode(AutoMode::Auto);

    // Add the remaining hour priorities to hourRemaining listbox
    hourRemaining.clear();
    for (i = 1; i <= dictEnum.values(); i++)
    {
        //Remove the option of 'Estimate Projects' from the priority field list for Forecast reduction setup
        if (dictEnum.index2Value(i) == ProjReduceCostPriority::EstimateProject)
        {
            continue;
        }

        if (dictEnum.index2Value(i) != ProjReduceCostPriority::None
        && dictEnum.index2Value(i) != enum2int(projParameter.HourReduceCostPriority1)
        && dictEnum.index2Value(i) != enum2int(projParameter.HourReduceCostPriority2)
        && dictEnum.index2Value(i) != enum2int(projParameter.HourReduceCostPriority3)
        && dictEnum.index2Value(i) != enum2int(projParameter.HourReduceCostPriority4)
        && dictEnum.index2Value(i) != enum2int(projParameter.HourReduceCostPriority5)
        && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(i)))
        {
            hourRemaining.add(dictEnum.index2Label(i));
        }
    }
    hourRemaining.displayLengthMode(AutoMode::Auto);

    if (isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.HourReduceMinimumRequirement)))
    {
        hourMinRequirement.selectText(dictEnum.index2Label(projParameter.HourReduceMinimumRequirement));
    }

    // Add the selected Expense priorities to expenseSelected listbox
    expenseSelected.clear();
    expenseMinRequirement.clear();
    if (projParameter.ExpenseReduceCostPriority1 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceCostPriority1)))
    {
        expenseSelected.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority1));
        expenseMinRequirement.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority1));
    }
    if (projParameter.ExpenseReduceCostPriority2 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceCostPriority2)))
    {
        expenseSelected.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority2));
        expenseMinRequirement.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority2));
    }
    if (projParameter.ExpenseReduceCostPriority3 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceCostPriority3)))
    {
        expenseSelected.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority3));
        expenseMinRequirement.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority3));
    }
    if (projParameter.ExpenseReduceCostPriority4 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceCostPriority4)))
    {
        expenseSelected.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority4));
        expenseMinRequirement.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority4));
    }
    if (projParameter.ExpenseReduceCostPriority5 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceCostPriority5)))
    {
        expenseSelected.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority5));
        expenseMinRequirement.add(dictEnum.index2Label(projParameter.ExpenseReduceCostPriority5));
    }
    expenseSelected.displayLengthMode(AutoMode::Auto);

    // Add the remaining expense priorities to expenseRemaining listbox
    expenseRemaining.clear();
    for (i = 1; i <= dictEnum.values(); i++)
    {
        //Remove the option of 'Estimate Projects' from the priority field list for Forecast reduction setup
        if (dictEnum.index2Value(i) == ProjReduceCostPriority::EstimateProject)
        {
            continue;
        }

        if (dictEnum.index2Value(i) != ProjReduceCostPriority::None
        && dictEnum.index2Value(i) != enum2int(projParameter.ExpenseReduceCostPriority1)
        && dictEnum.index2Value(i) != enum2int(projParameter.ExpenseReduceCostPriority2)
        && dictEnum.index2Value(i) != enum2int(projParameter.ExpenseReduceCostPriority3)
        && dictEnum.index2Value(i) != enum2int(projParameter.ExpenseReduceCostPriority4)
        && dictEnum.index2Value(i) != enum2int(projParameter.ExpenseReduceCostPriority5)
        && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(i)))
        {
            expenseRemaining.add(dictEnum.index2Label(i));
        }
    }
    expenseRemaining.displayLengthMode(AutoMode::Auto);

    if (isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ExpenseReduceMinimumRequirement)))
    {
        expenseMinRequirement.selectText(dictEnum.index2Label(projParameter.ExpenseReduceMinimumRequirement));
    }
    expenseLumpSumAllocationKey.text(projParameter.ExpenseLumpSumAllocationKey);

    // Add the selected item priorities to itemSelected listbox
    itemSelected.clear();
    itemMinRequirement.clear();
    if (projParameter.ItemReduceCostPriority1 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceCostPriority1)))
    {
        itemSelected.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority1));
        itemMinRequirement.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority1));
    }
    if (projParameter.ItemReduceCostPriority2 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceCostPriority2)))
    {
        itemSelected.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority2));
        itemMinRequirement.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority2));
    }
    if (projParameter.ItemReduceCostPriority3 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceCostPriority3)))
    {
        itemSelected.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority3));
        itemMinRequirement.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority3));
    }
    if (projParameter.ItemReduceCostPriority4 != projReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceCostPriority4)))
    {
        itemSelected.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority4));
        itemMinRequirement.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority4));
    }
    if (projParameter.ItemReduceCostPriority5 != ProjReduceCostPriority::None
    && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceCostPriority5)))
    {
        itemSelected.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority5));
        itemMinRequirement.add(dictEnum.index2Label(projParameter.ItemReduceCostPriority5));
    }
    itemSelected.displayLengthMode(AutoMode::Auto);

    // Add the remaining item priorities to itemRemaining listbox
    itemRemaining.clear();
    for (i = 1; i <= dictEnum.values(); i++)
    {
        //Remove the option of 'Estimate Projects' from the priority field list for Forecast reduction setup
        if (dictEnum.index2Value(i) == ProjReduceCostPriority::EstimateProject)
        {
            continue;
        }

        if (dictEnum.index2Value(i) != ProjReduceCostPriority::None
        && dictEnum.index2Value(i) != enum2int(projParameter.ItemReduceCostPriority1)
        && dictEnum.index2Value(i) != enum2int(projParameter.ItemReduceCostPriority2)
        && dictEnum.index2Value(i) != enum2int(projParameter.ItemReduceCostPriority3)
        && dictEnum.index2Value(i) != enum2int(projParameter.ItemReduceCostPriority4)
        && dictEnum.index2Value(i) != enum2int(projParameter.ItemReduceCostPriority5)
        && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(i)))
        {
            itemRemaining.add(dictEnum.index2Label(i));
        }
    }
    itemRemaining.displayLengthMode(AutoMode::Auto);

    if (isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(projParameter.ItemReduceMinimumRequirement)))
    {
        itemMinRequirement.selectText(dictEnum.index2Label(projParameter.ItemReduceMinimumRequirement));
    }
    itemLumpSumAllocationKey.text(projParameter.ItemLumpSumAllocationKey);
}

void loadFundingSearchCriteria()
{
    ProjFundingSearchParameter  projFundingSearchParameter;
    DictEnum                    dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    int                         numOfValues;
    int64                       recCount;
    int                         i;

    numOfValues = dictEnum.values();
    recCount = (select count(RecId) from projFundingSearchParameter).RecId;

    if (recCount != numOfValues)
    {
        //
        // Setup default list as record count is wrong
        // ProjFundingSearchCriteria::Priority should be first in the list, but is the last element
        // in the enumeration
        //
        fundingSearchCriteriaListbox.add(dictEnum.index2Label(ProjFundingSearchCriteria::Priority));
        for (i = 0; i < dictEnum.values()-1; i++)
        {
            fundingSearchCriteriaListbox.add(dictEnum.index2Label(i));
        }
    }
    else
    {
        // Load values from table
        while select * from projFundingSearchParameter order by projFundingSearchParameter.Priority
        {
            fundingSearchCriteriaListbox.add(dictEnum.index2Label(projFundingSearchParameter.SearchCriteria));
        }
    }
}

public void loadValues(DictEnum _dictEnum, FormListBoxControl _listBox, FormListBoxControl _selected)
{
    int i;
    DictEnum dictEnum = _dictEnum;

    _listBox.clear();

    for (i = 0; i <= dictEnum.values(); i++)
    {
        if (dictEnum.index2Value(i) != ProjReduceCostPriority::None              &&
           dictEnum.index2Value(i) != ProjReduceCostPriority::EstimateProject   &&
           dictEnum.index2Label(i) != _selected.getText(0)                      &&
           dictEnum.index2Label(i) != _selected.getText(1)                      &&
           dictEnum.index2Label(i) != _selected.getText(2)                      &&
           dictEnum.index2Label(i) != _selected.getText(3)                      &&
           dictEnum.index2Label(i) != _selected.getText(4))
        {
            _listBox.add(dictEnum.index2Label(i));
        }
    }
}

void numberSeqPostInit()
{
    ctrlAllowSameAs.visible(numberSeqApplicationModule.sameAsActive());
    ctrlLabelSameAs.visible(ctrlAllowSameAs.visible());
}

void numberSeqPreInit()
{
    runExecuteDirect   = false;
    numberSequenceModules = [NumberSeqModule::Proj];
    numberSeqApplicationModule = new NumberSeqModuleProject();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

/// <summary>
///    Asks the user whether to change the funding priority settings.
/// </summary>
/// <returns>
///    true if the user selects yes to change settings; otherwise, false.
/// </returns>
/// <remarks>
///    This method is called if the funding priority has changed and funding rules exist.
/// </remarks>
public boolean promptChangeFundingPriority()
{
    ProjInvoiceTable    projInvoiceTable;

    // Only prompt when contract records exist.
    select firstonly RecId from projInvoiceTable;
    if (projInvoiceTable.RecId != 0)
    {
        return (Box::yesNo("@SYS134228", DialogButton::Yes) == DialogButton::Yes);
    }

    return NoYes::Yes;
}

public void run()
{
    super();

    // Approve group should only be active if the Approve flag is set to active
    approveGroupId.enabled(projParameters.ApproveActive);

    // Fill the cost reduction tab
    this.loadCostReduction();

    // Fill the funding search creteria listbox
    this.loadFundingSearchCriteria();

    // Contract line based revenue recognition control should only be enabled if the parameter ContractLineBasedRevenueRecognition is disabled as it cannot change once enabled
    ProjParameters_ContractLineBasedRevenueRecognition.enabled(!ProjParameters.ContractLineBasedRevenueRecognition);
}

void saveCostReduction()
{
    ttsbegin;

    ProjParameters.selectForUpdate(true);

    DictEnum       dictEnum = new DictEnum(enumNum(ProjReduceCostPriority));

    // save hour priority
    int hourSelectedCount = hourSelected.count();

    switch (hourSelectedCount)
    {
        case 1:
            ProjParameters.HourReduceCostPriority1 = dictEnum.name2Value(hourSelected.getText(0));
            ProjParameters.HourReduceCostPriority2 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 2:
            ProjParameters.HourReduceCostPriority1 = dictEnum.name2Value(hourSelected.getText(0));
            ProjParameters.HourReduceCostPriority2 = dictEnum.name2Value(hourSelected.getText(1));
            ProjParameters.HourReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 3:
            ProjParameters.HourReduceCostPriority1 = dictEnum.name2Value(hourSelected.getText(0));
            ProjParameters.HourReduceCostPriority2 = dictEnum.name2Value(hourSelected.getText(1));
            ProjParameters.HourReduceCostPriority3 = dictEnum.name2Value(hourSelected.getText(2));
            ProjParameters.HourReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.HourReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 4:
            ProjParameters.HourReduceCostPriority1 = dictEnum.name2Value(hourSelected.getText(0));
            ProjParameters.HourReduceCostPriority2 = dictEnum.name2Value(hourSelected.getText(1));
            ProjParameters.HourReduceCostPriority3 = dictEnum.name2Value(hourSelected.getText(2));
            ProjParameters.HourReduceCostPriority4 = dictEnum.name2Value(hourSelected.getText(3));
            ProjParameters.HourReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 5:
            ProjParameters.HourReduceCostPriority1 = dictEnum.name2Value(hourSelected.getText(0));
            ProjParameters.HourReduceCostPriority2 = dictEnum.name2Value(hourSelected.getText(1));
            ProjParameters.HourReduceCostPriority3 = dictEnum.name2Value(hourSelected.getText(2));
            ProjParameters.HourReduceCostPriority4 = dictEnum.name2Value(hourSelected.getText(3));
            ProjParameters.HourReduceCostPriority5 = dictEnum.name2Value(hourSelected.getText(4));
            break;
    }

    str hourMinRequirementSelectedText = hourMinRequirement.getText(hourMinRequirement.selection());

    if (hourMinRequirementSelectedText !='')
    {
        ProjParameters.HourReduceMinimumRequirement = dictEnum.name2Value(hourMinRequirementSelectedText);
    }
    else if (hourSelectedCount > 0)
    {
        ProjParameters.HourReduceMinimumRequirement = dictEnum.name2Value(hourSelected.getText(0));
    }
    else
    {
        ProjParameters.HourReduceMinimumRequirement = ProjReduceCostPriority::None;
    }

    // save expense priority
    int expenseSelectedCount = expenseSelected.count();

    switch (expenseSelectedCount)
    {
        case 1:
            ProjParameters.ExpenseReduceCostPriority1 = dictEnum.name2Value(expenseSelected.getText(0));
            ProjParameters.ExpenseReduceCostPriority2 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 2:
            ProjParameters.ExpenseReduceCostPriority1 = dictEnum.name2Value(expenseSelected.getText(0));
            ProjParameters.ExpenseReduceCostPriority2 = dictEnum.name2Value(expenseSelected.getText(1));
            ProjParameters.ExpenseReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 3:
            ProjParameters.ExpenseReduceCostPriority1 = dictEnum.name2Value(expenseSelected.getText(0));
            ProjParameters.ExpenseReduceCostPriority2 = dictEnum.name2Value(expenseSelected.getText(1));
            ProjParameters.ExpenseReduceCostPriority3 = dictEnum.name2Value(expenseSelected.getText(2));
            ProjParameters.ExpenseReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ExpenseReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 4:
            ProjParameters.ExpenseReduceCostPriority1 = dictEnum.name2Value(expenseSelected.getText(0));
            ProjParameters.ExpenseReduceCostPriority2 = dictEnum.name2Value(expenseSelected.getText(1));
            ProjParameters.ExpenseReduceCostPriority3 = dictEnum.name2Value(expenseSelected.getText(2));
            ProjParameters.ExpenseReduceCostPriority4 = dictEnum.name2Value(expenseSelected.getText(3));
            ProjParameters.ExpenseReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 5:
            ProjParameters.ExpenseReduceCostPriority1 = dictEnum.name2Value(expenseSelected.getText(0));
            ProjParameters.ExpenseReduceCostPriority2 = dictEnum.name2Value(expenseSelected.getText(1));
            ProjParameters.ExpenseReduceCostPriority3 = dictEnum.name2Value(expenseSelected.getText(2));
            ProjParameters.ExpenseReduceCostPriority4 = dictEnum.name2Value(expenseSelected.getText(3));
            ProjParameters.ExpenseReduceCostPriority5 = dictEnum.name2Value(expenseSelected.getText(4));
            break;
    }

    str expenseMinRequirementSelectedText = expenseMinRequirement.getText(expenseMinRequirement.selection());

    if (expenseMinRequirementSelectedText != '')
    {
        ProjParameters.ExpenseReduceMinimumRequirement = dictEnum.name2Value(expenseMinRequirementSelectedText);
    }
    else if (expenseSelectedCount > 0)
    {
        ProjParameters.ExpenseReduceMinimumRequirement = dictEnum.name2Value(expenseSelected.getText(0));
    }
    else
    {
        ProjParameters.ExpenseReduceMinimumRequirement = ProjReduceCostPriority::None;
    }
    ProjParameters.ExpenseLumpSumAllocationKey = expenseLumpSumAllocationKey.text();

    // save item priority
    int itemSelectedCount = itemSelected.count();

    switch (itemSelectedCount)
    {
        case 1:
            ProjParameters.ItemReduceCostPriority1 = dictEnum.name2Value(itemSelected.getText(0));
            ProjParameters.ItemReduceCostPriority2 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 2:
            ProjParameters.ItemReduceCostPriority1 = dictEnum.name2Value(itemSelected.getText(0));
            ProjParameters.ItemReduceCostPriority2 = dictEnum.name2Value(itemSelected.getText(1));
            ProjParameters.ItemReduceCostPriority3 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 3:
            ProjParameters.ItemReduceCostPriority1 = dictEnum.name2Value(itemSelected.getText(0));
            ProjParameters.ItemReduceCostPriority2 = dictEnum.name2Value(itemSelected.getText(1));
            ProjParameters.ItemReduceCostPriority3 = dictEnum.name2Value(itemSelected.getText(2));
            ProjParameters.ItemReduceCostPriority4 = ProjReduceCostPriority::None;
            ProjParameters.ItemReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 4:
            ProjParameters.ItemReduceCostPriority1 = dictEnum.name2Value(itemSelected.getText(0));
            ProjParameters.ItemReduceCostPriority2 = dictEnum.name2Value(itemSelected.getText(1));
            ProjParameters.ItemReduceCostPriority3 = dictEnum.name2Value(itemSelected.getText(2));
            ProjParameters.ItemReduceCostPriority4 = dictEnum.name2Value(itemSelected.getText(3));
            ProjParameters.ItemReduceCostPriority5 = ProjReduceCostPriority::None;
            break;

        case 5:
            ProjParameters.ItemReduceCostPriority1 = dictEnum.name2Value(itemSelected.getText(0));
            ProjParameters.ItemReduceCostPriority2 = dictEnum.name2Value(itemSelected.getText(1));
            ProjParameters.ItemReduceCostPriority3 = dictEnum.name2Value(itemSelected.getText(2));
            ProjParameters.ItemReduceCostPriority4 = dictEnum.name2Value(itemSelected.getText(3));
            ProjParameters.ItemReduceCostPriority5 = dictEnum.name2Value(itemSelected.getText(4));
            break;
    }

    str itemMinRequirementSelectedText = itemMinRequirement.getText(itemMinRequirement.selection());

    if (itemMinRequirementSelectedText != '')
    {
        ProjParameters.ItemReduceMinimumRequirement = dictEnum.name2Value(itemMinRequirementSelectedText);
    }
    else if (itemSelectedCount > 0)
    {
        ProjParameters.ItemReduceMinimumRequirement = dictEnum.name2Value(itemSelected.getText(0));
    }
    else
    {
        ProjParameters.ItemReduceMinimumRequirement = ProjReduceCostPriority::None;
    }
    ProjParameters.ItemLumpSumAllocationKey = itemLumpSumAllocationKey.text();

    ProjParameters.update();
    ttscommit;
}

void saveFundingSearchCriteria()
{
    int                         counter;
    int                         i;
    ProjFundingSearchParameter  projFundingSearchParameter;
    DictEnum                    dictEnum  = new DictEnum(enumNum(ProjFundingSearchCriteria));

    // save funding search priority
    counter = dictEnum.values();

    ttsbegin;
    // remove all existing records
    delete_from projFundingSearchParameter;

    // insert the new values
    for (i = 0; i < counter; i++)
    {
        projFundingSearchParameter.clear();
        projFundingSearchParameter.Priority = i;
        projFundingSearchParameter.SearchCriteria = dictEnum.name2Value(fundingSearchCriteriaListbox.getText(i));
        projFundingSearchParameter.insert();
    }

    ProjFundingSearchParameter::recalculateRuleSearchKeys();

    ttscommit;
}

void setFieldVendorRelation()
{
    if (!Project::Enabled())
    {
        return;
    }

    if (psaVendPWPTxt.AccountCode == TableGroupAll::All)
    {
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).skip(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).allowEdit(false);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).skip(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).allowEdit(false);
    }
    else if (psaVendPWPTxt.AccountCode == TableGroupAll::Table)
    {
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).skip(false);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).allowEdit(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).skip(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).allowEdit(false);
    }
    else
    {
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).skip(false);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, VendGroup)).allowEdit(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).skip(true);
        psaVendPWPTxt_ds.object(fieldNum(PSAVendPWPTxt, AccountNum)).allowEdit(false);
    }
}

public void setPrioritySearchCriteriaFirst()
{
    DictEnum                    dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    str selectedTxt;

    //
    // Priority is designed to be either the First or Last item in the search criteria list
    // This method will move Priority to the top of the list
    //
    selectedTxt = fundingSearchCriteriaListbox.getText(dictEnum.values()-1);
    if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority)
    {
        //
        // Priority is at bottom of list
        // Move to top of list and renumber other items down one position
        //
        fundingSearchCriteriaListbox.delete(selectedTxt);
        fundingSearchCriteriaListbox.insert(selectedTxt,0);
        fundingSearchCriteriaListbox.selection(0);
    }
}

public void setPrioritySearchCriteriaLast()
{
    DictEnum                    dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    str selectedTxt;

    //
    // Priority is designed to be either the First or Last item in the search criteria list
    // This method will move Priority to the end of the list
    //
    selectedTxt = fundingSearchCriteriaListbox.getText(0);
    if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority)
    {
        //
        // Assume priority is at top of list
        // Move to bottom of list and renumber other items up one position
        //
        fundingSearchCriteriaListbox.delete(selectedTxt);
        fundingSearchCriteriaListbox.insert(selectedTxt,dictEnum.values()-1);
        fundingSearchCriteriaListbox.selection(dictEnum.values());
    }
}

void setupAllowEdit(boolean _allow)
{
    // Set edit status of the Show item consumption in overview, connection to the inventory dimensions
    showGridProject.allowEdit(_allow);
}

edit ProjUtilOption timeMaterialBillableType(boolean set, ProjUtilOption type)
{
    ProjUtilTypes t;

    // Terminate if user does not haveappropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return type;
    }

    select firstonly t where t.ProjType == ProjType::TimeMaterial;
    if (set)
    {
        ttsbegin;
        t.selectForUpdate(true)            ;
        t.Billable = type;
        t.update();
        ttscommit;
    }

    return t.Billable;
}

