public void uncheckAndDisableAbsence(ProjValParameter _validationParameter)
{
    if (_validationParameter == ProjValParameter::Mandatory &&
        projParameters.TimesheetValidateAbsence)
    {
        projParameters_ds.object(fieldNum(ProjParameters, TimesheetValidateAbsence)).setValue(false);
    }

    element.enableDisableValidationFields();
}

/// <summary>
/// Enables or disables Item Consumption based on Item requirement
/// </summary>
public void enableDisableItemConsumption()
{
    if(projParameters_ds.object(fieldNum(ProjParameters, CreateItemReqForPO)).getValue() == NoYes::No)
    {
        projParameters_ds.object(fieldNum(ProjParameters, AutomaticItemConsumption)).setValue(NoYes::No);
    }
    projParameters_ds.object(fieldNum(ProjParameters, AutomaticItemConsumption)).allowEdit(projParameters.CreateItemReqForPO);
}

/// <summary>
/// Enables or disables Block vendor payments till PM confirmation based on Manual verification by PM is required
/// </summary>
public void enableDisableBlockVendorPayments()
{
    if(projParameters_ds.object(fieldNum(ProjParameters, SubcontractInvoice)).getValue() == NoYes::No)
    {
        projParameters_ds.object(fieldNum(ProjParameters, BlockVendorPayment)).setValue(NoYes::No);
    }
    projParameters_ds.object(fieldNum(ProjParameters, BlockVendorPayment)).allowEdit(projParameters.SubcontractInvoice);
}

private void enableDisableRevRecInContractCurrency()
{
    if (projParameters_ds.object(fieldNum(ProjParameters, EnableContractCurrencyForRevRec)).getValue() == NoYes::No)
    {
        projParameters_ds.object(fieldNum(ProjParameters, RevRecInContractCurrency)).setValue(NoYes::No);
    }
    projParameters_ds.object(fieldNum(ProjParameters, RevRecInContractCurrency)).allowEdit(projParameters.EnableContractCurrencyForRevRec);
}

edit ProjInvoiceProposalCreationSubTasksNum numOfInvoiceProposalCreationSubTasks(boolean set, int _numSubTasks)
{
    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return ProjParameters.getMaxBundlesForInvoiceProposalCreation();
    }

    if (set)
    {
        projParameters_ds.object(fieldNum(ProjParameters, MaxBundlesForInvoiceProposalCreation)).setValue(_numSubTasks);
    }

    return ProjParameters.getMaxBundlesForInvoiceProposalCreation();
}

edit ProjMaxTasksForEstimateBatch numOfEstimateSubTasks(boolean _set, int _numSubTasks)
{
    // Terminate if user does not have appropriate rights to access this table.
    if (!this.hasRightsToUtilTable(AccessType::View) || (_set && !this.hasRightsToUtilTable(AccessType::Edit)))
    {
        return ProjParameters.getMaxTasksForEstimateBatch();
    }

    if (_set)
    {
        projParameters_ds.object(fieldNum(ProjParameters, MaxTasksForEstimateBatch)).setValue(_numSubTasks);
    }

    return ProjParameters.getMaxTasksForEstimateBatch();
}

public Common  lookupProjTransCFOP(FormReferenceControl _formReferenceControl, CustVendLocation_BR _location)
{
    CFOPTable_BR           recordSelected;

    SysReferenceTableLookup sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(CFOPTable_BR), _formReferenceControl);

    sysReferenceTableLookup.addLookupfield(fieldNum(CFOPTable_BR, cfopId));
    sysReferenceTableLookup.addLookupfield(fieldNum(CFOPTable_BR, Name));

    sysReferenceTableLookup.parmQuery(ProjParameters::projTransCFOPLookupQuery_BR(_location));

    recordSelected = sysReferenceTableLookup.performFormLookup();

    return recordSelected;
}

public int active()
{
    int ret;

    ret = super();

    element.enableDisableFundingFields();
    element.enableDisableCarryForwardFields();
    element.enableDisableCommittedCostBudgetReservation();
    element.enableDisableCommittedCostPurchaseOrder();
    element.enableDisableTimeRangeOverlap();
    element.enableDisableValidationFields();
    element.enableDisableItemConsumption();
    element.enableDisableRevRecInContractCurrency();
    element.enableDisableBlockVendorPayments();
    return ret;
}

public boolean validateWrite()
{
    boolean         ret;
    DictEnum        dictEnum=new DictEnum(enumNum(ProjReduceCostPriority));

    ret = super();

    if (ret)
    {
        if ((hourSelected.getText(0)
        && dictEnum.name2Value(hourSelected.getText(0)) != ProjReduceCostPriority::ParentProject
        && dictEnum.name2Value(hourSelected.getText(0)) != ProjReduceCostPriority::Project
        && dictEnum.name2Value(hourSelected.getText(0)) != ProjReduceCostPriority::EstimateProject
        && dictEnum.name2Value(hourSelected.getText(0)) != ProjReduceCostPriority::ActivityNum)

        || (expenseSelected.getText(0)
        && dictEnum.name2Value(expenseSelected.getText(0)) != ProjReduceCostPriority::ParentProject
        && dictEnum.name2Value(expenseSelected.getText(0)) != ProjReduceCostPriority::Project
        && dictEnum.name2Value(expenseSelected.getText(0)) != ProjReduceCostPriority::EstimateProject
        && dictEnum.name2Value(expenseSelected.getText(0)) != ProjReduceCostPriority::ActivityNum)

        || (itemSelected.getText(0)
        && dictEnum.name2Value(itemSelected.getText(0)) != ProjReduceCostPriority::ParentProject
        && dictEnum.name2Value(itemSelected.getText(0)) != ProjReduceCostPriority::Project
        && dictEnum.name2Value(itemSelected.getText(0)) != ProjReduceCostPriority::EstimateProject
        && dictEnum.name2Value(itemSelected.getText(0)) != ProjReduceCostPriority::ActivityNum))
        {
            return checkFailed("@SYS108451");
        }

        if (quotation_DefaultForecastItemAllocation.text()
            && itemLumpSumAllocationKey.text()
            && quotation_DefaultForecastItemAllocation.text() == itemLumpSumAllocationKey.text())
        {
            return checkFailed(strFmt("@SYS91477", "@SYS24158", "@SYS108442"));
        }
    }

    return ret;
}

public void write()
{
    super();

    this.formRun().doApply();
}

public edit ProjStageLanguageId ProjStageSelectedLanguage(boolean _set = false, ProjParameters _projParameters = ProjParameters, ProjStageLanguageId _language = projStageLanguage)
{
    if (_set && projStageLanguage != _language)
    {
        projStageLanguage = _language;

        TmpProjStatusSetup::loadProjectStages(ProjParameters_DS.ProjStageSelectedLanguage(false, ProjParameters), tmpProjStatusSetup);
        tmpProjStatusSetup_DS.refresh();
        TmpProjStatusSetup_DS.research();
    }

    return projStageLanguage;
}

