/// <summary>
/// Filters out non-fee journals.
/// </summary>
public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(ProjJournalName), _formControl);
    Query                   query = new Query();
    QueryBuildDataSource    oQueryDS;

    oQueryDS = query.addDataSource(tableNum(ProjJournalName));
    sysTableLookup.addLookupfield(fieldNum(ProjJournalName, JournalNameId));
    sysTableLookup.addLookupfield(fieldNum(ProjJournalName, Description));
    oQueryDS.addRange(fieldNum(ProjJournalName, JournalType)).value(queryValue(ProjJournalType::Revenue));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

