public void lookup(FormControl _formControl, str _filterStr)
{
    if (!Project::Enabled())
    {
        return;
    }

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProjCategory), _formControl);
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(ProjCategory));
    QueryBuildRange queryBuildRangeIndirect = queryBuildDataSource.addRange(fieldNum(ProjCategory, psaIndirectComponent));
    QueryBuildRange queryBuildRangeRetention = queryBuildDataSource.addRange(fieldNum(ProjCategory, psaCustPaymentRetention));
    QueryBuildRange queryBuildRangeCategoryType = queryBuildDataSource.addRange(fieldNum(ProjCategory, CategoryType));
    QueryBuildRange queryBuildRangeActive = queryBuildDataSource.addRange(fieldNum(ProjCategory, Active));
    QueryBuildRange queryBuildRangeSubscription = queryBuildDataSource.addRange(fieldNum(ProjCategory, SetupSubscription));
    QueryBuildRange queryBuildRangeEstimate = queryBuildDataSource.addRange(fieldNum(ProjCategory, SetupEstimate));

    queryBuildRangeIndirect.value(queryValue(NoYes::No));
    queryBuildRangeRetention.value(queryValue(NoYes::No));
    queryBuildRangeCategoryType.value(queryValue(ProjTransType::Revenue));
    queryBuildRangeActive.value(queryValue(NoYes::Yes));
    queryBuildRangeSubscription.value(queryValue(NoYes::No));
    queryBuildRangeEstimate.value(queryValue(NoYes::No));

    sysTableLookup.addLookupfield(fieldNum(ProjCategory, CategoryId));
    sysTableLookup.addLookupfield(fieldNum(ProjCategory, Name));
    sysTableLookup.addLookupfield(fieldNum(ProjCategory, CategoryType));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

