public void performCategoryLookup(ProjCategoryType _categoryType, FormControl _formControl)
{
    ProjCategoryLookup      projCategoryLookup;
    SysTableLookup          tableLookup;

    projCategoryLookup = new ProjCategoryLookup();
    projCategoryLookup.parmCategoryType(_categoryType);

    tableLookup = SysTableLookup::newParameters(tableNum(ProjCategory), _formControl);
    tableLookup.parmQuery(projCategoryLookup.buildQuery(tableNum(ProjIntercompanyParameters), '', 0));
    tableLookup.addLookupfield(fieldNum(ProjCategory, CategoryId));
    tableLookup.addLookupfield(fieldNum(ProjCategory, Name));
    tableLookup.addLookupfield(fieldNum(ProjCategory, CategoryType));
    tableLookup.performFormLookup();
}

