private boolean existProjectPrepayments()
{
    PurchPrepayTable purchPrepayTable;
    PurchTable purchTable;

    select firstonly RecId from purchPrepayTable
        where purchPrepayTable.IsDeleted == NoYes::No
        join RecId from purchTable
            where purchTable.PurchId == purchPrepayTable.PurchTable &&
                  purchTable.ProjId != '';

    return (purchPrepayTable.RecId != 0);
}

/// <summary>
/// Warn user if there are existing project prepayments
/// </summary>
/// <returns>True is value is modified</returns>
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        // If there are existing prepayments that would be affected by changing this parameter, display warning message
        if (this.existProjectPrepayments())
        {
            warning("@Proj:ProjExistingPrepayments");
        }
    }

    return ret;
}

/// <summary>
/// Get user input on whether hour base scheduling is enabled.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    if (!ResUtil::isPerformanceEnhancementFeatureEnabled(true) && PSAParameters_EnableHourScheduling.value() == NoYes::Yes)
    {
        Box::info("@Res:UpdateRollUpMessage");
    }
    return ret;
}

