void clicked()
{
    DictEnum    dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    int         selectedIndex;
    str         selectedTxt;

    if (!ProjInvoiceTable::isAdvancedFundingEnabled() || (ProjInvoiceTable::isAdvancedFundingEnabled() && element.promptChangeFundingPriority()))
    {
        selectedIndex = fundingSearchCriteriaListbox.selection();
        selectedTxt = fundingSearchCriteriaListbox.getText(selectedIndex);
        if (selectedIndex > 0) //0 means the top one, or the list box is empty
        {
            if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority)
            {
                element.setPrioritySearchCriteriaFirst();
            }
            else
            {
                if (element.checkFundingSearchOrder(selectedIndex-1))
                {
                    fundingSearchCriteriaListbox.delete(selectedTxt);
                    fundingSearchCriteriaListbox.insert(selectedTxt,selectedIndex-1);
                    fundingSearchCriteriaListbox.selection(selectedIndex-1);
                }
            }
        }

        element.enableDisableFundingButtons();

        super();
    }
}

void clicked()
{
    DictEnum        dictEnum = new DictEnum(enumNum(ProjFundingSearchCriteria));
    int             selectedIndex;
    int             counter;
    str             selectedTxt;

    if (!ProjInvoiceTable::isAdvancedFundingEnabled() || (ProjInvoiceTable::isAdvancedFundingEnabled() && element.promptChangeFundingPriority()))
    {
        counter = dictEnum.values();
        selectedIndex = fundingSearchCriteriaListbox.selection();
        selectedTxt = fundingSearchCriteriaListbox.getText(selectedIndex);
        if (selectedIndex < counter-1)
        {
            if (dictEnum.name2Value(selectedTxt) == ProjFundingSearchCriteria::Priority )
            {
                element.setPrioritySearchCriteriaLast();
            }
            else
            {
                if (element.checkFundingSearchOrder(selectedIndex+1))
                {
                    fundingSearchCriteriaListbox.delete(selectedTxt);
                    fundingSearchCriteriaListbox.insert(selectedTxt,selectedIndex+1);
                    fundingSearchCriteriaListbox.selection(selectedIndex+1);
                }
            }
        }

        element.enableDisableFundingButtons();

        super();
    }
}

