void clicked()
{
    int     counter;
    str     selectedStr;

    #DEFINE.AllowablePriorityNum(5)

    super();

    selectedStr=hourRemaining.getText(hourRemaining.selection());
    if (selectedStr)
    {
        counter=hourSelected.count();
        if (counter >= #AllowablePriorityNum )
        {
            throw error(strFmt("@SYS107338",int2str(#AllowablePriorityNum)));
        }
        hourSelected.add(selectedStr);
        hourRemaining.delete(selectedStr);
        hourMinRequirement.add(selectedStr);
        if (hourSelected.count() == 1)
        {
            hourMinRequirement.selectText(hourSelected.getText(0));
        }
    }
}

void clicked()
{
    str             selectedStr;
    DictEnum        dictEnum=new DictEnum(enumNum(ProjReduceCostPriority));
    str             tmp;

    super();

    selectedStr = hourSelected.getText(hourSelected.selection());
    if (selectedStr)
    {
        hourSelected.delete(selectedStr);
        tmp = hourMinRequirement.getText(hourMinRequirement.selection());
        hourMinRequirement.delete(selectedStr);
        if (selectedStr == tmp)
        {
            //remove the minimum requirement, set the first priority as minimum requirement
            if (hourSelected.count() > 0)
            {
                hourMinRequirement.selectText(hourSelected.getText(0));
            }
            else
            {
                hourMinRequirement.setEditText('');
            }
        }
        else
        {
            hourMinRequirement.selectText(tmp);
        }
    }

    element.loadValues(dictEnum, hourRemaining, hourSelected);
    this.setFocus();
}

void clicked()
{
    int selectedIndex;
    str selectedTxt;

    selectedIndex=hourSelected.selection();
    selectedTxt=hourSelected.getText(selectedIndex);
    if (selectedIndex != 0) //0 means the top one, or the list box is empty
    {
        hourSelected.delete(selectedTxt);
        hourSelected.insert(selectedTxt,selectedIndex-1);
        hourSelected.selection(selectedIndex-1);
    }

    super();
}

void clicked()
{
    int selectedIndex;
    int selectedCount;
    str selectedTxt;

    selectedCount=hourSelected.count();
    selectedIndex=hourSelected.selection();
    selectedTxt=hourSelected.getText(selectedIndex);
    if (selectedCount != 0 && selectedIndex < selectedCount-1)
    {
        hourSelected.delete(selectedTxt);
        hourSelected.insert(selectedTxt,selectedIndex+1);
        hourSelected.selection(selectedIndex+1);
    }
    super();
}

