public boolean modified()
{
    boolean ret;
    ProjParameters  projParameter;

    projParameter=ProjParameters::find();
    ret = super();

    if (ret)
    {
        if (ProjAllocateKey::checkExistLines(expenseLumpSumAllocationKey.text()))
        {
            return checkFailed("@SYS108452");
        }

        if (expenseLumpSumAllocationKey.text() &&  expenseLumpSumAllocationKey.text()!=projParameter.ExpenseLumpSumAllocationKey && ProjForecastCost::existProjLumpSumAllocation(expenseLumpSumAllocationKey.text()))
        {
            return checkFailed("@SYS108444");
        }
    }

    if (projParameter.ExpenseLumpSumAllocationKey != '' && expenseLumpSumAllocationKey.text() != projParameter.ExpenseLumpSumAllocationKey)
    {
        throw warning("@SYS108450");
    }

    return ret;
}

