public void lookup()
{
    ForecastItemAllocation::lookupNotExistLines(this);
}

public boolean modified()
{
    boolean ret;
    ProjParameters  projParameter;

    projParameter=ProjParameters::find();
    ret = super();
    if (ret)
    {
        if (this.text() && this.text() == quotation_DefaultForecastItemAllocation.text())
        {
            return checkFailed(strFmt("@SYS91477", "@SYS108442", "@SYS24158"));
        }

        if (ForecastItemAllocation::checkExistLines(itemLumpSumAllocationKey.text()))
        {
            return checkFailed("@SYS108452");
        }

        if (itemLumpSumAllocationKey.text() && itemLumpSumAllocationKey.text()!=projParameter.ItemLumpSumAllocationKey && ForecastSales::existProjLumpSumAllocation(itemLumpSumAllocationKey.text()))
        {
            return checkFailed("@SYS108444");
        }
    }

    if (projParameter.ItemLumpSumAllocationKey != '' && itemLumpSumAllocationKey.text() != projParameter.ItemLumpSumAllocationKey)
    {
        throw warning("@SYS108450");
    }

    return ret;
}

