/// <summary>
/// Disables the column when 'enable multiple contract lines for a project' feature is turned on.
/// </summary>
protected void initialize()
{
    super();

    this.visible(!element.enableMultipleContractLinesForProject);
    this.enabled(!element.enableMultipleContractLinesForProject);
}

public void clicked()
{
    if (!ProjStatusTypeRule::enableRule(tmpProjStatusSetup.ProjStatus, ProjType::FixedPrice, ProjStatusRule::AllowUse, this.checked()))
    {
        this.checked(!this.checked());
    }

    super();
}

/// <summary>
/// Disables the column when 'enable multiple contract lines for a project' feature is turned on.
/// </summary>
protected void initialize()
{
    super();

    this.visible(!element.enableMultipleContractLinesForProject);
    this.enabled(!element.enableMultipleContractLinesForProject);
}

