void setFieldAccess()
{
    switch (projCostSales)
    {
        case ProjCostSales::Cost    :
            costTabPage.visible(true);
            saleTabPage.visible(false);
            accruedProfitLoss.visible(false);
            accruedWIP.visible(false);
            costProfitLoss.visible(true);
            costWIP.visible(true);
            costPayroll.visible(true);
            break;

        case ProjCostSales::Sales   :
            costTabPage.visible(false);
            saleTabPage.visible(true);
            accruedProfitLoss.visible(true);
            accruedWIP.visible(true);
            costProfitLoss.visible(false);
            costWIP.visible(false);
            costPayroll.visible(false);
            break;
    }
}

