AmountMST totalProjTypePostingType(
    ProjTransType       _transType,
    LedgerPostingType   _postingType)
{
    TmpProjPeriodicLedger   tmpProjPeriodicLedgerTotal;
    AmountMST               debit,
                            credit;

    selectionUpdate.observe();

    tmpProjPeriodicLedgerTotal.setTmpData(tmpProjPeriodicLedger);

    select sum(AmountMST) from tmpProjPeriodicLedgerTotal
        where tmpProjPeriodicLedgerTotal.TransType      == _transType   &&
              tmpProjPeriodicLedgerTotal.PostingType    == _postingType;

    debit   = tmpProjPeriodicLedgerTotal.AmountMST;

    select sum(AmountMST) from tmpProjPeriodicLedgerTotal
        where tmpProjPeriodicLedgerTotal.TransType          == _transType   &&
              tmpProjPeriodicLedgerTotal.OffsetPostingType  == _postingType;

    credit  = tmpProjPeriodicLedgerTotal.AmountMST;

    return (debit-credit);
}

Map updatedRecs(Args     args)
{
    Map                 updatedRecs;
    FormDataSource      formDataSource;

    ProjCostTrans       projCostTransDS;
    ProjCostTrans       projCostTrans;

    ProjEmplTrans       projEmplTrans;
    ProjEmplTrans       projEmplTransDS;

    ProjItemTrans       projItemTransDS;
    ProjItemTrans       projItemTrans;

    ProjRevenueTrans    projRevenueTransDS;
    ProjRevenueTrans    projRevenueTrans;

    updatedRecs = new Map(Types::Int64,Types::Record);

    formDataSource  = FormDataUtil::getFormDataSource(args.record());
    if (formDataSource)
    {
        switch (args.dataset())
        {
            case tableNum(ProjCostTrans):
                projCostTransDS   = args.record();
                for (projCostTransDS = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projCostTransDS; projCostTransDS = formDataSource.getNext())
                {
                    projCostTrans   = ProjCostTrans::find(projCostTransDS.TransId);
                    updatedRecs.insert(projCostTrans.RecId,projCostTrans);
                }
                break;

            case tableNum(ProjEmplTrans):
                projEmplTransDS   = args.record();
                for (projEmplTransDS = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projEmplTransDS; projEmplTransDS = formDataSource.getNext())
                {
                    projEmplTrans   = ProjEmplTrans::find(projEmplTransDS.TransId);
                    updatedRecs.insert(projEmplTrans.RecId,projEmplTrans);
                }
                break;

            case tableNum(ProjItemTrans):
                projItemTransDS   = args.record();
                for (projItemTransDS = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projItemTransDS; projItemTransDS = formDataSource.getNext())
                {
                    projItemTrans   = ProjItemTrans::find(projItemTransDS.ProjTransId);
                    updatedRecs.insert(projItemTrans.RecId,projItemTrans);
                }
                break;

            case tableNum(ProjRevenueTrans):
                projRevenueTransDS   = args.record();
                for (projRevenueTransDS = formDataSource.getFirst(true) ?
                     formDataSource.getFirst(true) : formDataSource.cursor();
                     projRevenueTransDS; projRevenueTransDS = formDataSource.getNext())
                {
                    projRevenueTrans   = ProjRevenueTrans::find(projRevenueTransDS.TransId);
                    updatedRecs.insert(projRevenueTrans.RecId,projRevenueTrans);
                }
                break;
        }
    }

    return updatedRecs;
}

// If called from a single transaction update related transaction
void updateRelatedForm()
{
    FormRun             callerForm  = element.args().caller();
    FormDataSource      fds;
    Args                argsTrans = new Args();
    Map                 updatedRecs;

    argsTrans.record(element.args().record());
    if (argsTrans.record())
    {
        updatedRecs     = element.updatedRecs(argsTrans);

        if (!updatedRecs.empty())
        {
            RunBaseMultiParmApplication::formCacheUpdate(callerForm,element.args().record(),updatedRecs.pack());
            fds = FormDataUtil::getFormDataSource(element.args().record());
            fds.active();
        }
    }
}

void init()
{
    Query                       q = new Query();
    QueryBuildDataSource        qb_TmpProjPeriodic;

    super();

    qb_TmpProjPeriodic    = q.addDataSource(tableNum(TmpProjPeriodic));
    criteriaProjId        = qb_TmpProjPeriodic.addRange(fieldNum(TmpProjPeriodic, ProjId));

    this.query(q);
}

public int active()
{
    int ret;

    ret = super();

    tmpProjPeriodicCost_ds.executeQuery();
    tmpProjPeriodicSale_ds.executeQuery();

    element.setButtonAccess();
    element.setFieldVisible();

    return ret;
}

void delete()
{
    TmpProjPeriodicCost     tmpProjPeriodicCost_local;
    TmpProjPeriodicSale     tmpProjPeriodicSale_local;
    TmpProjPeriodicLedger   tmpProjPeriodicLedger_local;

    tmpProjPeriodicCost_local.setTmpData(tmpProjPeriodicCost);

    delete_from tmpProjPeriodicCost_local
        where tmpProjPeriodicCost_local.TransId    == tmpProjPeriodic.TransId;

    tmpProjPeriodicCost.setTmpData(tmpProjPeriodicCost_local);

    tmpProjPeriodicSale_local.setTmpData(tmpProjPeriodicSale);

    delete_from tmpProjPeriodicSale_local
        where tmpProjPeriodicSale_local.TransId    == tmpProjPeriodic.TransId;

    tmpProjPeriodicSale.setTmpData(tmpProjPeriodicSale_local);

    tmpProjPeriodicLedger_local.setTmpData(tmpProjPeriodicLedger);

    delete_from tmpProjPeriodicLedger_local
        where tmpProjPeriodicLedger_local.TransId    == tmpProjPeriodic.TransId;

    tmpProjPeriodicLedger.setTmpData(tmpProjPeriodicLedger_local);

    super();

    tmpProjPeriodicCost_ds.research();
    tmpProjPeriodicSale_ds.research();
    tmpProjPeriodicLedger_ds.research();

    element.setButtonAccess();
}

public void executeQuery()
{
    QueryBuildDataSource  qbds;
    Query                 query;

    query = this.query();
    qbds = query.dataSourceName(this.name());
    qbds.clearRanges();
    qbds.addRange(fieldNum(TmpProjPeriodicCost, TransId)).value(tmpProjPeriodic.TransId);

    super();
}

public void executeQuery()
{
    QueryBuildDataSource  qbds;
    Query                 query;

    query = this.query();
    qbds = query.dataSourceName(this.name());
    qbds.clearRanges();
    qbds.addRange(fieldNum(TmpProjPeriodicSale, TransId)).value(tmpProjPeriodic.TransId);

    super();
}

void clicked()
{
    Args                            args;
    ProjPeriodPostingLedger         projPeriod;
    TmpProjPeriodic                 tmpProjPeriodic_local;
    TmpProjPeriodicCost             tmpProjPeriodicCost_local;
    TmpProjPeriodicSale             tmpProjPeriodicSale_local;
    TmpProjPeriodicLedger           tmpProjPeriodicLedger_local;

    args = new Args();
    args.caller(element);
    args.parmEnumType(enumNum(ProjCostSales));
    args.parmEnum(enum2int(element.costSales()));

    super();

    projPeriod  = ProjPeriodPostingLedger::construct(args);
    projPeriod.getLast();

    if (element.costSales() == ProjCostSales::Cost)
    {
        projPeriod.parmProjLedgerStatus(element.projLedgerStatus());
        projPeriod.parmtransActionDate(element.parmPostingDate());
        projPeriod.parmAcknowledgementDate(element.parmAcknowledgementDate());
    }
    else
    {
        projPeriod.parmReverseTurnover(element.parmReverseTurnover());
        projPeriod.parmtransActionDate(element.parmPostingDate());
        projPeriod.parmAcknowledgementDate(element.parmAcknowledgementDate());
    }

    if (projPeriod.prompt())
    {
        // Create the filtered list of records
        while select tmpProjPeriodic
        {
            tmpProjPeriodic_local.clear();
            tmpProjPeriodic_local.data(tmpProjPeriodic);
            tmpProjPeriodic_local.insert();
        }
        projPeriod.parmTmpProjPeriodic(tmpProjPeriodic_local);

        while select tmpProjPeriodicCost
        {
            tmpProjPeriodicCost_local.clear();
            tmpProjPeriodicCost_local.data(tmpProjPeriodicCost);
            tmpProjPeriodicCost_local.insert();
        }
        projPeriod.parmTmpProjPeriodicCost(tmpProjPeriodicCost_local);

        while select tmpProjPeriodicSale
        {
            tmpProjPeriodicSale_local.clear();
            tmpProjPeriodicSale_local.data(tmpProjPeriodicSale);
            tmpProjPeriodicSale_local.insert();
        }
        projPeriod.parmTmpProjPeriodicSale(tmpProjPeriodicSale_local);

        projPeriod.runOperation();

        tmpProjPeriodic_local = null;
        tmpProjPeriodic.setTmpData(tmpProjPeriodic_local);
        tmpProjPeriodic_ds.research();

        tmpProjPeriodicCost_local = null;
        tmpProjPeriodicCost.setTmpData(tmpProjPeriodicCost_local);
        tmpProjPeriodicCost_ds.research();

        tmpProjPeriodicSale_local = null;
        tmpProjPeriodicSale.setTmpData(tmpProjPeriodicSale_local);
        tmpProjPeriodicSale_ds.research();

        tmpProjPeriodicLedger.setTmpData(tmpProjPeriodicLedger_local);
        tmpProjPeriodicLedger_ds.research();

        element.setButtonAccess();
    }
    else
    {
        tmpProjPeriodic_ds.research();
        tmpProjPeriodicCost_ds.research();
        tmpProjPeriodicSale_ds.research();
        tmpProjPeriodicLedger_ds.research();
    }

    selectionUpdate.markChanged();

    element.updateRelatedForm();
}

void clicked()
{
    Args                        args;
    ProjPeriodPostingSelect     projPeriod;

    args = new Args();
    args.caller(element);
    args.parmEnumType(enumNum(ProjCostSales));
    args.parmEnum(enum2int(element.costSales()));

    super();

    if (element.parmProjId())
    {
        projPeriod  = ProjPeriodPostingSelect::newProj(args,element.parmProjId());
    }
    else if (element.parmProjInvoiceId())
    {
        projPeriod  = ProjPeriodPostingSelect::newProjInvoiceId(args,element.parmProjInvoiceId());
    }
    else if (element.callerDataRecord())
    {
        projPeriod  = ProjPeriodPostingSelect::newProjTransId(args);
    }
    else
    {
        projPeriod  = ProjPeriodPostingSelect::construct(args);
    }

    if (projPeriod.prompt())
    {
        if (element.costSales() == ProjCostSales::Cost)
        {
            element.projLedgerStatus(projPeriod.projLedgerStatus());
        }

        projPeriod.createTrans();

        tmpProjPeriodic.clear();
        tmpProjPeriodicCost.clear();
        tmpProjPeriodicSale.clear();
        tmpProjPeriodicLedger.clear();

        tmpProjPeriodic.setTmpData(projPeriod.tmpProjPeriodic());
        tmpProjPeriodicCost.setTmpData(projPeriod.tmpProjPeriodicCost());
        tmpProjPeriodicSale.setTmpData(projPeriod.tmpProjPeriodicSale());
        tmpProjPeriodicLedger.setTmpData(projPeriod.tmpProjPeriodicLedger());
        element.parmPostingDate(projPeriod.parmTransActionDate());

        if (element.costSales() == ProjCostSales::Sales)
        {
            element.parmReverseTurnover(projPeriod.parmReverseTurnover());
        }

        tmpProjPeriodic_ds.research();
        tmpProjPeriodicCost_ds.research();
        tmpProjPeriodicSale_ds.research();
        tmpProjPeriodicLedger_ds.research();

        selectionUpdate.markChanged();
        element.setButtonAccess();
    }
}

