[Form]
public class ProjProjectContractsListPage extends FormRun implements OfficeIMenuCustomizer
{
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">
/// The menu options to be customized.
/// </param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    Set entitiesToExclude = new Set(Types::String);
    entitiesToExclude.add(dataentityviewstr(ProjCDSProjectContractEntity));

    Project::removeEntitiesFromOpenOfficeMenu(_menuOptions, entitiesToExclude);
}

/// <summary>
/// Initialize the company lookup value to be this LE.
/// </summary>
public void init()
{
    super();

    CtrlCompanyLookup.text(curExt());

    boolean projOperationsIntegrationFeatureEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();

    if (projOperationsIntegrationFeatureEnabled)
    {
        DeleteButton.visible(false);
        DeleteButton.enabled(false);
        CaseButton.visible(false);
        CaseButton.enabled(false);
        PrintMgmt.visible(false);
        PrintMgmt.enabled(false);
        TradeAgreementGroup.visible(false);
        TradeAgreementGroup.enabled(false);
        Plan.visible(false);
        Plan.enabled(false);
        NewGroup2.visible(false);
        NewGroup2.enabled(false);
        Service.visible(false);
        Service.enabled(false);
        Control.visible(false);
        Control.enabled(false);
        CostPriceGroup.visible(false);
        CostPriceGroup.enabled(false);
        CtrlItemRequirements.visible(false);
        CtrlItemRequirements.enabled(false);
        ProductionOrders.visible(false);
        ProductionOrders.enabled(false);
        CtrlSalesOrders.visible(false);
        CtrlSalesOrders.enabled(false);
        CtrlSalesAgreement.visible(false);
        CtrlSalesAgreement.enabled(false);

        boolean poEnabledForProjOps = FeatureStateProvider::IsFeatureEnabled(ProjOpsProjectPurchaseOrderFeature::instance());
        // when <ProjOpsProjectPurchaseOrderFeature > is turned on, enable Purchase orders, Purchase agreements, Purchase requisitions, request for quotations.
        CtrlItemTasks.visible(poEnabledForProjOps);
        CtrlItemTasks.enabled(poEnabledForProjOps);
        CtrlPurchaseAgreement.visible(poEnabledForProjOps);
        CtrlPurchaseAgreement.enabled(poEnabledForProjOps);

        ProjInvoiceTable_ds.object(fieldNum(ProjInvoiceTable, ProjDataSource)).visible(false);
    }
}

void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (projInvoiceTable_ds.cursor() == null)
    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Project, PrintMgmtNodeType::ProjInvoiceTable);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(projInvoiceTable_ds.cursor());
    setupContext.parmCountryRegionId(LogisticsPostalAddress::findByLocation(projInvoiceTable.InvoiceLocation).CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

public boolean modified()
{
    boolean                 ret;
    QueryBuildDataSource    qbds;
    boolean                 enabled;

    ret = super();

    qbds = ProjInvoiceTable_ds.query().dataSourceName(tablestr(ProjTable));
    Debug::assert(qbds != null);
    if (qbds)
    {
        enabled = CtrlActiveAll.selection() == ProjActiveAll::Active;
        qbds.enabled(enabled);
    }

    ProjInvoiceTable_ds.executeQuery();
    return ret;
}

public void lookup()
{
    Args args = new Args(formStr(ProjResourceCompanyLookup));
    FormRun formRun;

    args.callerFormControl(CtrlCompanyLookup);
    formRun = classfactory.formRunClass(args);
    formRun.init();
    this.performFormLookup(formRun);
    formRun.wait();
    ProjLegalEntityLookupListPageHelper::setFilterValuesFromControl(projInvoiceTable_ds, ctrlCompanyLookup.valueStr());
}
