void enableTransfer()
{
    boolean enable;

    // Enable transfer from button if there is any sales line loaded or we got a record in args.
    enable = (salesLine_ds.numberOfRowsLoaded() != 0)
          || (element.args().record() && element.args().record().RecId);

    projForecastTransferFromQuotationItemReq.enabled(enable);
    projItemReqCopyFromEstimates.enabled(enable);
}

public void  init()
{
    if (element.args().dataset()    == tableNum(SMAServiceOrderTable))
    {
        serviceOrderTable = element.args().record();
        projTable  =  ProjTable::find(serviceOrderTable.ProjId);
    }

    if (element.args().dataset()    == tableNum(ProjTable))
    {
        projTable  = element.args().record();
    }

    if (projTable)
    {
        if (!projTable.type().custAccount())
        {
            throw error("@SYS98240");
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)) ?
        element.args().dataset() == tableNum(PSATmpProjFinancialStatistic) :
        element.args().dataset()    == tableNum(CostControlTransCommittedCost))
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            m_tProjStatisitc = element.args().record();
            projTable  =  ProjTable::find(strLTrim(m_tProjStatisitc.ProjViewId));
        }
        else
        {
            costControlTransCommittedCost  =  element.args().record();
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)) &&
        element.args().dataset() == tableNum(SalesLine) &&
        element.args().caller().name() == menuItemDisplayStr(ProjWBSActivityDetails))
    {
        selectedSalesLine  =  element.args().record();
        isCalledFromWBSActivityDetails = true;
    }

    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)) &&
        element.args().dataset()    == tableNum(SalesLine))
    {
        selectedSalesLine  =  element.args().record();
    }

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        isIndiaIndirectTaxParameterMarked = TaxParameters::checkTaxParameters_IN();
        isIndiaWHTMarked                  = TaxWithholdParameters_IN::checkTaxParameters();
        isSalesLine_INEnabled             = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_IN));
    }
    // </GIN>

    super();

    if (element.args().menuItemName() == menuitemDisplayStr(ProjSalesItemReq_Action))
    {
        salesLine_ds.insertIfEmpty(true);
    }

    // Determine if sales order numbersequence is set to manual
    isSalesNumberSequenceManual = SalesParameters::numRefSalesId().numberSequenceTable().Manual;

    salesTableForm = SalesTableForm::construct((SalesTableFormId::ProjectSalesItem),projTable);

    if (isCalledFromWBSActivityDetails)
    {
        ctrlActiveAll.visible(false);
    }
    else if (element.args().dataset() != tableNum(ProjTable))
    {
        ctrlActiveAll.selection(enum2int(ProjActiveAll::Active));
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    salesLineGrid.visibleColsValue(8+conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));

    salesLine_ds.query(salesTableForm.querySalesLine(salesLine_ds.query()));

    linePosting_LedgerDimension.parmPostingType(LedgerPostingType::InventReceipt);
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    // Update transfer button state
    element.enableTransfer();

    purchCreateFromSalesOrder.enabled(ProjParameters::find().CreateItemReqForPO == NoYes::Yes ? false : true);

    // Determine if the funding source should be displayed
    boolean projItemRequirementMultipleFundingSourcesEnabled = ProjItemRequirementMultipleFundingSourcesFeatureHelper::isFeatureEnabled();
    element.updateFundingSourceInvoiceAccountControlStatus(projItemRequirementMultipleFundingSourcesEnabled, projTable);

    buttonJournalPackingSlip.visible(FeatureStateProvider::isFeatureEnabled(ProjCancelItemReqPackingSlipFeature::instance()));

    //<GIN>
    if (isIndiaIndirectTaxParameterMarked || isIndiaWHTMarked)
    {
        element.setVisible_IN();
    }
    //</GIN>

    // <GTE> <GIN>
    boolean isGTEEnabled = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();
    TransTaxInformation.visible(isIndiaIndirectTaxParameterMarked || isGTEEnabled);
    Tab_TaxItemGroup.visible(!isGTEEnabled);
    Tab_TaxGroup.visible(!isGTEEnabled);
    // </GTE> </GIN>
}

private void updateFundingSourceInvoiceAccountControlStatus(boolean _projItemRequirementMultipleFundingSourcesEnabled, ProjTable _projTable)
{
    if (_projItemRequirementMultipleFundingSourcesEnabled)
    {
        // funding source control is visible when the feature is enabled.
        SalesLine_FundingSource.visible(true);
        Group_InvoiceAccount.visible(true);

        // funding source is mandatory when the feature is enabled and project has contract with funding source.            
        if (_projTable.ProjInvoiceProjId)
        {
            SalesLine_FundingSource.mandatory(true);
            Group_InvoiceAccount.mandatory(true);
        }
        else
        {
            SalesLine_FundingSource.mandatory(false);
            Group_InvoiceAccount.mandatory(false);
        }
    }
    else
    {
        SalesLine_FundingSource.visible(false);
        SalesLine_FundingSource.mandatory(false);
        Group_InvoiceAccount.visible(false);
        Group_InvoiceAccount.mandatory(false);
    }
}

