private void setControlStatusAndDefaultValueForProjFundingSource()
{
    boolean projItemRequirementMultipleFundingSourcesEnabled = ProjItemRequirementMultipleFundingSourcesFeatureHelper::isFeatureEnabled();
    ProjTable selectedProj;

    if (element.SalesLine.ProjId && projItemRequirementMultipleFundingSourcesEnabled)
    {
        selectedProj = projTable::find(element.SalesLine.ProjId);
    }

    element.updateFundingSourceInvoiceAccountControlStatus(projItemRequirementMultipleFundingSourcesEnabled, selectedProj);

    if (selectedProj)
    {
        salesLine_ds.object(fieldNum(SalesLine, ProjFundingSource)).setValue(selectedProj.getProjectDefaultInvoiceAccount(false));

        if (selectedProj.isAssociatedToMultipleFundingSourcesContract())
        {
            salesLine_ds.object(fieldNum(SalesLine, ProjFundingSource)).allowEdit(true);
        }
        else
        {
            salesLine_ds.object(fieldNum(SalesLine, ProjFundingSource)).allowEdit(false);
        }
    }
}

public display CostPrice inventTableCostPrice()
{
    boolean isStocked = salesLine.isStocked();

    // To update cost price according to purchase price of its associated purchase order for both stock and non-stocked items.
    if (salesLine.InventRefType == InventRefType::Purch)
    {
        return this.getCostPriceUsedOnPO();
    }

    if (isStocked)
    {
        InventTable inventTable = salesLine.inventTable();
        InventDimParm       inventDimParmFinancial, inventDimParmActive;
        InventDimGroupSetup inventDimGroupSetup = InventDimGroupSetup::newItemId(salesLine.ItemId);

        inventDimGroupSetup.inventDimParmActiveFinancialInventory(inventDimParmFinancial);
        inventDimGroupSetup.inventDimParmActive(inventDimParmActive);

        InventOnhand inventOnhand = InventOnhand::newParametersActive(salesLine.ItemId, inventDim, inventDimParmFinancial, inventDimParmActive);
        CostPrice costPrice = inventOnhand.costPricePcs();

        if (costPrice)
        {
            if (salesLine.salesUnit != inventTable.inventUnitId())
            {
                return inventTable.convertCostPrice2Amount(salesLine.SalesUnit,
                                                            costPrice,
                                                            salesLine.PriceUnit,
                                                            salesLine.InventDimId,
                                                            inventDim);
            }
            else
            {
                return costPrice;
            }
        }
        else
        {
            if (salesLine.salesUnit != inventTable.inventUnitId())
            {
                return inventTable.convertCostPrice2Amount(salesLine.SalesUnit,
                                                            salesLine.CostPrice,
                                                            salesLine.PriceUnit,
                                                            salesLine.InventDimId,
                                                            inventDim);
            }
            else
            {
                return inventTable.costPcsPrice(salesLine.InventDimId, inventDim);
            }
        }
    }
    else
    {
        SalesQuotationLine  salesQuotationLine;

        select QuotationId from salesQuotationLine
            where salesQuotationLine.QuotationId == salesTable.QuotationId;

        if (salesQuotationLine)
        {
            return salesLine.CostPrice;
        }
        else
        {
            return 0;
        }
    }
}

