/// <summary>
/// Determines whether it is allowed to create an item requirement for a project associated to a contract with multiple funding sources.
/// </summary>
/// <returns>
/// true if it is allowed to create an item requirement for a project associated to a contract with multiple funding sources; otherwise, false.
/// </returns>
protected boolean isItemRequirementAllowedForMultipleFundingSources()
{
    if (element.args().dataset() == tableNum(ProjTable))
    {
        projTable = element.args().record();
        if (!projTable.isItemRequirementAllowedForMultipleFundingSources())
        {
            return false;
        }
    }

    return true;
}

