/// <summary>
/// Calculates cost price of item requirement according to cost price utilized on its associated purchase order.
/// </summary>
/// <returns>
/// Returns cost price of the item requirement.
/// </returns>
protected CostPrice getCostPriceUsedOnPO()
{
    SalesQty salesQty = salesLine.SalesQty;
    PurchLine purchLine = PurchLine::findInventTransId(salesLine.InventRefTransId);

    // For the case of a purchase order created from a consolidated planned purchase order
    // which can be associated with multiple item requirements along with current one,
    // so this purchase order's quantity is set with total sales quantity of its associated item requirements.
    if (purchLine.ReqPOId
        && !purchLine.InventRefId
        && !purchLine.InventRefTransId)
    {
        SalesLine salesLineConsolidatedPO;

        // Get total sales quantity of related item requirement lines, sum up and convert into sales unit of the current item requirement,
        // so that it will be useful to update cost price for current item requirement.
        while select sum(SalesQty) from salesLineConsolidatedPO
            group by SalesUnit, InventDimId
                where salesLineConsolidatedPO.InventRefTransId == salesLine.InventRefTransId
                    && salesLineConsolidatedPO.ItemId == salesLine.ItemId
                    && salesLineConsolidatedPO.RecId != salesLine.RecId
        {
            if (salesLineConsolidatedPO.SalesUnit == salesLine.SalesUnit)
            {
                salesQty += salesLineConsolidatedPO.SalesQty;
            }
            else
            {
                EcoResProductUnitConverter unitConverter = EcoResProductUnitConverter::newGivenUnitSymbolsForReleasedProduct(salesLine.ItemId,
                                                                                                                    salesLineConsolidatedPO.InventDimId,
                                                                                                                    salesLineConsolidatedPO.SalesUnit,
                                                                                                                    salesLine.SalesUnit,
                                                                                                                    NoYes::Yes);
                salesQty += unitConverter.convertValue(salesLineConsolidatedPO.SalesQty);
            }
        }
    }

    PriceCur purchPrice;
    if (purchLine.PurchPrice != 0)
    {
        if (salesQty != 0)
        {
            InventPriceMap inventPriceMap = salesLine.inventTable().inventCostPriceMap(purchLine.InventDimId, purchLine.inventDim(), null);

            AmountCur purchAmount = PriceDisc::price2Amount(purchLine.PurchPrice,
                                                                    purchLine.PriceUnit,
                                                                    purchLine.DiscAmount,
                                                                    purchLine.PurchQty,
                                                                    purchLine.PurchQty,
                                                                    inventPriceMap.Markup(),
                                                                    purchLine.DiscPercent,
                                                                    '',
                                                                    0,
                                                                    true);

            purchPrice = purchAmount / salesQty;
        }
        else
        {
            purchPrice = purchLine.PurchPrice;
        }
    }

    return ExchangeRateHelper::curPrice2CurPrice(purchPrice, purchLine.CurrencyCode, CompanyInfoHelper::standardCurrency());
}

