public void executeQuery()
{
    if (isCalledFromWBSActivityDetails && selectedSalesLine)
    {
        QueryBuildDataSource queryBuildDataSource = salesLine_ds.query().dataSourceTable(tableNum(SalesLine));
        queryBuildDataSource.clearRanges();
        queryBuildDataSource.addRange(fieldNum(SalesLine, RecId)).value(queryValue(selectedSalesLine.RecId));
    }
    else
    {
        switch (ctrlActiveAll.selection())
        {
            case enum2int(ProjActiveAll::Active):
                activeAll.value(queryRangeConcat(queryValue(SalesStatus::None), SalesStatus::Backorder));
                break;

            case enum2int(ProjActiveAll::All):
                activeAll.value(SysQuery::valueUnlimited());
                break;
        }
    }

    logisticsPostalAddressLine_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressLine_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();
}

