public void init()
{
    QueryBuildDataSource oqbds_ProjTable;

    super();

    qbds = this.query().dataSourceTable(tableNum(SalesLine));

    qbds.clearDynalinks();
    qbds.addRange(fieldNum(SalesLine, SalesType)).value(queryValue(SalesType::ItemReq));

    m_oCriteriaProjId = qbds.addRange(fieldNum(SalesLine, ProjId));

    if (! projTable)
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            m_oCriteriaProjId.value(SysQuery::valueNotEmptyString());
        }
        else
        {
            qbds.addRange(fieldNum(SalesLine, ProjId)).value(SysQuery::valueNotEmptyString());
        }
    }

    if (costControlTransCommittedCost)
    {
        qbds.addRange(fieldNum(SalesLine, SalesId)).value(queryValue(costControlTransCommittedCost.RefId));
        qbds.addRange(fieldNum(SalesLine, LineNum)).value(queryValue(costControlTransCommittedCost.LineNum));
    }

    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)) && selectedSalesLine)
    {
        qbds.addRange(fieldNum(SalesLine, RecId)).value(queryValue(selectedSalesLine.RecId));
    }

    activeAll           = qbds.addRange(fieldNum(SalesLine, SalesStatus));

    switch (element.args().dataset())
    {
        case tableNum(PSATmpProjFinancialStatistic):
            m_oProjStatistic = element.args().caller().getProjStatistic();

            if (m_oProjStatistic.parmProjInvoiceProjId())
            {
                oqbds_ProjTable = qbds.addDataSource(tableNum(ProjTable));
                oqbds_ProjTable.addLink(fieldNum(SalesLine, ProjId), fieldNum(ProjTable, ProjId));
                oqbds_ProjTable.fetchMode(0);
                oqbds_ProjTable.joinMode(JoinMode::ExistsJoin);
                m_oCriteriaProjInvoiceProjId = oqbds_ProjTable.addRange(fieldNum(ProjTable, ProjInvoiceProjId));
            }
            break;
    }
}

void  write()
{
    // <GEEPL>
    RefRecId serviceTariffNumber;
    // </GEEPL>

    salesLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    salesTableForm.salesLine_Write(salesLine);

    // <GEEPL>
    serviceTariffNumber = taxServiceTariffLine.ServiceTariffNumber;
    // </GEEPL>

    super();

    salesLine.type().setConfirmedDates(SalesConfirmDatesUIStrategy::construct());

    // <GIN>
    if (!salesLine_IN.RecId)
    {
        // need to force the write in the create case since the form won't do it.
        salesLine_IN.SalesLine = salesLine.RecId;
        salesLine_IN_ds.write();
    }
    // </GIN>

    // <GBR>
    if (BrazilParameters::isEnabled() && !salesLine_BR.RecId)
    {
        // need to force the write in the create case since the form won't do it.
        salesLine_BR.SalesLine = salesLine.RecId;
        salesLine_BR_ds.write();
    }
    // </GBR>

    // <GEEPL>
    // The service tariff relationship is lost during the super() call
    // from write since it is on a different data source. So, copy
    // the original value back in to ensure saving is done correctly.
    if (serviceTariffNumber && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        taxServiceTariffLine.ServiceTariffNumber = serviceTariffNumber;
        taxServiceTariffLine.ParentRecId = salesLine.RecId;
        taxServiceTariffLine.ParentTableId = salesLine.TableId;
    }
    // </GEEPL>

    // <GIN>
    if (isSalesLine_INEnabled)
    {
        salesLine_IN.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesLine);
    }
    // </GIN>

    // <GIN>
    salesTable = salesLine.salesTable();
    salesLine.updateSalesTableWithCustomsExportOrderIN(salesTable, true);
    // </GIN>

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        salesLine_BR.SysExtensionSerializerExtensionMap::insertAfterBaseTable(salesLine);
    }
    // </GBR>

    if (salesLine.isCategoryBased() && !salesLineForeignTradeCategory.SalesLineInventTransId)
    {
        salesLineForeignTradeCategory.initFromSalesLine(salesLine);
        if (salesLineForeignTradeCategory_ds.validateWrite())
        {
            salesLineForeignTradeCategory_ds.write();
        }
        else
        {
            throw Exception::Error;
        }
    }

    if (salesLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(salesLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

void  linkActive()
{
    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
    QueryBuildRange projIdCriteria = queryBuildDataSource.findRange(fieldNum(SalesLine,ProjId));

    while (projIdCriteria &&
           (isConfigurationkeyEnabled(configurationKeyNum(Project)) ? !m_oProjStatistic : true))
    {
        queryBuildDataSource.clearRange(fieldNum(SalesLine,ProjId));
        projIdCriteria  = queryBuildDataSource.findRange(fieldNum(SalesLine,ProjId));
    }

    this.setProjIdRangeByLinkedTable();

    super();

    // Set caption
    if (!salesLine)
    {
        element.setCaptionText();
    }
}

/// <summary>
/// Sets the <c>ProjId</c> range value based on the linked table.
/// </summary>
protected void setProjIdRangeByLinkedTable()
{
    const str star = "*";
    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
    boolean ret;

    switch (element.args().dataset())
    {
        case (tableNum(SMAServiceOrderTable)):
            serviceOrderTable = element.args().record();
            projTable = ProjTable::find(serviceOrderTable.ProjId);
            QueryBuildRange serviceOrderIdCriteria = queryBuildDataSource.findRange(fieldNum(SalesLine,ServiceOrderId));

            if (!serviceOrderIdCriteria)
            {
                serviceOrderIdCriteria = queryBuildDataSource.addRange(fieldNum(SalesLine,ServiceOrderId));
            }
            serviceOrderIdCriteria.value(serviceOrderTable.ServiceOrderId);

            queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId);

            queryBuildDataSource.clearRange(fieldNum(SalesLine,SalesId));
            queryBuildDataSource.clearRange(fieldNum(SalesLine,LineNum));

            break;

        case(tableNum(ProjTable)):
            projTable   = element.args().record();
            ProjId projId = projTable.ProjId;

            queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId + star);
            break;

        case tableNum(ProjInvoiceTable)  :
            ProjInvoiceTable projInvoiceTable = element.args().record();

            while select ProjId from projTable
                where projTable.ProjInvoiceProjId   == projInvoiceTable.ProjInvoiceProjId
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId);
                ret = true;
            }

            if (!ret)
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(SysQuery::valueNotEmptyString());
            }
            break;

        case tableNum(ProjWIPTable):
            ProjWIPTable projWIPTable = element.args().record();

            while select ProjId from projTable
                where projTable.wipProject   == projWIPTable.ProjId
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(projTable.ProjId);
                ret = true;
            }

            if (!ret)
            {
                queryBuildDataSource.addRange(fieldNum(SalesLine,ProjId)).value(SysQuery::valueNotEmptyString());
            }
            break;

        //Link from PSATmpProjFinancialStatistic
        case tableNum(PSATmpProjFinancialStatistic):
            if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
            {
                m_tProjStatisitc = element.args().record();
                PSATmpStr sProjId = m_tProjStatisitc.ProjViewId;
                ProjInvoiceProjId sProjInvoiceProjId = m_oProjStatistic.parmProjInvoiceProjId();

                m_oCriteriaProjId.value('');
                if (sProjInvoiceProjId)
                {
                    m_oCriteriaProjInvoiceProjId.value('');
                }

                if (m_tProjStatisitc.ProjTotal == PSAProjTotal::None)
                {
                    m_oCriteriaProjId.value(queryValue(sProjId));
                }
                else if (m_tProjStatisitc.ProjTotal == PSAProjTotal::LevelTotal)
                {
                    if (m_oProjStatistic.psaParmDisplayLevel() == 0)
                    {
                        if (sProjInvoiceProjId)
                        {
                            m_oCriteriaProjInvoiceProjId.value(sProjInvoiceProjId);
                        }
                        else if (m_oProjStatistic.parmProjId())
                        {
                            if (m_oProjStatistic.parmProjTree())
                            {
                                m_oCriteriaProjId.value(sProjId + star);
                            }
                            else
                            {
                                m_oCriteriaProjId.value(sProjId);
                            }
                        }
                    }
                    else
                    {
                        m_oCriteriaProjId.value(sProjId + star);
                    }
                }
                else
                {
                    if (sProjInvoiceProjId && m_tProjStatisitc.ProjTotal == PSAProjTotal::GrandTotal)
                    {
                        m_oCriteriaProjInvoiceProjId.value(sProjInvoiceProjId);
                    }
                    else
                    {
                        m_oCriteriaProjId.value(strLTrim(sProjId) + star);
                    }
                }
            }
            break;
    }
}

void  initValue()
{
    Date systemDate = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::getSystemDateTime(), DateTimeUtil::getUserPreferredTimeZone()));

    // For category based lines Name should be kept if manually modified.
    keepName = false;

    salesLine.SalesType = SalesType::ItemReq;

    super();

    salesTableForm.salesLine_initValue(salesLine);

    if (serviceOrderTable)
    {
        salesLine.initFromSMAServiceOrderTable(serviceOrderTable);
    }

    if (SalesLine.ProjId && !salesLine.ActivityNumber)
    {
        SalesLineType_ItemReq::setSalesLineReceiptDate(salesLine);
    }

    element.setControlStatusAndDefaultValueForProjFundingSource();
}

void create(boolean append = true)
{
    if (!isSalesNumberSequenceManual)
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)) &&
            projTable && !ProjStatusType::construct(projTable).validateWriteItemRequirement())
        {
            throw error("@SYS18447");
        }
        element.setEditProjIdAllowed(true);
        element.setEditItemOrCategoryAllowed(true);

        super(append);

        // filter exist on itemId
        if (salesLine.ItemId)
        {
            this.itemIdModified(true);
        }
        // filter exists on SalesCategory
        else if (salesLine.SalesCategory)
        {
            this.changedStockedStatus(true);
        }

        salesLine_ds.refresh();

        salesTableForm.salesLine_Create(salesLine);
    }
    else
    {
        // Number sequence '%1' is manual.
        error(strFmt("@SYS26209", SalesParameters::numRefSalesId().referenceLabel()));
    }
}

int  active()
{
    int ret;

    ret = super();

    // Set caption
    element.setCaptionText();

    buttonLineUpdate.enabled(true);
    buttonLineFunction.enabled(true);
    buttonLineInquiry.enabled(true);
    buttonLinePriceCalc.enabled(true);

    if (!SalesLine.lastPackingSlip())
    {
        buttonJournalPackingSlip.enabled(false);
    }
    else
    {
        buttonJournalPackingSlip.enabled(true);
    }

    if (salesLine.RecId)
    {
        element.setEditProjIdAllowed(false);
    }
    else
    {
        element.setEditProjIdAllowed(true);
    }
    element.setEditItemOrCategoryAllowed(salesLine.RecId == 0);

    element.setEditSalesCurrencyIdAllowed();

    salesLine_ds.allowEdit(element.editLineAllowed());
    salesLine_ds.allowCreate(element.editLineAllowed());

    if (SalesLine.SalesStatus == SalesStatus::Invoiced)
    {
        salesLine_ds.allowDelete(false);
    }
    else
    {
        salesLine_ds.allowDelete(element.editLineAllowed());
    }

    salesLine_ds.object(fieldNum(SalesLine, DeliveryPostalAddress)).allowEdit(salesLine.canInvoiceOrderLineFromCurrentSalesStatus());

    element.checkCreditNoteLine();
    element.updateDesign(InventDimFormDesignUpdate::Active);

    inventdim_ds.active();

    this.fieldModified();

    salesLine_ds.rereadReferenceDataSources();

    //enable PC configuration
    element.enableLinePCActions();

    // Update transfer button state.
    element.enableTransfer();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.enableDeliverySlip_BR();
    }
    // </GBR>

    if (isIndiaWHTMarked)
    {
        element.setTaxWithholdEnabled_IN();
    }
    // </GIN>

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;

    if (!salesLine.ProjId)
    {
        ret = checkFailed("@SYS50091");
    }

    if (ret)
    {
        ret = super();
    }

    if (ret)
    {
        ret = salesTableForm.salesLine_validateWrite(salesLine,inventDim);
    }

    if (ret)
    {
        SalesCalcAvailableDlvDates  salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(salesLine);

        if (SalesLine.ItemId != SalesLine.orig().ItemId)
        {
            element.updateDesign(InventDimFormDesignUpdate::FieldChange);
        }

        ret = salesCalcAvailableDlvDates.validateWritePrompt(salesLine);
    }

    return ret;
}

