    boolean     ret;

    ret = super();

    projTableLocal = ProjTable::find(salesLine.ProjId);
    if (ret && !projTableLocal.type().custAccount())
    {
        ret = checkFailed("@SYS98240");
    }

    if (ret && !projTableLocal.isItemRequirementAllowedForMultipleFundingSources())
    {
        ret = checkFailed("@Proj:ItemRequirementMultipleFundingSources");
    }

    return ret;
}

