public void jumpRef()
{
    //  Handling the jumpRef separately as ProjFundingSource is not a datasource on the form
    //  Without this, the clicking on the funding source value will throw an error
    //  do not call super in this case.
    //  super();

    ProjFundingSource projFundingSourceLocal = ProjFundingSource::find(element.SalesLine.ProjFundingSource);

    //setup menu item call for jumpRef
    Args args = new Args();
    args.caller(element);
    args.record(projFundingSourceLocal);
    args.lookupRecord(projFundingSourceLocal);
    args.copyCallerQuery(CopyCallerQuery::No);
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(projFundingSourceDetail), MenuItemType::Display);
    menuFunction.run(args);
}

public void modified()
{
    SalesCategory   salesCategory;
    ItemFreeTxt     name;
    SalesDeliveryDateControlType    salesDeliveryControlType;

    if (keepName)
    {
        name           = salesLine.Name;
    }
    salesDeliveryControlType = SalesLine.DeliveryDateControlType;

    // Reset line, but keep category
    salesCategory = salesLine.SalesCategory;

    salesTableForm.resetSalesLine(salesLine);

    salesLine.SalesCategory   = salesCategory;

    super();

    // Name is defaulted from category in super(). So we revert to saved value it if needed.
    if (keepName)
    {
        salesLine.Name = name;
    }
    SalesLine.DeliveryDateControlType = salesDeliveryControlType;

    // Enabled/disable fields
    salesLine_ds.changedStockedStatus(true);

    element.enableLinePCActions();

    if (salesLine.isCategoryBased())
    {
        if (salesTable.MatchingAgreement)
        {
            salesLine.salesPurchLineInterface().autoMatchAgreementLine(salesTable.MatchingAgreement);
        }

        if (salesLineForeignTradeCategory.SalesLineDataAreaId == '')
        {
            //Only create record if no one exists
            salesLineForeignTradeCategory_ds.allowCreate(true);
            salesLineForeignTradeCategory_ds.create();
            salesLineForeignTradeCategory_ds.allowEdit(true);
            salesLineForeignTradeCategory_ds.allowCreate(false);
        }
    }
    else
    {
        salesLineForeignTradeCategory.clearFormFields();
        salesLineForeignTradeCategory_ds.allowEdit(false);
        salesLineForeignTradeCategory_ds.refresh();
    }
}

