    }

    super();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    CFOPAttributesDeterminer_BR cfopAttributes;

    if (BrazilParameters::isEnabled())
    {
        cfopAttributes = new ItemReqLineCFOPAttributesDeterminer_BR(salesLine);
        cfopAttributes.getCFOPAttributes();

        cfopAttributes.parmFiscalEstablishment(inventDim.inventSite().fiscalEstablishment());

        ret = CFOPLookupHelper_BR::lookupCFOPTable(_formReferenceControl, cfopAttributes);
    }

    return ret;
}

void  clicked()
{
    salesTableForm.enableUpdateJournalTransButtons(salesLine,
                                                   null,
                                                   buttonUpdatePickingList,
                                                   buttonUpdatePickingListRegistrate,
                                                   null,
                                                   null,
                                                   buttonUpdatePackingSlipProject);

    super();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.enableDeliverySlip_BR();
    }
    // </GBR>
}

void clicked()
{
    SalesTable locSalesTable;

    // Manually position salesTable record since link type is delayed.
    locSalesTable = SalesTable::find(salesLine_ds.cursor().(fieldNum(SalesLine, SalesId)));

    if (!locSalesTable)
    {
        return;
    }

    PrintMgmtSetupContext setupContext = salesTableForm.printMgmtSetupContext();

    super();

    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(locSalesTable);
    setupContext.parmCountryRegionId(LogisticsPostalAddress::findRecId(locSalesTable.DeliveryPostalAddress).CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

void  clicked()
{
    buttonLineExpandBOM.enabled(!salesLine.deliveredInTotal());

    buttonLineQuickQuote.enabled(projTable.RecId != 0);

    super();
}

void clicked()
{
    Args            args = new Args();
    FormRun         bomListExpandSales;

    if (!salesLine.inventTable().inventItemType().canHaveBOM())
    {
        throw error("@SYS2069");
    }

    args.name(formStr(BOMExpandSales));
    args.caller(element);
    args.record(salesLine);
    bomListExpandSales = classfactory.formRunClass(args);

    bomListExpandSales.run();
    bomListExpandSales.wait();

    salesLine_ds.research(true);
}

void clicked()
{
    if (ProjTable::find(salesLine.ProjId).status().validateWritePurchTable())
    {
        super();
    }
}

void clicked()
{
    super();

    if (PCRuntimeLibrary::isConstraintBasedConfigurable(salesLine.ItemId))
    {
        PCExecuteVariantConfiguration::execute(salesLine, salesLine.ItemId, inventDim.ConfigId);
    }

    salesLine_ds.research(true);
}

void clicked()
{
    InventItemType  inventItemType = salesLine.ItemId ? salesLine.inventTable().inventItemType() : null;

    boolean         canHaveBOM     = inventItemType ? inventItemType.canHaveBOM()    : false;
    boolean         canBeProduced  = inventItemType ? inventItemType.canBeProduced() : false;

    SalesTableType  localSalesTableType = salesLine.salesTable().type();;

    buttonLineProduction.enabled    (localSalesTableType.showProductionMenu() && canBeProduced);
    buttonLineExplosion.enabled     (localSalesTableType.canExplosionBeExecuted());
    bomConsistOfTree.enabled        (canHaveBOM);

    super();
}

