public boolean modified()
{
    boolean ret;
    RecId projRecId, smmActivityParentLinkRecId;
    str activityNumber;

    ret = super();

    activityNumber = group_activityNumber.text();

    if (activityNumber != '')
    {
        projRecId = ProjTable::find(salesLine.ProjId).RecId;
        smmActivityParentLinkRecId =  smmActivityParentLinkTable::findByActivityAndParent(activityNumber, smmActivityParentType::Project, projRecId, tableNum(ProjTable)).RecId;

        if (smmActivityParentLinkRecId == 0)
        {
            salesLine.ActivityNumber = '';
            group_activityNumber.update();
        }
    }

    return ret;
}

