    const str BudgetMenuID = 'BudgetMenuID';

    private boolean enableProjMultipleContractLinesForProjectFeature = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();

}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    OfficeGeneratedExportMenuItem menuItem = OfficeGeneratedExportMenuItem::construct(tableStr(PSAActualEntity), ActualMenuID);
    menuItem.displayName(strFmt('%1 (%2)', new SysDictTable(tableNum(PSAActualEntity)).label(), curExt()));

    _menuOptions.customMenuItems().addEnd(menuItem);

    menuItem = OfficeGeneratedExportMenuItem::construct(tableStr(PSAForecastEntity), BudgetMenuID);
    menuItem.displayName(strFmt('%1 (%2)', new SysDictTable(tableNum(PSAForecastEntity)).label(), curExt()));

    _menuOptions.customMenuItems().addEnd(menuItem);
}

/// <summary>
/// Gets the <c>ExportToExcelDataEntityContext</c> that specifies what should be exported for the specifed menu item.
/// </summary>
/// <param name="_menuItem">The menu item.</param>
/// <returns>
/// Returns the <c>ExportToExcelDataEntityContext</c> that specifies what should be exported for the specifed menu item;
/// otherwise, null.
/// </returns>
public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = ExportToExcelDataEntityContext::construct();
    ExportToExcelDataEntityInfo entity = new ExportToExcelDataEntityInfo();

    if (_menuItem.id() == ActualMenuID)
    {
        entity.entityName(tablestr(PSAActualEntity));
    }
    else
    {
        entity.entityName(tablestr(PSAForecastEntity));
    }

    var filter = ProjStatement::getCustomExportToExcelFilterNode(entity.entityName(), element.projStatistic());

    entity.filter(filter);

    context.entities().addEnd(entity);

    ExportToExcelDataEntityBindingInfo binding = new ExportToExcelDataEntityBindingInfo();
    binding.entityName(entity.entityName());

    binding.useFieldGroup(true);
    binding.fieldGroupName(ExportToExcelFieldGroupName);

    context.bindings().addEnd(binding);

    return context;
}

void clearTmpProjStatistic(boolean grayButton = false)
{
    select actual;
    if (actual)
    {
        actual.delete();
        actual.clear();
    }

    select budget;
    if (budget)
    {
        budget.delete();
        budget.clear();
    }

    select sumDeviation;
    if (sumDeviation)
    {
        sumDeviation.delete();
        sumDeviation.clear();
    }

    select tmpProjStatisticLink;
    if (tmpProjStatisticLink)
    {
        tmpProjStatisticLink.delete();
        tmpProjStatisticLink.clear();
    }

    ctrlTrans.enabled(grayButton);

    if (element.args().dataset()  == tableNum(SMASubscriptionTable))
    {
        ctrlForecast.enabled(false);
    }
    else
    {
        ctrlForecast.enabled(grayButton);
    }

    tmpProjStatisticLink_ds.research();
}

public void init()
{
    callerForm = element.args().caller();

    if (element.args().dataset() ==  tableNum(ProjSorting))
    {
        sorting = true;
    }

    if (element.args().dataset() == tableNum(ProjTable))
    {
        projTable = element.args().record();
        if (ProjTable::child(projTable.ProjId))
        {
            showSubProjects = true;
        }
    }

    super();

    element.setBudget(false);
    element.setSum(false);
    element.setDisplayLabel();
    forecastModel.visible(false);
    projBreakDate.visible(false);
    ledgerBreakDate.visible(false);

    includeSubprojects.visible(showSubProjects);

    // Is statistics form opened from Subscription
    if  (callerForm
     && (callerForm.name() == formStr(SMASubscriptionTable)))

    {
        // Disable the actual vs budget selection
        actualBudgetSumDeviation.enabled(false);
    }

    element.setButtonVisibility();
}

TmpProjStatistic insertSumDiviation(TmpProjStatistic tmpActual, TmpProjStatistic tmpBudget)
{
    TmpProjStatistic    tmpSumDiviation;
    projActualVsBudget  actualVsBudget;
    AmountMST           costTotal;

    actualVsBudget = ProjListProjHelpClass::ab2avb( actualBudgetSumDeviation.selection() );

    tmpSumDiviation.GeneralHoursQty             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.GeneralHoursQty            , tmpBudget.GeneralHoursQty            ,actualVsBudget, false);

    tmpSumDiviation.plInvRevInvoiceRevenue      = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plInvRevInvoiceRevenue     , tmpBudget.plInvRevInvoiceRevenue     ,actualVsBudget, false);
    tmpSumDiviation.plInvRevTotalInvoiceRevenue = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plInvRevTotalInvoiceRevenue, tmpBudget.plInvRevTotalInvoiceRevenue,actualVsBudget, false);
    tmpSumDiviation.plInvRevInvoiceOnAcc        = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plInvRevInvoiceOnAcc       , tmpBudget.plInvRevInvoiceOnAcc       ,actualVsBudget, false);

    tmpSumDiviation.plAccRevSalesValue          = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevSalesValue         , tmpBudget.plAccRevSalesValue         ,actualVsBudget, false);
    tmpSumDiviation.plAccRevSubscript           = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevSubscript          , tmpBudget.plAccRevSubscript          ,actualVsBudget, false);
    tmpSumDiviation.plAccRevProduction          = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevProduction         , tmpBudget.plAccRevProduction         ,actualVsBudget, false);
    tmpSumDiviation.plAccRevProfit              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevProfit             , tmpBudget.plAccRevProfit             ,actualVsBudget, false);
    tmpSumDiviation.plAccRevAccOnAcc            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevAccOnAcc           , tmpBudget.plAccRevAccOnAcc           ,actualVsBudget, false);
    tmpSumDiviation.plAccRevTotalAccRev         = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plAccRevTotalAccRev        , tmpBudget.plAccRevTotalAccRev        ,actualVsBudget, false);

    tmpSumDiviation.plTotalRevenue              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plTotalRevenue             , tmpBudget.plTotalRevenue             ,actualVsBudget, false);
    tmpSumDiviation.plCostItem                  = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plCostItem                 , tmpBudget.plCostItem                 ,actualVsBudget, true);
    tmpSumDiviation.plCostExpenses              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plCostExpenses             , tmpBudget.plCostExpenses             ,actualVsBudget, true);
    tmpSumDiviation.plCostAccCost               = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plCostAccCost              , tmpBudget.plCostAccCost              ,actualVsBudget, true);
    tmpSumDiviation.plTotalValueAddedAmount     = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plTotalValueAddedAmount    , tmpBudget.plTotalValueAddedAmount    ,actualVsBudget, false);
    tmpSumDiviation.plCostHour                  = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plCostHour                 , tmpBudget.plCostHour                 ,actualVsBudget, true);
    tmpSumDiviation.plTotalGrossMargin          = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plTotalGrossMargin         , tmpBudget.plTotalGrossMargin         ,actualVsBudget, false);

    tmpSumDiviation.plCostTotalCost             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.plCostTotalCost            , tmpBudget.plCostTotalCost            ,actualVsBudget, true);

    tmpSumDiviation.wipCostpriceHour            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipCostpriceHour           , tmpBudget.wipCostpriceHour           ,actualVsBudget, true);
    tmpSumDiviation.wipCostpriceExp             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipCostpriceExp            , tmpBudget.wipCostpriceExp            ,actualVsBudget, true);
    tmpSumDiviation.wipCostpriceItem            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipCostpriceItem           , tmpBudget.wipCostpriceItem           ,actualVsBudget, true);
    tmpSumDiviation.wipCostpriceAccruedCost     = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipCostpriceAccruedCost    , tmpBudget.wipCostpriceAccruedCost    ,actualVsBudget, true);

    tmpSumDiviation.wipSalesPriceSalesValue     = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipSalesPriceSalesValue    , tmpBudget.wipSalesPriceSalesValue    ,actualVsBudget, false);
    tmpSumDiviation.wipSalespriceProduction     = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipSalespriceProduction    , tmpBudget.wipSalespriceProduction    ,actualVsBudget, false);
    tmpSumDiviation.wipSalesPriceProfit         = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipSalesPriceProfit        , tmpBudget.wipSalesPriceProfit        ,actualVsBudget, false);
    tmpSumDiviation.wipSalespriceTotal          = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipSalespriceTotal         , tmpBudget.wipSalespriceTotal         ,actualVsBudget, false);

    tmpSumDiviation.wipTotalGrossWIP            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipTotalGrossWIP           , tmpBudget.wipTotalGrossWIP           ,actualVsBudget, false);
    tmpSumDiviation.wipInvoiceOnAcc             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipInvoiceOnAcc            , tmpBudget.wipInvoiceOnAcc            ,actualVsBudget, false);
    tmpSumDiviation.wipTotalNetWIP              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipTotalNetWIP             , tmpBudget.wipTotalNetWIP             ,actualVsBudget, false);

    tmpSumDiviation.wipCostpriceTotal           = ProjListProjHelpClass::calcActualVsBudget(tmpActual.wipCostpriceTotal          , tmpBudget.wipCostpriceTotal          ,actualVsBudget, true);

    tmpSumDiviation.InvoiceHourQty              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceHourQty             , tmpBudget.InvoiceHourQty             ,actualVsBudget, false);
    tmpSumDiviation.InvoiceHourRate             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceHourRate            , tmpBudget.InvoiceHourRate            ,actualVsBudget, false);
    tmpSumDiviation.InvoiceHour                 = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceHour                , tmpBudget.InvoiceHour                ,actualVsBudget, false);
    tmpSumDiviation.InvoiceExpense              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceExpense             , tmpBudget.InvoiceExpense             ,actualVsBudget, false);
    tmpSumDiviation.InvoiceItem                 = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceItem                , tmpBudget.InvoiceItem                ,actualVsBudget, false);
    tmpSumDiviation.InvoiceFee                  = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceFee                 , tmpBudget.InvoiceFee                 ,actualVsBudget, false);
    tmpSumDiviation.InvoiceOnAccount            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceOnAccount           , tmpBudget.InvoiceOnAccount           ,actualVsBudget, false);
    tmpSumDiviation.InvoiceTotal                = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceTotal               , tmpBudget.InvoiceTotal               ,actualVsBudget, false);

    tmpSumDiviation.InvOnAccPrepayment          = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvOnAccPrepayment         , tmpBudget.InvOnAccPrepayment         ,actualVsBudget, false);
    tmpSumDiviation.InvOnAccDeduction           = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvOnAccDeduction          , tmpBudget.InvOnAccDeduction          ,actualVsBudget, false);
    tmpSumDiviation.InvOnAccMilestone           = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvOnAccMilestone          , tmpBudget.InvOnAccMilestone          ,actualVsBudget, false);
    tmpSumDiviation.InvoiceOnAccount            = ProjListProjHelpClass::calcActualVsBudget(tmpActual.InvoiceOnAccount           , tmpBudget.InvoiceOnAccount           ,actualVsBudget, false);

    tmpSumDiviation.OtherConsumHour             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherConsumHour            , tmpBudget.OtherConsumHour            ,actualVsBudget, true);
    tmpSumDiviation.OtherConsumHoursQty         = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherConsumHoursQty        , tmpBudget.OtherConsumHoursQty        ,actualVsBudget, true);
    if (tmpSumDiviation.OtherConsumHoursQty)
    {
        tmpSumDiviation.OtherConsumHourRate     = tmpSumDiviation.OtherConsumHour / tmpSumDiviation.OtherConsumHoursQty;
    }
    tmpSumDiviation.OtherConsumExp              = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherConsumExp             , tmpBudget.OtherConsumExp             ,actualVsBudget, true);
    tmpSumDiviation.OtherConsumItem             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherConsumItem            , tmpBudget.OtherConsumItem            ,actualVsBudget, true);

    tmpSumDiviation.OtherConsumNotCapital       = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherConsumNotCapital      , tmpBudget.OtherConsumNotCapital      ,actualVsBudget, true);

    tmpSumDiviation.OtherPayroll                = ProjListProjHelpClass::calcActualVsBudget(tmpActual.OtherPayroll               , tmpBudget.OtherPayroll               ,actualVsBudget, false);

    tmpSumDiviation.GeneralHoursQty             = ProjListProjHelpClass::calcActualVsBudget(tmpActual.GeneralHoursQty            , tmpBudget.GeneralHoursQty            ,actualVsBudget, false);

    if (tmpSumDiviation.GeneralHoursQty)
    {
        tmpSumDiviation.GeneralHourRevenue              = tmpSumDiviation.plTotalRevenue / tmpSumDiviation.GeneralHoursQty;
        tmpSumDiviation.GeneralHourValuaddedHourRate    = tmpSumDiviation.plTotalValueAddedAmount / tmpSumDiviation.GeneralHoursQty;
        tmpSumDiviation.GeneralHourCostHourRate         = tmpSumDiviation.plCostHour / tmpSumDiviation.GeneralHoursQty;
        tmpSumDiviation.GeneralHourGrossMarginHourRate  = tmpSumDiviation.plTotalGrossMargin / tmpSumDiviation.GeneralHoursQty;
    }

    costTotal   = tmpSumDiviation.plCostExpenses + tmpSumDiviation.plCostHour + tmpSumDiviation.plCostItem;
    switch (ProjParameters::find().GrossMarginDisplay)
    {
        case ProjGrossMarginDisplay::MarkupFactor:
            if (costTotal)
            {
                tmpSumDiviation.GeneralFactorGrossMargin       = tmpSumDiviation.plTotalRevenue / costTotal;
            }
            break;
        case ProjGrossMarginDisplay::ContributionMargin:
            if (tmpSumDiviation.plTotalRevenue)
            {
                tmpSumDiviation.GeneralFactorGrossMargin       = tmpSumDiviation.plTotalGrossMargin /
                                                                  tmpSumDiviation.plTotalRevenue * 100;
            }
            break;
        case ProjGrossMarginDisplay::MarkupPct:
            if (costTotal)
            {
                tmpSumDiviation.GeneralFactorGrossMargin       = tmpSumDiviation.plTotalGrossMargin /
                                                                  costTotal * 100;
            }
            break;
    }

    switch (ProjParameters::find().ValueAddedDisplay)
    {
        case ProjValueAddedDisplay::ValueAddedMarkup:
            if (tmpSumDiviation.plCostHour)
            {
                tmpSumDiviation.GeneralFactorValueaddedFactor  = tmpSumDiviation.plTotalValueAddedAmount /
                                                                  tmpSumDiviation.plCostHour;
            }
            break;

        case ProjValueAddedDisplay::ValueAddedRation:
            if (tmpSumDiviation.plTotalRevenue)
            {
                tmpSumDiviation.GeneralFactorValueaddedFactor  = tmpSumDiviation.plTotalValueAddedAmount /
                                                                  tmpSumDiviation.plTotalRevenue * 100;
            }
            break;

        case ProjValueAddedDisplay::ValueAddedMarupPct:
            if (tmpSumDiviation.plCostHour)
            {
                tmpSumDiviation.GeneralFactorValueaddedFactor  = tmpSumDiviation.plTotalValueAddedAmount /
                                                                  tmpSumDiviation.plCostHour * 100;
            }
            break;
    }

    tmpSumDiviation.TransId                     = transId;
    tmpSumDiviation.ActualBudget                = actualBudgetSumDeviation.selection();
    tmpSumDiviation.insert();

    return tmpSumDiviation;
}

ProjStatistic projStatistic()
{
    projStatistic   = new ProjStatistic();

    projStatistic.parmFixedPrice(fixedPrice.value());
    projStatistic.parmTimeMaterial(timeMaterial.value());
    projStatistic.parmInternalCost(internalCost.value());
    projStatistic.parmInvestment(investment.value());
    projStatistic.parmTime(timeProject.value());

    projStatistic.parmHour(hour.value());
    projStatistic.parmExpenses(expenses.value());
    projStatistic.parmItem(item.value());
    projStatistic.parmRevenue(fee.value());
    projStatistic.parmOnAcc(onAcc.value());

    projStatistic.parmLedgerBreakDate(ledgerBreakDate.dateValue());
    projStatistic.parmProjBreakDate(projBreakDate.dateValue());

    projStatistic.parmForecastModelId(forecastModel.text());

    projstatistic.parmGrantId(grantId);
    projStatistic.parmResource(resourceRecId);
    projStatistic.parmResourceCategory(resourceCategoryRecId);
    projStatistic.parmitemId(itemId);
    projStatistic.parmSubscriptionId(subscriptionId);
    projStatistic.parmSubscriptionGroupId(subscriptionGroupId);

    projStatistic.parmProjCategoryId(categoryId);
    projStatistic.parmCategoryGroupId(categoryGroupId);

    ProjInvoiceProposalCreateLines::prepareProjIdForQuery(projStatistic.parmProjId(projId));
    projStatistic.parmProjTree(includeSubprojects.value());
    projStatistic.parmProjWIPId(projWipId);
    switch (sortCode)
    {
        case ProjSortValue::Sorting1:
            projStatistic.parmSorting1(sortingId);
            break;

        case ProjSortValue::Sorting2:
            projStatistic.parmSorting2(sortingId);
            break;

        case ProjSortValue::Sorting3:
            projStatistic.parmSorting3(sortingId);
            break;
    }
    projStatistic.parmProjGroupId(projGroupId);
    projStatistic.parmProjInvoiceProjId(projInvoiceProjId);
    projStatistic.parmCustAccount(custAccount);
    projStatistic.parmActualBudget(actualBudgetSumDeviation.selection());

    projStatistic.parmLedgerFromDate(ledgerFromDate.dateValue());
    projStatistic.parmLedgerToDate(ledgerToDate.dateValue());
    projStatistic.parmProjFromDate(projFromDate.dateValue());
    projStatistic.parmProjToDate(projToDate.dateValue());

    return projStatistic;
}

private void setTabularColumnVisibility(FormGroupControl tabularGroupControl, int column, boolean visible)
{
    int                 ControlNum;
    FormGroupControl    formGroupControl;
    formControl         formControl;

    for (ControlNum = 1; ControlNum <= tabularGroupControl.controlCount(); ControlNum++)
    {
        formGroupControl = tabularGroupControl.controlNum(ControlNum);
        // Validate that this group control contains the right number of controls.
        if (formGroupControl.controlCount() == COLUMNS+1)
        {
            formGroupControl.controlNum(column+1).visible(visible);
        }
    }
}

void setColumnVisibility(int column, boolean visible)
{
    this.setTabularColumnVisibility(GroupGeneral, column, visible);
    this.setTabularColumnVisibility(GroupPL, column, visible);
    this.setTabularColumnVisibility(GroupWIP, column, visible);
    this.setTabularColumnVisibility(GroupConsumption, column, visible);
    this.setTabularColumnVisibility(GroupInvoice, column, visible);
    this.setTabularColumnVisibility(GroupHourRate, column, visible);
}

void setActual(boolean visible)
{
    this.setColumnVisibility(COLUMNACTUAL, visible);
}

void setBudget(boolean visible)
{
    this.setColumnVisibility(COLUMNBUDGET, visible);
}

void setBudgetLabel(boolean showLabel)
{
    #define.space(' ')

    str 50 sumDeviationLabel;

    if (actualBudgetSumDeviation.selection() != ProjActualBudget::Actual &&
        actualBudgetSumDeviation.selection() != ProjActualBudget::Budget)
    {
        sumDeviationLabel = ProjListProjHelpClass::descActualVsBudget(ProjListProjHelpClass::ab2avb(actualBudgetSumDeviation.selection()));
    }

    generalColumnHeaderSum.text(sumDeviationLabel);
    plColumnHeaderSum.text(sumDeviationLabel);
    wipColumnHeaderSum.text(sumDeviationLabel);
    consumptionColumnHeaderSum.text(sumDeviationLabel);
    invoiceColumnHeaderSum.text(sumDeviationLabel);
    hourRateColumnHeaderSum.text(sumDeviationLabel);
}

/// <summary>
/// Set the visibility of transaction and forecast buttons.
/// </summary>
private void setButtonVisibility()
{
    AccessRight     menuItemAccessRight;
    SecurityRights  securityRights;

    securityRights = SecurityRights::construct();

    // Hour button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransEmpl));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        hourButton.visible(false);
    }

    // Expense button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransCost));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        expensesButton.visible(false);
    }

    // Item button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransItem));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        itemButton.visible(false);
    }

    // Revenue button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransRevenue));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        revenueButton.visible(false);
    }

    // On-Account button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projTransOnAcc));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        onAccButton.visible(false);
    }

    // Hour forecast button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projForecastEmpl));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        hourForecastButton.visible(false);
    }

    // Expense forecast button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projForecastCost));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        expensesForecastButton.visible(false);
    }

    // Item forecast button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(forecastSales));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        itemForecastButton.visible(false);
    }

    // Revenue forecast button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projForecastRevenue));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        revenueForecastButton.visible(false);
    }

    // On-Account forecast button
    menuItemAccessRight = securityRights.menuItemAccessRight(SecurableType::MenuItemDisplay, menuitemDisplayStr(projForecastOnAcc));

    if (menuItemAccessRight == AccessRight::NoAccess)
    {
        onAccForecastButton.visible(false);
    }
}

void setDisplayLabel()
{
    ProjParameters  projParameters  = ProjParameters::find();

    generalFactorValueaddedFactor.label(ProjParameters::find().valueAddedDisplayLabel());
    generalFactorGrossMargin.label(ProjParameters::find().grossMarginDisplayLabel());
    generalFactorValueaddedFactor.helpText(ProjParameters::find().valueAddedHelpLabel());
    generalFactorGrossMargin.helpText(ProjParameters::find().grossMarginHelpLabel());

    budget_GeneralFactorValueaddedFactor.label(ProjParameters::find().valueAddedDisplayLabel());
    budget_GeneralFactorGrossMargin.label(ProjParameters::find().grossMarginDisplayLabel());
    budget_GeneralFactorValueaddedFactor.helpText(ProjParameters::find().valueAddedHelpLabel());
    budget_GeneralFactorGrossMargin.helpText(ProjParameters::find().grossMarginHelpLabel());

    generalSumFactor_GeneralFactorValueaddedFactor.label(ProjParameters::find().valueAddedDisplayLabel());
    generalSumFactor_GeneralFactorGrossMargin.label(ProjParameters::find().grossMarginDisplayLabel());
    generalSumFactor_GeneralFactorValueaddedFactor.helpText(ProjParameters::find().valueAddedHelpLabel());
    generalSumFactor_GeneralFactorGrossMargin.helpText(ProjParameters::find().grossMarginHelpLabel());

    generalFactorValueaddedFactor.noOfDecimals(projParameters.ValueAddedDecimals);
    budget_GeneralFactorValueaddedFactor.noOfDecimals(projParameters.ValueAddedDecimals);
    generalSumFactor_GeneralFactorValueaddedFactor.noOfDecimals(projParameters.ValueAddedDecimals);

    generalFactorGrossMargin.noOfDecimals(projParameters.GrossMarginDecimals);
    budget_GeneralFactorGrossMargin.noOfDecimals(projParameters.GrossMarginDecimals);
    generalSumFactor_GeneralFactorGrossMargin.noOfDecimals(projParameters.GrossMarginDecimals);
}

container setLedgerProjDate(ProjActualBudget _actualBudget)
{
    TransDate   pFromDate, pToDate, ledFromDate, ledToDate;

    if (actualBudgetSumDeviation.selection() == ProjActualBudget::Sum_)
    {
        if (_actualBudget   == ProjActualBudget::Actual)
        {
            ledFromDate = ledgerFromDate.dateValue();
            if (ledgerBreakDate.dateValue() != dateNull())
            {
                ledToDate = ledgerBreakDate.dateValue();
            }
            else
            {
                ledToDate = ledgerToDate.dateValue();
            }

            pFromDate   = projFromDate.dateValue();
            if (projBreakDate.dateValue() != dateNull())
            {
                pToDate = projBreakDate.dateValue();
            }
            else
            {
                pToDate = projToDate.dateValue();
            }
        }
        else if (_actualBudget   == ProjActualBudget::Budget)
        {
            ledToDate = ledgerToDate.dateValue();

            if (ledgerBreakDate.dateValue() != dateNull())
            {
                ledFromDate = ledgerBreakDate.dateValue()+1;
            }
            else
            {
                ledFromDate = ledgerFromDate.dateValue();
            }

            pToDate     = projToDate.dateValue();
            if (projBreakDate.dateValue() != dateNull())
            {
                pFromDate   = projBreakDate.dateValue()+1;
            }
            else
            {
                pFromDate   = projFromDate.dateValue();
            }
        }
    }
    else
    {
        pFromDate       = projFromDate.dateValue();
        pToDate         = projToDate.dateValue();
        ledFromDate     = ledgerFromDate.dateValue();
        ledToDate       = ledgerToDate.dateValue();
    }

    return [pFromDate, pToDate, ledFromDate, ledToDate];
}

void setSum(boolean visible)
{
    this.setColumnVisibility(COLUMNSUM, visible);
}

void updateButton()
{
    boolean proceed = true;
    projStatisticCalc       projStatisticCalc;
    TmpProjStatistic        tmpActual, tmpBudget, tmpSumDiviation;
    TransDate               pFromDate, pToDate, ledFromDate, ledToDate;

    if (actualBudgetSumDeviation.selection() != ProjActualBudget::Actual)
    {
        if (!forecastModel.text())
        {
            if (Box::yesNo(strfmt("@Proj:NoBudgetModelIsSelected", "@SYS103933"), DialogButton::No) == DialogButton::No)
            {
                proceed = false;
            }
            else
            {
                ForecastModel forecastModelTable;

                select firstonly ModelId from forecastModelTable
                    where forecastModelTable.ProjBudgetType == ProjBudgetType::OriginalBudget;

                if (forecastModelTable.ModelId)
                {
                    forecastModel.text(forecastModelTable.ModelId);
                }
            }
        }
    }

    if (proceed)
    {
        element.projStatistic();
        element.clearTmpProjStatistic(true);

        ttsBegin;
        tmpProjStatisticLink.Resource               = resourceRecId;
        tmpProjStatisticLink.ResourceCategory       = resourceCategoryRecId;
        tmpProjStatisticLink.ItemId                 = itemId;
        tmpProjStatisticLink.smaSubscriptionId      = subscriptionId;
        tmpProjStatisticLink.smaSubscriptionGroupId = subscriptionGroupId;

        tmpProjStatisticLink.CategoryId        = categoryId;
        tmpProjStatisticLink.CategoryGroupId   = categoryGroupId;

        tmpProjStatisticLink.ProjId            = projId;
        tmpProjStatisticLink.ProjWIPId         = projWipId;
        tmpProjStatisticLink.SortingId         = sortingId;

        tmpProjStatisticLink.ProjGroupId       = projGroupId;
        tmpProjStatisticLink.ProjInvoiceProjId = projInvoiceProjId;
        tmpProjStatisticLink.CustAccount       = custAccount;

        tmpProjStatisticLink.insert();
        ttsCommit;

        transId                                 = tmpProjStatisticLink.TransId;

        // Calculate actual
        if (actualBudgetSumDeviation.selection() != ProjActualBudget::Budget)
        {
            projStatisticCalc = new projStatisticCalc(actual);
            projStatistic.parmActual(true);
            [pFromDate, pToDate, ledFromDate, ledToDate] = element.setLedgerProjDate(ProjActualBudget::Actual);
            projStatistic.parmLedgerFromDate(ledFromDate);
            projStatistic.parmLedgerToDate(ledToDate);
            projStatistic.parmProjFromDate(pFromDate);
            projStatistic.parmProjToDate(pToDate);
            projStatistic.parmActualBudget(actualBudgetSumDeviation.selection());

            // Pack the ProjStatistic object here before passing it to the ProjStatisticCalc on the server
            projStatisticCalc.parmProjStatisticCon(projStatistic.pack());
            projStatisticCalc.parmTransId(transId);
            projStatisticCalc.parmActualBudget(ProjActualBudget::Actual);
            projStatisticCalc.parmUseDataEntity(true);
            projStatisticCalc.parmIgnoreExportToExcelPivot(true);

            projStatisticCalc.run();

            tmpActual.setTmpData(projStatisticCalc.tmpProjStatistic());
            select tmpActual;
            actual.setTmpData(projStatisticCalc.tmpProjStatistic());
        }

        // Calculate budget
        if (actualBudgetSumDeviation.selection() != ProjActualBudget::Actual)
        {
            projStatisticCalc = new projStatisticCalc(budget);
            projStatistic.parmActual(false);
            [pFromDate, pToDate, ledFromDate, ledToDate] = element.setLedgerProjDate(ProjActualBudget::Budget);

            projStatistic.parmForecastModelId(forecastmodel.text());
            projStatistic.parmLedgerFromDate(ledFromDate);
            projStatistic.parmLedgerToDate(ledToDate);
            projStatistic.parmProjFromDate(pFromDate);
            projStatistic.parmProjToDate(pToDate);
            projStatistic.parmActualBudget(actualBudgetSumDeviation.selection());

            projStatisticCalc.parmProjStatisticCon(projStatistic.pack());
            projStatisticCalc.parmTransId(transId);
            projStatisticCalc.parmActualBudget(ProjActualBudget::Budget);
            projStatisticCalc.parmUseDataEntity(true);
            projStatisticCalc.parmIgnoreExportToExcelPivot(true);

            projStatisticCalc.run();

            tmpBudget.setTmpData(projStatisticCalc.tmpProjStatistic());
            select tmpBudget;
            budget.setTmpData(projStatisticCalc.tmpProjStatistic());
        }

        // Update Sum / Deviation
        if (actualBudgetSumDeviation.selection() != ProjActualBudget::Actual &&
            actualBudgetSumDeviation.selection() != ProjActualBudget::Budget)
        {
            tmpBudget.setTmpData(projStatisticCalc.tmpProjStatistic());
            select tmpBudget;
            budget.setTmpData(projStatisticCalc.tmpProjStatistic());

            tmpSumDiviation = element.insertSumDiviation(tmpActual,tmpBudget);
            sumDeviation.setTmpData(tmpSumDiviation);
        }

        if (actualBudgetSumDeviation.selection() == ProjActualBudget::Budget)
        {
            element.setBudgetLabel(true);
        }
        else
        {
            element.setBudgetLabel(false);
        }
    }
}

public void init()
{
    super();

    this.query().dataSourceName('actual').addRange(fieldNum(TmpProjStatistic, ActualBudget)).value(queryValue(ProjActualBudget::Actual));
}

public void init()
{
    super();

    this.query().dataSourceName('budget').addRange(fieldNum(TmpProjStatistic, ActualBudget)).value(queryValue(ProjActualBudget::Budget));
}

void linkFromCustTable()
{
    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        this.fixedPrice(true,true);
        this.timeMaterial(true,true);
        this.internalCost(true,true);
        this.investment(true,true);
        this.timeProject(false,false);
    }

    this.hour(true,true);
    this.expenses(true,true);
    this.fee(true,true);
    this.item(true,true);
    this.onAccount(true,true);
}

void linkFromProjSorting()
{
    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        this.fixedPrice(true,true);
        this.timeMaterial(true,true);
        this.internalCost(true,true);
        this.investment(true,true);
        this.timeProject(true,true);
    }

    this.hour(true,true);
    this.expenses(true,true);
    this.fee(true,true);
    this.item(true,true);
    this.onAccount(true,true);
}

void linkFromProjGroup(ProjGroup    _projGroup)
{
    switch (_projGroup.ProjType)
    {
        case ProjType::TimeMaterial:
            timeMaterial.value(true);
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            this.fee(true,true);
            this.onAccount(true,true);
            break;

        case ProjType::FixedPrice:
            fixedPrice.value(true);
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            this.fee(true,true);
            this.onAccount(true,true);
            break;

        case ProjType::Investment:
            investment.value(true);
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            break;

        case ProjType::Cost:
            internalCost.value(true);
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            break;

        case ProjType::Internal:
            internalCost.value(true);
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            break;

        case ProjType::Time:
            timeProject.value(true);
            this.hour(true,false);
            break;

        case ProjType::None:
            this.hour(true,true);
            this.expenses(true,true);
            this.item(true,true);
            this.fee(true,true);
            this.onAccount(true,true);
            break;
    }
}

void linkFromProjWIPTable(ProjWIPTable  _projWIPTable)
{
    ProjTable projTableLocal = _projWIPTable.projTable();

    if (element.enableProjMultipleContractLinesForProjectFeature)
    {
        if (projTableLocal.ProjInvoiceProjId)
        {
            if (ProjMultipleContractLinesForProjectFeatureHelper::findBillingMethodContractLineForHourExpenseItemTransType(projTableLocal.ProjId, projTableLocal.ProjInvoiceProjId, ProjContractBillingMethod::FixedPrice))
            {
                this.onAccount(true);
            }
            if (ProjMultipleContractLinesForProjectFeatureHelper::isTransTypeIncludedByBillingMethod(projTableLocal.ProjId, projTableLocal.ProjInvoiceProjId, ProjTransType::Revenue, ProjContractBillingMethod::FixedPrice))
            {
                this.fee(true);
                this.onAccount(true);
            }
        }
    }
    else
    {
        if (projTableLocal.Type == ProjType::FixedPrice)
        {
            this.fixedPrice(true,false);
            this.investment(false,false);
            this.fee(true,true);
            this.onAccount(true,true);
        }
        else if (projTableLocal.Type == ProjType::Investment)
        {
            this.fixedPrice(false,false);
            this.investment(true,false);
            this.onAccount(false, false);
        }
    }

    this.hour(true,true);
    this.expenses(true,true);
    this.item(true,true);
}

void linkFromProjCateogryGroup(ProjCategoryGroup    _projCategoryGroup)
{
    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        this.fixedPrice(true);
        this.timeMaterial(true);
        this.investment(true);
        this.internalCost(true);
        this.timeProject(false,false);
    }

    hourButton.enabled(false);
    expensesButton.enabled(false);
    revenueButton.enabled(false);
    onAccButton.enabled(false);
    itemButton.enabled(false);
    hourForecastButton.enabled(false);
    expensesForecastButton.enabled(false);
    revenueForecastButton.enabled(false);
    onAccForecastButton.enabled(false);
    itemForecastButton.enabled(false);

    switch (_projCategoryGroup.CategoryType)
    {
        case ProjCategoryType::Revenue:
            this.fee(true,true);
            revenueButton.enabled(true);
            revenueForecastButton.enabled(true);
            break;

        case ProjCategoryType::Hour:
            this.hour(true,true);

            if (!element.enableProjMultipleContractLinesForProjectFeature)
            {
                this.timeProject(true,true);
            }

            hourButton.enabled(true);
            hourForecastButton.enabled(true);
            break;

        case ProjCategoryType::Cost:
            this.expenses(true,true);
            expensesButton.enabled(true);
            expensesForecastButton.enabled(true);
            break;

        case ProjCategoryType::Item:
            this.item(true,true);
            itemButton.enabled(true);
            itemForecastButton.enabled(true);
            break;
    }
}

void linkFromProjCategory(ProjCategory _projCategory)
{
    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        this.fixedPrice(true);
        this.timeMaterial(true);
        this.investment(true);
        this.internalCost(true);
        this.timeProject(false,false);
    }

    hourButton.enabled(false);
    expensesButton.enabled(false);
    revenueButton.enabled(false);
    onAccButton.enabled(false);
    itemButton.enabled(false);
    hourForecastButton.enabled(false);
    expensesForecastButton.enabled(false);
    revenueForecastButton.enabled(false);
    onAccForecastButton.enabled(false);
    itemForecastButton.enabled(false);

    switch (_projCategory.CategoryType)
    {
        case ProjCategoryType::Revenue:
            this.fee(true,true);

            if (!element.enableProjMultipleContractLinesForProjectFeature)
            {
                this.timeMaterial(true,false);
                this.internalCost(false,false);
                this.investment(false,false);

                if (_projCategory.SetupEstimate)
                {
                    this.fixedPrice(true,false);
                }
                else
                {
                    this.fixedPrice(false,false);
                }
            }

            revenueButton.enabled(true);
            revenueForecastButton.enabled(true);
            break;

        case ProjCategoryType::Hour:
            this.hour(true,true);

            if (!element.enableProjMultipleContractLinesForProjectFeature)
            {
                this.timeProject(true,true);
            }

            hourButton.enabled(true);
            hourForecastButton.enabled(true);
            break;

        case ProjCategoryType::Cost:
            this.expenses(true,true);
            expensesButton.enabled(true);
            expensesForecastButton.enabled(true);
            break;

        case ProjCategoryType::Item:
            this.item(true,true);
            itemButton.enabled(true);
            itemForecastButton.enabled(true);
            break;
    }
}

void linkFromProjTable(ProjTable _projTable)
{
    ProjTable projTableCheck;
    boolean showChild;

    projId = _projTable.ProjId;

    if (element.args().dataset() != tableNum(ProjGrantView))
    {
        showChild = includeSubprojects.value();
    }

    this.fixedPrice(false,false);
    this.timeMaterial(false,false);
    this.investment(false,false);
    this.internalCost(false,false);
    this.timeProject(false,false);

    this.fee(false,false);
    this.hour(false,false);
    this.expenses(false,false);
    this.item(false,false);
    this.onAccount(false,false);

    if (element.enableProjMultipleContractLinesForProjectFeature)
    {
        this.hour(true);
        this.expenses(true);
        this.item(true);

        if (showChild)
        {
            PSAContractLineItems contractLine;
            ProjId projIdFilter  = projId + '*';

            select firstonly projTableCheck
                where projTableCheck.ProjId like projIdFilter
                    exists join contractLine
                        where contractLine.ProjInvoiceProjId == projTableCheck.ProjInvoiceProjId &&
                                (contractLine.IncludeHourTrans == NoYes::Yes ||
                                 contractLine.IncludeExpenseTrans == NoYes::Yes ||
                                 contractLine.IncludeItemTrans == NoYes::Yes);
            if (projTableCheck)
            {
                this.onAccount(true);
            }
            select firstonly projTableCheck
                where projTableCheck.ProjId like projIdFilter
                    exists join contractLine
                        where contractLine.ProjInvoiceProjId == projTableCheck.ProjInvoiceProjId &&
                                contractLine.IncludeFeeTrans == NoYes::Yes;
            if (projTableCheck)
            {
                this.fee(true);
                this.onAccount(true);
            }
        }
        else if (_projTable.ProjInvoiceProjId)
        {
            if (ProjMultipleContractLinesForProjectFeatureHelper::getProjContractLineByContractIdTransType(_projTable.ProjId, _projTable.ProjInvoiceProjId, ProjTransType::Cost) ||
                ProjMultipleContractLinesForProjectFeatureHelper::getProjContractLineByContractIdTransType(_projTable.ProjId, _projTable.ProjInvoiceProjId, ProjTransType::Hour) ||
                ProjMultipleContractLinesForProjectFeatureHelper::getProjContractLineByContractIdTransType(_projTable.ProjId, _projTable.ProjInvoiceProjId, ProjTransType::Item))                    
            {
                this.onAccount(true);
            }
            if (ProjMultipleContractLinesForProjectFeatureHelper::getProjContractLineByContractIdTransType(_projTable.ProjId, _projTable.ProjInvoiceProjId, ProjTransType::Revenue))
            {
                this.fee(true);
                this.onAccount(true);
            }
        }
    }
    else
    {
        if (showChild)
        {
            ProjId projIdFilter = projId + '*';

            select firstonly projTableCheck
               where projTableCheck.ProjId like projIdFilter
                  && projTableCheck.Type    == ProjType::TimeMaterial;

            if (projTableCheck)
            {
                this.timeMaterial(true);

                this.fee(true);
                this.hour(true);
                this.expenses(true);
                this.item(true);
                this.onAccount(true);
            }
            else
            {
                this.timeMaterial(false, false);
            }

            select firstonly projTableCheck
               where projTableCheck.ProjId like projIdFilter
                  && projTableCheck.Type    == ProjType::FixedPrice;

            if (projTableCheck)
            {
                this.fixedPrice(true);

                this.fee(true);
                this.hour(true);
                this.expenses(true);
                this.item(true);
                this.onAccount(true);
            }
            else
            {
                this.fixedPrice(false, false);
            }

            select firstonly projTableCheck
               where projTableCheck.ProjId like projIdFilter
                  && projTableCheck.Type    == ProjType::Investment;

            if (projTableCheck)
            {
                this.investment(true);

                this.hour(true);
                this.expenses(true);
                this.item(true);
            }
            else
            {
                this.investment(false, false);
            }

            select firstonly projTableCheck
               where  projTableCheck.ProjId like projIdFilter
                  && (projTableCheck.Type    == ProjType::Internal
                  ||  projTableCheck.Type    == ProjType::Cost);

            if (projTableCheck)
            {
                this.internalCost(true);

                this.hour(true);
                this.expenses(true);
                this.item(true);
            }
            else
            {
                this.internalCost(false, false);
            }

            select firstonly projTableCheck
               where projTableCheck.ProjId like projIdFilter
                  && projTableCheck.Type    == ProjType::Time;

            if (projTableCheck)
            {
                this.timeProject(true);
                this.hour(true);
            }
            else
            {
                this.timeProject(false, false);
            }
        }
        else
        {
            switch (_projTable.Type)
            {
                case ProjType::TimeMaterial:
                    this.timeMaterial(true,false);
                    this.fee(true);
                    this.hour(true);
                    this.expenses(true);
                    this.item(true);
                    this.onAccount(true);
                    break;

                case ProjType::FixedPrice:
                    this.fixedPrice(true,false);
                    this.hour(true);
                    this.expenses(true);
                    this.item(true);
                    this.fee(true);
                    this.onAccount(true);
                    break;

                case ProjType::Investment:
                    this.investment(true,false);
                    this.hour(true);
                    this.expenses(true);
                    this.item(true);
                    break;

                case ProjType::Cost:
                    this.internalCost(true,false);
                    this.hour(true);
                    this.expenses(true);
                    this.item(true);
                    break;

                case ProjType::Internal:
                    this.internalCost(true,false);
                    this.hour(true);
                    this.expenses(true);
                    this.item(true);
                    break;

                case ProjType::Time:
                    this.timeProject(true,false);
                    this.hour(true,false);
                    break;
            }
        }
    }
}

