public boolean modified()
{
    boolean ret;

    ret = super();

    switch (actualBudgetSumDeviation.selection())
    {
        case ProjActualBudget::Actual:
            element.setActual(true);
            element.setBudget(false);
            element.setSum(false);
            projBreakDate.visible(false);
            ledgerBreakDate.visible(false);
            forecastModel.visible(false);
            break;

        case ProjActualBudget::Budget:
            element.setActual(false);
            element.setBudget(true);
            element.setSum(false);
            projBreakDate.visible(false);
            ledgerBreakDate.visible(false);
            forecastModel.visible(true);
            break;

        case ProjActualBudget::Sum_:
            element.setActual(true);
            element.setBudget(true);
            element.setSum(true);
            projBreakDate.visible(true);
            ledgerBreakDate.visible(true);
            forecastModel.visible(true);
            break;

        case ProjActualBudget::Deviation:
            element.setActual(true);
            element.setBudget(true);
            element.setSum(true);
            projBreakDate.visible(false);
            ledgerBreakDate.visible(false);
            element.setTabularColumnVisibility(GroupHourRate, 3, false);   // dont show a deviation on hour rates ??
            forecastModel.visible(true);
            break;
    }

    element.setBudgetLabel( actualBudgetSumDeviation.selection()==ProjActualBudget::Budget );

    element.clearTmpProjStatistic();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.clearTmpProjStatistic();

    return ret;
}

public void clicked()
{
    super();

    element.clearTmpProjStatistic();

    if (projTable)
    {
        tmpProjStatisticLink_ds.linkFromProjTable(projTable);
    }
}
