/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Clear the menu options which are driven by the root datasource of an entity, as some
    // of the of the options are valid for only certain journal types.
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());
    OfficeMenuDataEntityOptions dataEntityOption;

    var projectDataEntity = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled() ? dataentityviewstr(ProjProjectV2Entity) : dataentityviewstr(ProjectEntity);

    while (dataEntityIterator.more())
    {
        if (dataEntityIterator.value().dataEntityName() == projectDataEntity)
        {
            dataEntityOption = dataEntityIterator.value();
        }

        dataEntityIterator.delete();
    }

    if (dataEntityOption)
    {
        _menuOptions.dataEntityOptions().addEnd(dataEntityOption);
    }
}

ProjId createprojId(ProjId _createprojId = createprojId)
{
    createprojId  = _createprojId;

    return createprojId;
}

/// <summary>
/// Create a project hierarchy tree for the given project ID.
/// </summary>
/// <param name="_selectedProjId">
/// The project ID for which to create a hierarchy tree.
/// </param>
public void createTree(ProjId   _selectedProjId = '')
{
    projHierarchy.visible(true);

    tree.deleteAll();

    projectTree = ProjTree::construct(tree, ProjTreeType::ProjectHierarchy, rootProjId);

    if (projectTree)
    {
        projectTree.parmCreateMaster(true);
        projectTree.setupChart();
    }
}

void displayURL()
{
    CollabSiteTable collabSiteTable;

    internalUrl.text("");
    externalUrl.text("");

    collabSiteTable = CollabSiteLink::getCollabSiteProject(projTable.ProjId);
    if (collabSiteTable)
    {
        internalUrl.text(collabSiteTable.InternalURL);
        externalUrl.text(collabSiteTable.ExternalURL);
    }
}

