protected ProjId getProjId(Common _common)
{
    ProjId projIdLoc;

    switch (_common.TableId)
    {
        case tableNum(ProjAdvancedJournalLine):
            ProjAdvancedJournalLine projAdvancedJournalLine = element.args().record();
            projIdLoc = projAdvancedJournalLine.ProjId;
            break;

        case tableNum(PurchReqTable):
            PurchReqTable purchReqTable = element.args().record();
            projIdLoc = purchReqTable.ProjId;
            break;

        case tableNum(PurchReqLine):
            PurchReqLine purchReqLine = element.args().record();
            projIdLoc = purchReqLine.ProjId;
            break;

        default:
            ProjTable projTableLoc = element.args().record();
            projIdLoc = projTableLoc.ProjId;
            break;
    }

    return projIdLoc;
}

void init()
{
    // Opt-out of the flight 'DetailsTransitionArgsRecordRestrictionFlight' 
    this.skipAddRecIdRangeForQueryPerf();

    if (element.args().menuItemName() == menuItemDisplayStr(ProjTable) &&
        ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        ProjId projIdLoc = element.args().lookupValue();

        if (!projIdLoc)
        {
            Common common = element.args().record();
            projIdLoc = this.getProjId(common);
        }

        if (projIdLoc)
        {
            this.openProjectInCE(projIdLoc, curext());
        }

        this.close();
    }
    else
    {
        boolean        enableLGFeature = false;
        SecurityRights securityRights  = SecurityRights::construct();

        doTransactionsExist            = NoYesUnchanged::Unchanged;

        if (element.args().dataset() == tableNum(ProjTable))
        {
            selectedProjTable = element.args().record();
            selectedProjId = selectedProjTable.ProjId;
        }

        if (element.args().caller())
        {
            callerName = element.args().caller().name();
        }

        if ((SysFormRun::isCalledFromJumpRef(this.args(), fieldNum(ProjTable, ProjId)) && callerName != formStr(projProjectsListPage)))
        {
            selectedProjId = this.args().lookupValue();
            selectedProjTable = ProjTable::find(selectedProjId);
        }

        if (this.enableJumpToMain())
        {
            jumpToMain = NoYes::Yes;
        }

        if (selectedProjTable.RecId)
        {
            hasAccessToActivity = securityRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(ProjActivity));
        }
        else
        {
            hasAccessToActivity = securityRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(ProjActivityOriginal));
        }

        // <GIN>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
        {
            isIndiaIndirectTaxParameterMarked   = TaxParameters::checkTaxParameters_IN();
            isIndiaWHTMarked                    = TaxWithholdParameters_IN::checkTaxParameters();
        }
        // </GIN>

        super();

        boolean projForecastListPageV2FeatureEnabled = FeatureStateProvider::IsFeatureEnabled(ProjForecastListPageV2Feature::instance());
        CtrlAllForecasts.visible(!projForecastListPageV2FeatureEnabled);
        CtrlAllForecastsV2.visible(projForecastListPageV2FeatureEnabled);
        // To avoid deleting the data from table <c>LogisticsLocation</c>. It will just clear the address link to project.
        if (ProjClearAddressFromProjectFlight::instance().isEnabled())
        {
            ClearAddress.parameters('ClearLinkOnly');
        }

        ProjMutilContractLinesFeatureFormController::updateFormControlVisibity(
            element,
            [ProjTable_Type.id(), CtrlCollaborationWorkspace.id(), ProjGroupChange.id(), Proj_Type.id(), CtrlWizard.id(), TabCollaboration.id()]);

        // <GTE>
        isGTEEnabled = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();

        if (isGTEEnabled)
        {
            Tax_TaxGroupId.visible(false);
        }
        // </GTE>

        // <GTE> <GIN>
        if (TaxIntegrationUtils::isTaxInformationEnabled())
        {
            TransTaxInformation.visible(true);
        }
        // </GTE> </GIN>

        boolean resourceRequestWorkflowEnable = ResRequestHelper::isWorkflowEnabled();

        ResConfirmBtn.visible(!resourceRequestWorkflowEnable);
        if (resourceRequestWorkflowEnable)
        {
            AssignResource.text("@Res:ViewResourceRequests");
        }

        toIncludeSubProjects = true;
        projHierarchy.visible(false);
        projHierarchyGridTab.visible(false);
        if (!isConfigurationkeyEnabled(configurationKeyNum(SMASubscription)))
        {
            ctrlSMAAccrueSubscription.visible(false);
            ctrlSMAAccrueSubscription.enabled(false);
        }

        cdsLockingOptionProjectResourcing = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ProjectResourcing);
        cdsLockingOptionEditProjects = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::EditProjects);
        cdsLockingOptionProjectQuotations = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ProjectQuotations);

        // Set the visiblity of LG controls.
        enableLGFeature = BankLGFeatureChecker::checkBankLGEnabled();
        bankLGRequestForm.visible(enableLGFeature);
        projTable_DS.object(fieldNum(ProjTable, BankDocumentType)).visible(enableLGFeature);
        bankDocument.visible(enableLGFeature);

        addressController = LogisticsPostalAddressFormHandler::newParameters(projTable_ds, logisticsPostalAddress_ds);
        addressController.callerUpdateQuery(fieldNum(ProjTable,DeliveryLocation));

        zakat_SA.visible(LedgerParameters::find().IsZakatEnabled_SA);

        this.initResourceSchedulingController();
        ResourceTeamHierarchicalGrid.actionClicked += eventhandler(element.teamHierarchicalGridButtonClickHandler);

        ProjStagesSelector::setStageSelectorText(ctrlCreated, ctrlEstimated, ctrlScheduled, ctrlStartUp, ctrlUser1, ctrlUser2, ctrlUser3, ctrlReportedFinish);

        // Display warning if this project has outstanding extended fulfillment to be managed.
        if (this.hasExtendedFulfillment())
        {
            warning("@Res:FulfillmentNotificationProjTableAlert");
        }

        if (element.enableMultipleContractLinesForProject)
        {
            RevenueRec_revenueProfile.visible(true);
        }

        if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
        {
            RevenueRec_WIPProject.label("@Proj:RevProjectId");
        }
    }
}

/// <summary>
/// Determines if jump to main should be enabled.
/// </summary>
/// <returns>
/// true if jump to main should be enabled; otherwise, false.
/// </returns>
protected boolean enableJumpToMain()
{
    return ((SysFormRun::isCalledFromJumpRef(this.args(), fieldNum(ProjTable, ProjId)) && callerName != formStr(projProjectsListPage)) ||
               element.args().dataset() == tableNum(smmCampaignTable) ||
                (callerName == formStr(smmBusRelTable) ||
                 callerName == formStr(smmLeadTable) ||
                 callerName == formStr(smmOpportunityTable) ||
                 callerName == formStr(CaseDetail) ||
                 callerName == formStr(PSAResourceAssignmentAnalysis))
           ) ||
           (this.args().refField() || element.args().menuItemName() == menuitemDisplayStr(ProjTableView) ||
            element.args().menuItemName() == menuitemDisplayStr(ProjTable) ||
            element.args().menuItemName() == menuitemDisplayStr(ProjTableAuto) ||
            callerName == formStr(ProjTableCreate) ||
            callerName == formStr(JmgProjStatusListPage) ||
            (element.args().caller() && element.args().menuItemName()  == menuitemDisplayStr(ProjTableEdit))
           );
}

private void initResourceSchedulingController()
{
    resourceSchedulingController = ResResourceSchedulingController::construct();
    resourceSchedulingController.parmResourceTeamControl(ResourceTeamHierarchicalGrid);
    resourceSchedulingController.setSessionContextProjId(selectedProjId);
    resourceSchedulingController.CallerForm(element);
}

private ActionDataContract parseAction(str _action)
{
    ActionDataContract action;

    if (_action)
    {
        action = FormControlUtil::convertStrToObject(className2Id(classstr(ActionDataContract)), _action);
    }
    else
    {
        action  = ActionDataContract::construct();
    }
    return action;
}

private void teamHierarchicalGridButtonClickHandler(str _action, HierarchicalGridSessionContext _sessionContext)
{
    Enumerator          argsEnumerator;
    ActionDataContract  dataContract = this.parseAction(_action);

    argsEnumerator = dataContract.parmArgs().getEnumerator();

    switch (dataContract.parmName())
    {
        case 'openEditResourceDialog':
            argsEnumerator.moveNext();
            int64 activityResource = str2int64(argsEnumerator.current());
            resourceSchedulingController.editResourceClicked(activityResource);
            break;
    }
}

