    isInCreatemode = mode;

    return isInCreatemode;
}

/// <summary>
/// Invokes the menu function for line property
/// </summary>
public void jumpRefInvStatus()
{
    Args                args;
    ProjLineProperty    projLineProperty;

    args = new Args();

    projLineProperty = ProjLineProperty::find(projTable.projLinePropertyId(false, projTable, ''));

    args.lookupRecord(projLineProperty);
    args.caller(this);

    new MenuFunction(menuitemDisplayStr(ProjLineProperty), MenuItemType::Display).run(args);
}

/// <summary>
/// Builds the query and does a lookup on Project WIP.
/// </summary>
/// <param name="_ctrl">
/// Form String control.
/// </param>
/// <param name="_projGroupId">
/// Project group ID.
/// </param>
void lookupProjWIP(FormStringControl _ctrl, ProjGroupId _projGroupId)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProjWIPTable), _ctrl);
    Query query = new Query();

    sysTableLookup.addLookupfield(fieldNum(ProjWIPTable, ProjId));
    sysTableLookup.addLookupfield(fieldNum(ProjWIPTable, Description));

    QueryBuildDataSource queryBuildDataSource1 = query.addDataSource(tableNum(ProjWIPTable));
    QueryBuildDataSource queryBuildDataSource2 = queryBuildDataSource1.addDataSource(tableNum(ProjTable));

    QueryBuildRange queryBuildRange = queryBuildDataSource2.addRange(fieldNum(ProjTable, ProjGroupId));
    queryBuildRange.value(_projGroupId);

    queryBuildDataSource2.clearLinks();
    queryBuildDataSource2.addLink(fieldNum(ProjWIPTable, ProjId), fieldNum(ProjTable, ProjId));

    queryBuildDataSource2.joinMode(JoinMode::ExistsJoin);
    queryBuildDataSource2.relations(false);

    if (!element.enableMultipleContractLinesForProject)
    {
        QueryBuildDataSource queryBuildDataSource3 = queryBuildDataSource2.addDataSource(tableNum(ProjGroup));
        queryBuildRange = queryBuildDataSource3.addRange(fieldNum (ProjGroup, MatchingPrincip));
        queryBuildRange.value(SysQuery::valueNot(ProjMatchingPrincip::NoMatching));

        queryBuildDataSource3.joinMode(JoinMode::ExistsJoin);
        queryBuildDataSource3.relations(true);
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

