/// <summary>
/// Refreshes the <c>ProjTable</c> data source.
/// </summary>
public void refreshDataSource()
{
    if (!element.args())
    {
        return;
    }

    projTable_ds.reread();
    projTable_ds.research(true);

    if (projTreePosition)
    {
        projTable_ds.setPosition(projTreePosition);
    }
}

public void run()
{
    // If there is no selectedProjTable e.g. Report Drill-through scenario
    if (!selectedProjTable && projTable)
    {
        selectedProjTable = projTable;
        selectedProjId    = projTable.ProjId;
    }

    rootProjId = ProjTable::getRootProjId(selectedProjId);

    // If there is no caller, show the entire project hierarchy tree.
    // If a caller exists, show the tree only if the selected project is part of a hierarchy.
    if (selectedProjTable)
    {
        // When the project hierarchy tree is shown, traversing through the project ids within the hierarchy should be possible.
        projTable_ds.query().dataSourceTable(tableNum(ProjTable)).clearDynalinks();

        // Does the select project ID belong to a hierarchy?
        if (element.showProjectHierarchyGrid(selectedProjTable))
        {
            projectHierarchyGridShown = true;
            ProjHierarchyGridTab.visible(true);
            projHierarchy.visible(false);

            // This is needed as the executeQuery of the ProjTable datasource sets the cursor to tempSelectedProjTable if tree doesnt exist
            tempSelectedProjTable = selectedProjTable; 

        }
        else if( element.showProjectHierarchyTree(selectedProjTable))
        {
            projectTreeShown = true;
            element.createTree(selectedProjId);
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;
        }

        if (!(projectHierarchyGridShown || projectTreeShown))
        {
            tempSelectedProjTable = selectedProjTable;
            projHierarchy.visible(false);
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;
        }
    }

    super();

    if (projectTreeShown)
    {
        projectTree.selectNode(selectedProjId);
    }
}

void selectionChangedTimeOut()
{
    projTable_ds.treeChange(selectedProjId);
    projTreePosition = projtable_ds.getPosition();
}

protected void updateProjHierarchyGrid(boolean _execResearch = true)
{
    ProjHierarchyGridTab.visible(true);
    projectHierarchyGridShown = true;
    changecompany(selectedProjTable.DataAreaId)
    {
        //Checks if corresponding sorting record is present, if record is not present create using data helper
        ProjTableSorting localProjTableSorting = ProjTableSorting::findProjRecId(selectedProjTable.RecId);
        if (!localProjTableSorting)
        {
            ProjTableSortingDataHelper::performMigrationForProject(selectedProjId);
            localProjTableSorting = ProjTableSorting::findProjRecId(selectedProjTable.RecId);
        }
        //Check if current record is a part of existing tree, if not then load the temp table with data of current tree
        if (localProjTableSorting.RefRootProjTable != curRefRootProjTable)
        {
            ProjTableFormHierarchyGridHelper::deleteAllProjHierarchyTempTableRecords(ProjHierarchyTemp);

            if (_execResearch)
            {
                ProjHierarchyTemp_ds.init();
                ProjHierarchyTemp_ds.research();
            }
        }

        //Check if ProjHierarchyTemp cursor is on same record if grid shown, if not set the cursor to it
        ProjHierarchyTemp temp = ProjHierarchyTemp_ds.cursor();
        if(temp && temp.RefRecId != selectedProjTable.RecId)
        {
            ProjTableFormHierarchyGridHelper::expandAllParentsOfCurProjAndMakeItVisible(selectedProjTable.ProjId, ProjHierarchyTemp.getPhysicalTableName());

            if (_execResearch)
            {
                ProjHierarchyTemp_ds.research();
            }
        }
    }
}

/// <summary>
