/// <summary>
/// Enables or disables buttons.
/// </summary>
/// <remarks>
/// This method enables or disables buttons based mainly on form mode,
/// Some buttons are always enabled regardless of form mode.
/// </remarks>
void setButtonAccess()
{
    boolean         canCreateInvoiceProposal;
    ProjStatusType  projStatusType  = projTable.status();
    boolean         hasPublished    = ProjPlanVersionDetail::checkProjectHasPublishedHierarchy(projTable.ProjId);

    isRecordExternallyManaged = Project::isProjectExternallyManaged(projTable.ProjId);
    boolean         lockFieldsForEditProjects = cdsLockingOptionEditProjects && isRecordExternallyManaged;
    boolean         lockFieldsForCreateSubprojects = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::CreateSubprojects) && isRecordExternallyManaged;
    boolean         lockFieldsForProjectQuotations = cdsLockingOptionProjectQuotations && isRecordExternallyManaged;
    boolean         lockFieldsForForecasts = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::Forecasts) && isRecordExternallyManaged;
    boolean         lockFieldsForAdjustments = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::Adjustments) && isRecordExternallyManaged;

    if (!lockFieldsForAdjustments
        && projTable.Status == ProjStatus::Completed)
    {
        lockFieldsForAdjustments = true;
    }

    [showSchedule, showRetention, canAssign, hasBillingRules,
     hasBudgetedTransactions, isCtrlSalesOrderTableEnabled, collabRecordExist,
     isSupportsBudgeting, isFieldPwpThresholdPercent, canCreateInvoiceProposal, hasWBS, canCreateItemRequirement] = projTable.loadValuesServer();

    if (!ProjInvoiceTable::hasExternalFundingSources(projTable.ProjInvoiceProjId))
    {
        CtrlSalesOrders.enabled(false);
    }
    else
    {
        CtrlSalesOrders.enabled(true);
    }

    if (!element.isInCreateMode())
    {
        element.setButtonActivity();
        element.setButtonSetup();
        element.setButtonFunction();
        element.setButtonInvoice();
        element.setButtonInquiry();
        element.setButtonForecast();
        element.setButtonTransaction();
        element.setButtonExternal();
        element.setCollabWorkspaceButtonAccess();
        element.setResourceSchedulingButtonAccess();

        ctrlStages.enabled(projStatusType.ctrlStages());
        ctrlWizard.enabled(projStatusType.ctrlWizard() && !lockFieldsForEditProjects);

        subProjectNew.enabled(projTable.Format ? !lockFieldsForCreateSubprojects : false);
    }
    else
    {
        ctrlWizard.enabled(!lockFieldsForEditProjects);
        SubProjectNew.enabled(!lockFieldsForCreateSubprojects);
        projectQuotation.enabled(!lockFieldsForEditProjects);
    }

    element.setButtonBudget();
    element.setServiceManagementButtonAccess();
    element.setBankDocumentButtonAccess();

    boolean canCustomerAdvanceBeCreated = ProjStatusTypeRule::exist(projTable.Status, projTable.Type, ProjStatusRule::CreateInvoiceProposal)
        && (ProjControlDeductionCalculationFlight::instance().isEnabled() || ProjTable.type != ProjType::FixedPrice);

    CustomerAdvanceAmount.enabled(canCustomerAdvanceBeCreated);
    CustomerAdvancePercent.enabled(canCustomerAdvanceBeCreated && projTable.UseBudgeting);

    element.setProjBudgetBalancesButtonAccess();

    if (hasBillingRules)
    {
        psaProjProposalSelection.visible(false);
        psaProjProposalSelectionCLIN.visible(true);
    }
    else
    {
        psaProjProposalSelection.visible(true);
        psaProjProposalSelectionCLIN.visible(false);
    }
    ProjWBSTrackingViewButton.enabled(hasPublished);
    psaProjProposalSelection.enabled(canCreateInvoiceProposal && !element.enableMultipleContractLinesForProject);
    psaProjProposalSelectionCLIN.enabled(canCreateInvoiceProposal);
    proDebitNoteProposalSelection_BR.enabled(canCreateInvoiceProposal);

    ActionsButtonGroup.enabled(!lockFieldsForEditProjects);
    ProjTable_ds.allowDelete(!lockFieldsForEditProjects);
    ProjLinePropertySetup.enabled(!lockFieldsForEditProjects);
    CostPriceGroup.enabled(!lockFieldsForEditProjects);
    SalesPriceGroup.enabled(!lockFieldsForEditProjects);
    ActivityGroup.enabled(!lockFieldsForEditProjects);
    ItemReqGroup.enabled(!lockFieldsForEditProjects);
    ProjForecastBudgetCopy.enabled(!lockFieldsForForecasts);
    ProjForecastBudgetDelete.enabled(!lockFieldsForForecasts);
    TransferQuotationLines.enabled(!lockFieldsForProjectQuotations && !lockFieldsForForecasts);
    ProjForecastTransferFromQuotationItemReq.enabled(!lockFieldsForProjectQuotations);
    ProjForecastTransferFromWbs.enabled(!lockFieldsForForecasts);
    ValidationGroup.enabled(!lockFieldsForEditProjects);
    CreateAdjustment.enabled(!lockFieldsForAdjustments);
    ProjectTrackingGroup.enabled(!lockFieldsForEditProjects);
    UtilizationGroup.enabled(!lockFieldsForEditProjects);

    FulfillmentNotification.enabled(this.hasExtendedFulfillment());
}

/// <summary>
/// Set the enabled status of the Activity button based on access and project status.
/// </summary>
void setButtonActivity()
{
    ProjStatusType projStatusType = projTable.status();

    boolean lockFieldsForWbs = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::WorkBreakdownStructure) && isRecordExternallyManaged;

    if (projtable.Header)
    {
        ctrlActivity.enabled(false);
    }
    else
    {
        ctrlActivity.enabled(hasAccessToActivity && projStatusType.ctrlActivity() && !lockFieldsForWbs);
    }

    btnUnlinkProject.enabled(CtrlActivity.enabled() && ProjectWbsLockBox::isSynchedInMSProject(projTable.ProjId) && !lockFieldsForWbs);

    ProjectWbsOpenInMSProject.enabled(CtrlActivity.enabled() && ProjPlanVersionDetail::findProjectDraftVersion(projTable.ProjId).RecId != 0 && !lockFieldsForWbs);
}

/// <summary>
/// Set the status of project budget related buttons based on if budgetting is supported.
/// </summary>
private void setButtonBudget()
{
    boolean lockFieldsForProjectBudget = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ProjectBudget) && isRecordExternallyManaged;

    projBudget.enabled(projTable.UseBudgeting && !lockFieldsForProjectBudget);
    projYearEndProcess.enabled(projTable.UseBudgeting);
    projbudgetbalances.enabled(projTable.supportsBudgeting());
}

