/// <summary>
/// Sets the enabled or disabled state of <c>createSite</c> and <c>deleteSite</c> buttons.
/// </summary>
void setCollabWorkspaceButtonAccess()
{
    boolean lockFieldsForCollaborationWorkspace = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::CollaborationWorkspace) && isRecordExternallyManaged;
    collabRecordExist = CollabSiteLink::existCollabSiteProject(projTable.ProjId);

    createSite.enabled(!collabRecordExist && !lockFieldsForCollaborationWorkspace);
    linkSite.enabled(!collabRecordExist && !lockFieldsForCollaborationWorkspace);

    // Need for security as this is not a command menu item button
    if (!hasMenuItemAccess(menuitemDisplayStr(CollabSiteCreate), MenuItemType::Display))
    {
        createSite.enabled(false);
    }

    // Need for security as this is not a command menu item button
    if (!hasMenuItemAccess(menuitemDisplayStr(CollabSiteLink), MenuItemType::Display))
    {
        linkSite.enabled(false);
    }

    deleteSite.enabled(collabRecordExist && !lockFieldsForCollaborationWorkspace);
    unlinkSite.enabled(collabRecordExist && !lockFieldsForCollaborationWorkspace);

    if (!CollabSiteParameters::exist(CollabSiteEntityType::Project))
    {
        createSite.enabled(false);
        deleteSite.enabled(false);
    }
}

