/// <summary>
/// Enables or disables project fields.
/// </summary>
/// <remarks>
/// This method enables or disables form fields mainly depending upon form mode,
/// Some fields are always enabled or disabled regardless of form mode.
/// </remarks>
void setFieldAccess()
{
    boolean         subProjHasBudget, rootBudgetApproved, isRootProj, isStraightLine;
    ProjBudget      projBudgetTemp;
    ProjStatusType  projStatusType = projTable.status();
    boolean         localProjIdIsExternallyManaged = Project::isProjectExternallyManaged(projTable.ProjId);
    boolean         lockFieldsForEditProjects = cdsLockingOptionEditProjects && localProjIdIsExternallyManaged;
    boolean         lockFieldsForProjectResourcing = cdsLockingOptionProjectResourcing && localProjIdIsExternallyManaged;

    if (!this.isInCreateMode())
    {
        element.setRevRecFieldAccess();
        ctrlProjGroup1.allowEdit(projStatusType.ctrlProjGroup());
        ctrlCustAccount.allowEdit(projStatusType.ctrlCustAccount());
        projInvoiceProjIdTab.allowEdit(projStatusType.ctrlInvoice());
        element.setResourceSchedulingFieldAccess();
    }

    this.updateTransactionsExist();
    if (!projTable.Header && doTransactionsExist == NoYesUnchanged::Yes)
    {
        projTable_ds.object(fieldNum(ProjTable, Header)).allowEdit(false);
    }
    else
    {
        projTable_ds.object(fieldNum(ProjTable, Header)).allowEdit(!lockFieldsForEditProjects);
    }

    if (element.enableMultipleContractLinesForProject)
    {
        projTable_ds.object(fieldNum(ProjTable, ProjGroupId)).allowEdit(true);
    }

    smaServiceOrderCreate_Auto.enabled(projTable.Status != ProjStatus::Completed);
    smaServiceOrderTransfer.enabled(toIncludeSubProjects || projStatusType.ctrlSMAServiceOrderTransfer());

    projtable_ds.object(fieldNum(ProjTable, AssetId)).allowEdit(
                        (projTable.Status !=  ProjStatus::Completed) &&
                        (projTable.wipProject == "") &&
                        (projTable.Type == ProjType::Investment ||
                         projTable.Type == ProjType::Internal ||
                         projTable.Type == ProjType::Cost ||
                         (element.enableMultipleContractLinesForProject && !projTable.ProjInvoiceProjId)));

    checkBudget.enabled(!projTable.UseBudgeting);
    budgetaryControl_UseAlternateProject.enabled(projTable.UseBudgeting);
    budgetaryControl_AlternateBudgetProject.enabled(NoYes::No);
    menuItemButton.enabled(!projTable.UseAlternateProject);
    budgetaryControl_ProjBudgetInterval.enabled(projTable.UseBudgeting);
    budgetaryControl_ProjBudgetaryControlOn.enabled(projTable.UseBudgeting);
    budgetaryControl_ProjBudgetOverrunOption.enabled(projTable.UseBudgeting);
    budgetaryControl_IndependentBudget.enabled(projTable.UseBudgeting);
    if (projTable.UseBudgeting == NoYes::No)
    {
        projtable_ds.object(fieldNum(ProjTable, ProjCarryForwardRemainingBudget)).allowEdit(false);
    }
    else
    {
        isRootProj = projTable.ProjId == rootProjId;

        //enable independent budgeting if
        //1. Root project and
        //2. no budget created for descendents and
        //3. root budget hasn't been approved
        if (isRootProj)
        {
            //try and find a budget which in the hierarchy which is not root
            select firstonly RecId from projBudgetTemp
                where projBudgetTemp.RootProjId != rootProjId
                && projBudgetTemp.RootProjId like (rootProjId + #star);

            subProjHasBudget = projBudgetTemp.RecId != 0;

            if (!subProjHasBudget)
            {
                //is the budget associated with the root approved
                select firstonly RecId from projBudgetTemp
                    where projBudgetTemp.RootProjId == rootProjId
                        && projBudgetTemp.BudgetWorkflowStatus == ProjBudgetWorkflowStatus::Approved;
                rootBudgetApproved = projBudgetTemp.RecId;
            }
        }
        projtable_ds.object(fieldNum(ProjTable, ProjBudgetManagement)).allowEdit(isRootProj && !subProjHasBudget && !rootBudgetApproved);
        projtable_ds.object(fieldNum(ProjTable, ProjCarryForwardRemainingBudget)).allowEdit(true);
    }

    if (projTable.ProjCarryForwardRemainingBudget == NoYes::No)
    {
        projtable_ds.object(fieldNum(ProjTable, ProjCarryForwardNegativeBudget)).allowEdit(false);
    }
    else
    {
        projtable_ds.object(fieldNum(ProjTable, ProjCarryForwardNegativeBudget)).allowEdit(true);
    }

    // Bank document type
    projTable_ds.object(fieldNum(ProjTable, BankDocumentType)).allowEdit(projTable.status().ctrlBankDocumentType());

    //Start date and end date must be filled in for the StraightLine projects.
    isStraightLine = ProjControl::isStraightLine(projTable.WIPProject);
    projTable_ds.object(fieldNum(ProjTable, StartDate)).mandatory(isStraightLine);
    projTable_ds.object(fieldNum(ProjTable, EndDate)).mandatory(isStraightLine);

    ProjScheduleInformation.enabled(showSchedule);
    projTable_PSASchedIgnoreCalendar.enabled(projTable.psaSchedCalendarId != '' && !lockFieldsForProjectResourcing);
    custPaymRetentionTerm.visible(showRetention);
    psaRetentionRelease.visible(showRetention);
    psaCustomerPaymentRetentionInquiry.visible(showRetention);

    ProjTable_ds.object(fieldNum(ProjTable, ProjId)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, Name)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, ProjInvoiceProjId)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, CustAccount)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, Email)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, WorkerResponsibleSales)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, WorkerResponsible)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, Created)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, ProjectedStartDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, StartDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, ProjectedEndDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, ExtensionDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, EndDate)).allowEdit(!lockFieldsForEditProjects);

    ResourceSchedulingTab.enabled(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedScheduled)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedStartDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedEndDate)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedCalendarId)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedDuration)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, PSASchedIgnoreCalendar)).allowEdit(!lockFieldsForEditProjects);

    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityHourForecast)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityExpenseForecast)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityItemForecast)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityHourTrx)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityExpenseTrx)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, RequireActivityItemTrx)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, ProjLinePropertySearch)).allowEdit(!lockFieldsForEditProjects);
    projLinePropertyId.enabled(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, TimePostDetailSummary)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, MinTimeIncrement)).allowEdit(!lockFieldsForEditProjects);

    ProjTable_ds.object(fieldNum(ProjTable, ParentId)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, Format)).allowEdit(!lockFieldsForEditProjects);
    ProjTable_ds.object(fieldNum(ProjTable, Template)).allowEdit(!lockFieldsForEditProjects);

    Address.enabled(!lockFieldsForEditProjects);
    TabCollaboration.enabled(!lockFieldsForEditProjects);
}

/// <summary>
/// Set the readonly state of the <c>VendorRetion</c> field.
/// </summary>
void setFieldVendorRelation()
{
    if (projPWPVendorRetentionSetting.AccountCode == TableGroupAll::All)
    {
        projPWPVendorRetentionSetting_ds.object(fieldNum(ProjPWPVendorRetentionSetting, VendorRelation)).skip(true);
        projPWPVendorRetentionSetting_ds.object(fieldNum(ProjPWPVendorRetentionSetting, VendorRelation)).allowEdit(false);
    }
    else
    {
        projPWPVendorRetentionSetting_ds.object(fieldNum(ProjPWPVendorRetentionSetting, VendorRelation)).skip(false);
        projPWPVendorRetentionSetting_ds.object(fieldNum(ProjPWPVendorRetentionSetting, VendorRelation)).allowEdit(true);
    }
}

