/// <summary>
/// Enables or disables resource scheduling buttons.
/// </summary>
void setResourceSchedulingButtonAccess()
{
    boolean lockFieldsForProjectResourcing = cdsLockingOptionProjectResourcing && isRecordExternallyManaged;

    bgProjRole.enabled(!lockFieldsForProjectResourcing);

    boolean enableButton = projTable.Status == ProjStatus::Completed ? false : true;

    ResEditResourceBtn.enabled(enableButton);

    if (enableButton && !ProjTable.PSASchedCalendarId)
    {
        enableButton = false;
    }

    ResAddResourceBtn.enabled(enableButton);
}

/// <summary>
/// Allows or disallows editing resource scheduling fields.
/// </summary>
void setResourceSchedulingFieldAccess()
{
    boolean allowEditField = projTable.Status == ProjStatus::Completed ? false : true;

    ProjTable_PSASchedStartDate.allowEdit(allowEditField);
    ProjTable_PSASchedEndDate.allowEdit(allowEditField);
    ProjTable_PSASchedCalendarId.allowEdit(allowEditField);
    ProjTable_PSASchedDuration.allowEdit(allowEditField);

    boolean lockFieldsForProjectResourcing = cdsLockingOptionProjectResourcing && isRecordExternallyManaged;
    if (lockFieldsForProjectResourcing)
    {
        ProjTable_PSASchedIgnoreCalendar.enabled(!lockFieldsForProjectResourcing);
    }
    else
    {
        ProjTable_PSASchedIgnoreCalendar.allowEdit(allowEditField);
    }
}

/// <summary>
/// Enables or disables Revenue recogniton fields.
/// </summary>
/// <remarks>
/// This method enables or disables form fields mainly depending upon the type of project
/// </remarks>
void setRevRecFieldAccess()
{
    ProjStatusType projStatusType = projTable.status();
    revenueRec_WIPProject.allowEdit(projStatusType.ctrlControlGroup() && !projtable.AssetId);

    if (element.enableMultipleContractLinesForProject)
    {
        revenueRec_controlId.mandatory(false);
        revenueRec_projPeriodId.mandatory(false);
        wipTable_ds.object(fieldNum(ProjWIPTable, ControlId)).allowEdit(false);
        wipTable_ds.object(fieldNum(ProjWIPTable, PeriodId)).allowEdit(false);
    }
    else
    {
        if (projTable.wipProject == '')
        {
            revenueRec_controlId.mandatory(false);
            revenueRec_projPeriodId.mandatory(false);
        }
        else
        {
            revenueRec_controlId.mandatory(true);
            revenueRec_projPeriodId.mandatory(true);
        }
        if (wipTable.ControlId == '' && projTable.wipProject != '')
        {
            wipTable_ds.object(fieldNum(ProjWIPTable, ControlId)).allowEdit(true);
        }
        else
        {
            wipTable_ds.object(fieldNum(ProjWIPTable, ControlId)).allowEdit(false);
        }

        if (wipTable.PeriodId == '' && projTable.wipProject != '')
        {
            wipTable_ds.object(fieldNum(ProjWIPTable, PeriodId)).allowEdit(true);
        }
        else
        {
            wipTable_ds.object(fieldNum(ProjWIPTable, PeriodId)).allowEdit(false);
        }
    }

    revenueRecGroup.allowEdit(projStatusType.ctrlControlGroup()&& !projtable.AssetId);
}

/// <summary>
///     Enables or disables service management buttons.
/// </summary>
void setServiceManagementButtonAccess()
{
    serviceAggreement.enabled(projTable.Type != ProjType::Time);
    ctrlSMAIndexSubscription.enabled(projTable.Type != ProjType::Time);
    ctrlSMAAccrueSubscription.enabled(projTable.Type != ProjType::Time);
}

/// <summary>
/// Update the project hierarchy tree and select the new project ID.
/// </summary>
/// <param name="_projId">
/// The project ID to be selected in the hierarchy tree.
/// </param>
/// <param name="_reExecQuery">
/// Will reexecute the query on the ProjTable datasource after updating the tree hierarachy.
/// </param>
/// <remarks>
/// This method is called to update the project hierarchy tree when a new project or subproject is created or
/// a project is copied. The method shows or hides the project hierarchy tree depending on whether the project is
/// part of a hierarchy or not.
/// </remarks>
public void updateProjectTreeHierarchy(ProjId  _projId, boolean _reExecQuery = true)
{
    ProjTable   projTableLocal = ProjTable::find(_projId);

    rootProjId = ProjTable::getRootProjId(projTableLocal.ProjId);

    //
    // The project passed can be any one of these,
    // 1. A newly created project - this will not have a parent
    // 2. A sub project - this will have a parent
    // 3. A copied project which can have a parent
    // 4. A copied project which has sub projects
    // 5. A copied project which has no sub projects or a parent
    // We need to show the tree only for cases 2, 3 & 4 and hide it for 1 & 5.
    //
    if (element.showProjectHierarchyTree(projTableLocal))
    {
        if (!projectTreeShown) // The tree has not been created yet, so create it.
        {
            projectTreeShown = true;
            projHierarchy.visible(true);

            element.createTree(_projId);
            projTable_ds.query().dataSourceTable(tableNum(ProjTable)).clearDynalinks();
        }
        else if (projTableLocal.ParentId)
        {
            projectTree.createNode(projTableLocal, false, projectTree.treeNode(projTableLocal.ParentId), true);
        }
        else
        {
            projectTree.createNode(projTableLocal, false, projectTree.treeNode(projTableLocal.ParentId));
        }
        projectTree.selectNode(_projId);
    }
    else
    {
        projectTreeShown = false;
        projHierarchy.visible(false);
    }

    if (projTableLocal)
    {
        projIdCriteria.value(rootProjId + '*');
    }
    else
    {
        projIdCriteria.value('*');
        element.parentId('');
    }

    // This is needed to update the joined datasources and position the current projtable buffer as active buffer.
    if (_reExecQuery)
    {
        projTable_ds.executeQuery();
        projTable_ds.positionToRecord(projTableLocal);
    }
}

/// <summary>
/// Refreshes form data sources while maintaining the current cursor position.
/// </summary>
public void updateTempDataSourcesKeepCursor()
{
    Common cursor;
    RefTableId table = tableNum(ProjHierarchyTemp);
    cursor.RecId = ProjHierarchyTemp.RecId;

    ProjHierarchyTemp_ds.research(false);

    ProjHierarchyTemp_ds.findRecord(cursor);
}

/// <summary>
/// Determines whether transactions exist for a project.
/// </summary>
/// <param name="forceReload">
/// When set to true, will always reload the value from the database.
/// </param>
public void updateTransactionsExist(boolean forceReload = false)
{
    boolean         transactionsExist;

    if (doTransactionsExist == NoYesUnchanged::Unchanged || forceReload || ProjTable.ProjId != transactionsCheckProjId)
    {
        transactionsExist       = projTable.trxExists();
        doTransactionsExist     = transactionsExist ? NoYesUnchanged::Yes : NoYesUnchanged::No;
        transactionsCheckProjId = ProjTable.ProjId;
    }
}

/// <summary>
/// Executed on clicking the edit icon/image from AppBar
/// </summary>
/// <param name = "_taskId">Identifies the task</param>
/// <returns>Task execution result</returns>
public int task(int _taskId)
{
    int ret = super(_taskId);
    projTreePosition = projtable_ds.getPosition();

    if (projTable_ds.allowEdit() && projTable)
    {
        element.setFieldAccess();
    }
    return ret;
}

/// <summary>
/// Determines if the lookup on workers should filter to current company.
/// </summary>
/// <returns>
/// false.
/// </returns>
public boolean shouldWorkerLookupFilterToCurrentCompany()
{
    // This method is used for extensibility.
    return false;
}

/// <summary>
/// Determines if this project has extend fulfillment.
/// </summary>
/// <returns>true if this project has extend fulfillment; otherwise fase.</returns>
private boolean hasExtendedFulfillment()
{
    ActivityRecId rootActivity = ActivityFacade::findActivityByProjectId(selectedProjId);

    ResExtendFulfillmentTable extendFulfillment;
    select firstonly extendFulfillment
        where extendFulfillment.RootActivity == rootActivity;

    return extendFulfillment ? true : false;
}

/// <summary>
///     Check for the calender ID on the Project.
/// </summary>
/// <returns>
///     true if the calender ID exists on the Project; otherwise, false.
/// </returns>
public boolean validateSchedCalendarId()
{
    boolean ret = false;

    if (projTable.PSASchedCalendarId)
    {
        ret = true;
    }

    return ret;
}

/// <summary>
/// Determines whether to show the project hierarchy.
/// </summary>
/// <param name="_selectedProjTable">
///    A <c>ProjTable</c> buffer.
/// </param>
/// <returns>
///    true if the selected project is the sub-project or it is the parent of sub-project; otherwise, false.
/// </returns>
[Wrappable]
public boolean showProjectHierarchy(ProjTable _selectedProjTable)
{
    return _selectedProjTable.childExist() || _selectedProjTable.ParentId;
}

/// <summary>
/// Determines whether to show the project hierarchy by tree control.
/// </summary>
/// <param name="_selectedProjTable">
///    A <c>ProjTable</c> buffer.
/// </param>
/// <returns>
///    true if the hierarchy should be shown by tree control; otherwise, false.
/// </returns>
public boolean showProjectHierarchyTree(ProjTable _selectedProjTable)
{
    //Allow to add Check for control/Feature - provide backwards compatibility
    return element.showProjectHierarchy(_selectedProjTable);
}

/// <summary>
/// Determines whether to show the project hierarchy by grid.
/// </summary>
/// <param name="_selectedProjTable">
///    A <c>ProjTable</c> buffer.
/// </param>
/// <returns>
///    true if the hierarchy should be shown by grid control; otherwise, false.
/// </returns>
public boolean showProjectHierarchyGrid(ProjTable _selectedProjTable)
{
    return (showProjHierarchyInGridFeature && element.showProjectHierarchy(_selectedProjTable));
}

/// <summary>
/// Gets the record for which the Document Handling should be attached.
/// </summary>
/// <returns>
/// The <c>ProjTable</c> table buffer.
/// </returns>
public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (projTable && docCursor.TableId == tableNum(ProjHierarchyTemp))
    {
        docCursor = projTable;
    }

    return docCursor;
}

