public int first()
{
    int ret;

    ret = super();

    // If there is a previous record, the focus should be on the selected project id else the last selected project node.
    if (projectTreeShown && ret)
    {
        projectTree.selectNode(projTable.ProjId);
    }

    return ret;
}

public int last()
{
    int ret;

    ret = super();

    // If there is a previous record, the focus should be on the selected project id else the last selected project node.
    if (projectTreeShown && ret)
    {
        projectTree.selectNode(projTable.ProjId);
    }

    return ret;
}

void treeChange(ProjId  _selectedProjId)
{
    ProjTable   projTableTemp;

    // Check if the current row is different from the selected project?
    if (projTable.ProjId != _selectedProjId)
    {
        projTableTemp = ProjTable::find(_selectedProjId);

        element.args().lookupRecord(projTableTemp);
        projTable_ds.removeFilter();
    }
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    // Store the parent of the project being deleted. This is needed to take the focus back to the parent if a sub project is deleted.
    // Store the recId of the project being deleted. This is needed to remove that project from the tree.
    if (ret)
    {
        selectedProjId   = projTable.ParentId;
        deletedProjectId = projTable.RecId;
        deletedProjectRecId = ProjTable.RecId;
    }

    return ret;
}

public int next()
{
    int ret;

    ret = super();

    // If there is a previous record, the focus should be on the selected project id else the last selected project node.
    if (projectTreeShown && ret)
    {
        projectTree.selectNode(projTable.ProjId);
    }

    return ret;
}

