    if (projectTreeShown && ret)
    {
        projectTree.selectNode(projTable.ProjId);
    }

    return ret;
}

/// <summary>
/// Finds or creates query ranges based on <c>ProjSorting</c> table value.
/// </summary>
/// <param name = "_queryBuildDataSource">
/// <c>QueryBuildDataSource</c> on which the sorting range to be created.
/// </param>
/// <param name = "_projSorting">
/// <c>ProjSorting</c> table buffer.
/// </param>
/// <returns>
/// <c>QueryBuildRange</c> object.
/// </returns>
protected QueryBuildRange createFindProjSortingRange(QueryBuildDataSource _queryBuildDataSource, ProjSorting _projSorting)
{
    QueryBuildRange sortingIdRange;

    switch (_projSorting.SortCode)
    {
        case ProjSortValue::Sorting1:
            sortingIdRange = SysQuery::findOrCreateRange(_queryBuildDataSource, fieldNum(ProjTable,SortingId), 1, 1);
            break;
        case ProjSortValue::Sorting2:
            sortingIdRange = SysQuery::findOrCreateRange(_queryBuildDataSource, fieldNum(ProjTable,SortingId),1, 2);
            break;
        case ProjSortValue::Sorting3:
            sortingIdRange = SysQuery::findOrCreateRange(_queryBuildDataSource, fieldNum(ProjTable,SortingId),1, 3);
            break;
    }

    return sortingIdRange;
}

void createFindRanges()
{
    QueryBuildDataSource    queryBuildDataSource;
    ProjSorting             projSorting;

    queryBuildDataSource =  projTable_ds.query().dataSourceTable(tableNum(ProjTable));
    activeAll = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, Status));

    parentCriteria  = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, ParentId));
    customerAccount = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, CustAccount));
    projWIPTableCriteria  = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, wipProject));
    projInvoiceTableCriteria  = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, ProjInvoiceProjId));
    projGroupCriteria  = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, ProjGroupId));
    assetIDQBR = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, AssetId));
    projIdCriteria = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable, ProjId));

    if (element.args().dataset()==tableNum(ProjSorting))
    {
        projSorting = element.args().record() as ProjSorting;
        projSortingCriteria = this.createFindProjSortingRange(queryBuildDataSource, projSorting);
    }
    else
    {
        projSortingCriteria  = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(ProjTable,SortingId), 1, 1);
    }
}

int active()
{
    int ret;

    ret = super();

    element.setButtonAccess();
    element.setFieldAccess();

    addressController.callerActive();
    addressController.callerUpdateButtons(newAddress, editAddress, clearAddress, mapButton);

    beginningBalance.enabled(ProjTable::isBegBalRuleDefined(projTable.ProjId));

    // display collabSite URLs
    element.displayURL();

    budgetaryControl_UseAlternateProject.enabled(projTable.UseBudgeting);
    if (budgetaryControl_UseAlternateProject.valueStr() && hasBudgetedTransactions)
    {
        budgetaryControl_AlternateBudgetProject.enabled(false);
    }

    checkBudget.enabled(!projTable.UseBudgeting);
    projTable.updateBudgetControls(projTable.AlternateBudgetProject);
    menuItemButton.enabled(!projTable.UseAlternateProject);
    budgetaryControl_ProjBudgetInterval.enabled(projTable.UseBudgeting);
    budgetaryControl_ProjBudgetaryControlOn.enabled(projTable.UseBudgeting);
    budgetaryControl_ProjBudgetOverrunOption.enabled(projTable.UseBudgeting);

    if (projTable.Format)
    {
        element.parentId(projTable.ProjId);
    }

    vendorAgreementGrid_PwpThresholdPercent.visible(isFieldPwpThresholdPercent);
    percent_Percent.enabled(!hasBillingRules);

    selectedProjId = projTable.ProjId;
    selectedProjTable = projTable;

    if (projectTreeShown && !showProjHierarchyInGridFeature)
    {
        projectTree.selectNode(selectedProjId);
    }
    else if(element.showProjectHierarchyGrid(selectedProjTable))
    {
        element.updateProjHierarchyGrid();
    }
    else
    {
        ProjHierarchyGridTab.visible(false);
        projectHierarchyGridShown = false;
    }

    return ret;
}

public void create(boolean _append = false)
{
    Args            args;
    FormRun         formCreate;
    ProjTable       newProjTable;

    createprojId = '';

    args = new Args();
    args.name(formStr(ProjTableCreate));
    args.caller(element);
    args.record(projTable);
    args.openMode(OpenMode::New);

    formCreate = classfactory.formRunClass(args);
    formCreate.init();
    formCreate.run();
    formCreate.wait();

    if (element.createprojId() && formCreate.closedOk())
    {
        newProjTable = ProjTable::find(createprojId);
        element.setActive();
        selectedProjId = createprojId;
        rootProjId = createprojId;
        selectedProjTable = newProjTable;

        projTable.data(newProjTable);
        projTable_ds.reread();

        if (!showProjHierarchyInGridFeature)
        {
            //create and show tree
            element.updateProjectTreeHierarchy(selectedProjId, true);
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;
        }
        else if (element.showProjectHierarchyGrid(selectedProjTable))
        {
            changecompany(selectedProjTable.DataAreaId)
            {
                //Check if ProjTableSorting record exists for current record, if it doesnt generate the ProjTableSorting records
                ProjTableSorting localProjTableSorting = ProjTableSorting::findProjRecId(selectedProjTable.RecId);
                if (!localProjTableSorting)
                {
                    ProjTableSortingDataHelper::performMigrationForProject(selectedProjId);
                    localProjTableSorting = ProjTableSorting::findProjRecId(selectedProjTable.RecId);
                }
                else
                {
                    //If ProjTableSorting record exists for current record, check if it belongs to the currently loaded tree
                    if (localProjTableSorting.RefRootProjTable != curRefRootProjTable)
                    {
                        //If ProjTableSorting record doesnt belong to the currently loaded tree, initialize the tree again
                        ProjTableFormHierarchyGridHelper::deleteAllProjHierarchyTempTableRecords(ProjHierarchyTemp);
                        ProjHierarchyTemp_ds.init();
                        ProjHierarchyTemp_ds.research();
                    }
                    else
                    {
                        ProjTableFormHierarchyGridHelper::addHierarchyTempTableRecord(ProjHierarchyTemp, selectedProjTable);
                        projHierarchyTemp_ds.research();
                    }
                }
            }
        }
        else
        {
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;
            projHierarchy.visible(false);

            ProjTable_ds.executeQuery();
            ProjTable_ds.positionToRecord(newProjTable);
        }
    }
}

public void delete()
{
    super();

    // If the hierarchy tree is visible, update it by removing the deleted project node and select the parent project node.
    if (projectTreeShown && selectedProjId)
    {
        projTable_ds.positionToRecord(ProjTable::find(selectedProjId));
        projTable_ds.refresh();

        projectTree.deleteNode(deletedProjectId);
        projectTree.selectNode(selectedProjId);
    }
    else if (projectHierarchyGridShown && selectedProjId)
    {
        selectedProjTable = ProjTable::find(selectedProjId);
        element.args().lookupRecord(selectedProjTable);
        projtable_ds.removeFilter();
        ProjTableFormHierarchyGridHelper::deleteProjHierarchyTempTableRecord(ProjHierarchyTemp, deletedProjectRecId);
        ProjHierarchyTemp_ds.research();
        ProjHierarchyTemp_ds.positionToRecord(ProjHierarchyTemp.findByReferenceV2(ProjTable::find(selectedProjId).RecId));
    }   
}

void executeQuery()
{
    this.createFindRanges();

    if (!jumpToMain)
    {
        if (element.parentId())
        {
            parentCriteria.value(element.parentId()+'*');
        }
        else
        {
            parentCriteria.value(SysQuery::valueUnlimited());
        }
    }
    else
    {
        // Set projIdCriteria so that it will load current project and its subprojects. See createFindRanges() method.
        // Need this when creating new project on top of a newly created project form.
        if (projectTreeShown || showProjHierarchyInGridFeature)
        {
            projIdCriteria.value(rootProjId+'*');
        }

        // ProjTable datasource sets the cursor to tempSelectedProjTable if tree doesnt exist
        if (!projectTreeShown && tempSelectedProjTable)
        {
            element.args().lookupRecord(tempSelectedProjTable);
            tempselectedProjTable = null;
        }
    }

    super();

    if (!selectedProjTable)
    { 
        selectedProjTable = ProjTable_DS.cursor();
        selectedProjId = selectedProjTable.ProjId;

        if (showProjHierarchyInGridFeature)
        {
            ProjHierarchyTemp_ds.init();
            ProjHierarchyTemp_ds.research();
        }
    }

    projTreePosition = projtable_ds.getPosition();
    element.setButtonAccess();
}

public void init()
{
    Query                   query;
    QueryBuildDataSource    queryProjTable;

    if (!jumpToMain)
    {
        projTable_ds.autoQuery(false);

        query = new Query();

        queryProjTable = query.addDataSource(tableNum(ProjTable));
        queryProjTable.name(projTable_ds.name());

        this.query(query);
    }

    super();
}

        public void linkActive()
        {
            ProjTable           localProjTable;
            CustTable           custTable;
            ProjWIPTable        projWIPTable;
            ProjInvoiceTable    projInvoiceTable;
            ProjGroup           projGroup;
            ProjSorting         projSorting;
            AssetTable          localAssetTable;
            ProjGrantView       projGrantViewCaller;
            FormRun             formRunObj;

            this.createFindRanges();

            if (!jumpToMain && (element.args().dataset() == tableNum(ProjTable)) && (element.args().caller() == null || element.args().caller().name() != formStr(projProjectsListPage)) )
            {
                localProjTable  = element.args().record() as ProjTable;
                parentCriteria.value(localProjTable.ProjId);

                if (localProjTable.Format)
                {
                    element.parentId(localProjTable.ProjId);
                }
            }

            customerAccount.value('');
            projWIPTableCriteria.value('');
            projInvoiceTableCriteria.value('');
            projGroupCriteria.value('');
            projSortingCriteria.value('');
            projIdCriteria.value('');

            if (element.args())
            {
                switch (element.args().dataset())
                {
                    // Is the form called from the activities form
                    case tableNum(smmActivities) :
                        smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(ProjTable)), element.args().record() as smmActivities );
                        break;

                    case tableNum(CustTable):
                        custTable = element.args().record() as CustTable;
                        customerAccount.value(custTable.AccountNum);
                        break;

                    case tableNum(ProjWIPTable):
                        projWIPTable = element.args().record() as ProjWIPTable;
                        projWIPTableCriteria.value(projWIPTable.ProjId);
                        break;

                    case tableNum(ProjInvoiceTable):
                        projInvoiceTable = element.args().record() as ProjInvoiceTable;
                        projInvoiceTableCriteria.value(projInvoiceTable.ProjInvoiceProjId);
                        break;

                    case tableNum(ProjGroup):
                        projGroup = element.args().record() as ProjGroup;
                        projGroupCriteria.value(projGroup.ProjGroupId);
                        break;

                    case tableNum(ProjSorting):
                        projSorting = element.args().record() as ProjSorting;
                        projSortingCriteria.value(projSorting.SortingId);
                        break;
                    case tableNum(AssetTable):
                        localAssetTable = element.args().record() as AssetTable;
                        assetIDQBR.value(localAssetTable.AssetId);
                        break;

                    case tableNum(ProjGrantView):
                        projGrantViewCaller = element.args().record() as ProjGrantView;
                        formRunObj  =   element.args().caller();
                        if (formRunObj.name() == formStr(ProjGrant))
                        {
                            projIdCriteria.value(projGrantViewCaller.ProjId);
                        }
                        break;

                    case tableNum(ProjTable):
						projIdCriteria.value(this.getProjIdCriteria());
                        break;
                }
            }

            super();
        }

/// <summary>
/// Determines the project id criteria to filter by. 
/// </summary>
/// <returns>
/// ProjId to filter the projIdCriteria with.
/// </returns>
protected ProjId getProjIdCriteria()
{
	return ProjTable::getRootProjId(selectedProjId) + '*';
}

void write()
{
    // Check if dimensions have been modified and if sub projects exists for this project.
    if (!DimensionDefaultFacade::areEqual(projTable.orig().DefaultDimension, projTable.DefaultDimension) &&
        ProjTable::child(projTable.ProjId))
    {
        projTable.editSubProj(fieldNum(ProjTable, DefaultDimension), projTable.DefaultDimension, projTable.orig().DefaultDimension);
    }

    //---update sub-projects' calendar id
    if (projTable_PSASchedCalendarId.enabled() && projTable.psaSchedCalendarId != "")
    {
        projTable.updateSubProjectCalendar();
    }

    boolean updatedWorkerResponsible = (projTable.WorkerResponsible && projTable.orig().WorkerResponsible != projTable.WorkerResponsible);

    super();

    element.isInCreateMode(false);

    // Adding project manager to project as a team member.
    if (updatedWorkerResponsible)
    {
        PSAProjSchedRole::addTeamMemberForProject(projTable.ProjId, projTable.WorkerResponsible,
            projTable.PSASchedStartDate, projTable.PSASchedEndDate);
    }
    projTable_DS.reread();
}

/// <summary>
/// Select a different project.
/// </summary>
public void selectionChanged()
{
    super();
    selectedProjTable = projTable;
    ApplicationBar.enabled(null != selectedProjTable);
    //refreshes the resource scheduling view as well.
    resourceSchedulingController.setSessionContextProjId(ProjTable.ProjId, true);
}

/// <summary>
/// Refresh project content.
/// </summary>
/// <param name = "_retainPosition">Retain project record position.</param>
public void research(boolean _retainPosition = false)
{
    super(_retainPosition);
    //refreshes the resource scheduling view as well.
    resourceSchedulingController.setSessionContextProjId(ProjTable.ProjId, true);
}

/// <summary>
/// Get the planned effort for the project
/// Get planned effort from total task effort when WBS exists
/// Get planned effort from total hard booked hours when no WBS was created for the project
/// </summary>
display real getPlannedEffort()
{
    #TimeConstants

    if (!hasWBS)
    {
        ResActivityView activity;
        ResActivitiesBookedView activityBooked;

        select activity
            where activity.ProjectId == ProjTable.ProjId
        join Capacity from activityBooked
            where activityBooked.Activity == activity.Activity
            && activityBooked.CommitType == ResCommitType::HardBook;

        return activityBooked.Capacity / #SecondsPerHour;
    }

    return ProjTable.PSASchedEffort;
}

