public void jumpRef()
{
    //  Handling the jumpRef separately as ProjFundingSource is not a datasource on the form
    //  Without this, the clicking on the funding source value will throw an error
    //  do not call super in this case.
    //  super();

    ProjFundingSource projFundingSourceLocal = ProjFundingSource::find(element.ProjTable.DefaultFundingSource);

    //setup menu item call for jumpRef
    Args args = new Args();
    args.caller(element);
    args.record(projFundingSourceLocal);
    args.lookupRecord(projFundingSourceLocal);
    args.copyCallerQuery(CopyCallerQuery::No);
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(projFundingSourceDetail), MenuItemType::Display);
    menuFunction.run(args);
}

