    qbds.clearLinks();
    qbds.clearRanges();

    SysQuery::findOrCreateRange(qbds, fieldNum(ProjStageTable, Language)).value(currentUserLanguage());
    SysQuery::findOrCreateRange(qbds, fieldnum(ProjStageTable, Status)).value(queryValue(ProjTable.Status));
    super();
}

/// <summary>
/// Gets the project name to display in the hierarchical grid
/// ProjId:Name
/// </summary>
/// <param name = "_record">ProjHierarchyTemp record</param>
/// <returns>Project name to display for the hierarchical grid record</returns>
display ProjIdAndName getDisplayProjName(ProjHierarchyTemp _record)
{
    const str tripleSpaceCharacter = '   ';
    str leadingSpace = '';
    ProjTableSorting localSort = ProjTableSorting::findRecId(_record.RefSortRecId);

    for (int i = 1; i < localSort.ProjHierarchyLevel; i++)
    {
        leadingSpace += tripleSpaceCharacter;
    }
    ProjIdAndName displayProjName = '';
    ProjTable localProj = ProjTable::findRecId(_record.RefRecId);
    if (localProj)
    {
        displayProjName = leadingSpace + localProj.ProjId + ' : ' + localProj.Name;
    }
    return displayProjName;
}

/// <summary>
/// Gets the state image for the record in the hierarchical grid
/// Image indicates if the record is expanded or not
/// </summary>
/// <param name = "_record">ProjHierarchyTemp record project</param>
/// <returns>Image to display for hierarchical grid record</returns>
display container getProjStateImage(ProjHierarchyTemp _record)
{
    container imageContainer;

    ProjTableSorting localSort = ProjTableSorting::findRecId(_record.RefSortRecId);
    if (localSort.EndNode)
    {
        return imageContainer;
    }

    // Otherwise, just make image based on expand/collapse state of temp table record
    if (_record.Expand)
    {
        imageContainer = ImageReference::constructForSymbol(ImageReferenceSymbol::ChevronDown, "@ProjPlan:Expanded").pack();
    }
    else
    {
        imageContainer = ImageReference::constructForSymbol(ImageReferenceSymbol::ChevronUp, "@ProjPlan:Collapsed").pack();
    }

    return imageContainer;

}

public void init()
{
    if (showProjHierarchyInGridFeature && selectedProjId != "")
    {
        changecompany(selectedProjTable.DataAreaId)
        {
            ProjTable localProjTable = ProjTable::find(selectedProjId);
            ProjTableSorting localProjTableSorting = ProjTableSorting::findProjRecId(localProjTable.RecId);
            if (!localProjTableSorting)
            {
                //ProjTableSorting does not have the record, create record in ProjTableSorting
                ProjTableSortingDataHelper::performMigrationForProject(localProjTable.ProjId);
                localProjTableSorting = ProjTableSorting::findProjRecId(localProjTable.RecId);
            }
            curRefRootProjTable = localProjTableSorting.RefRootProjTable;
            ProjTableFormHierarchyGridHelper::initializeProjHierarchyTemp(curRefRootProjTable, ProjHierarchyTemp.getPhysicalTableName(), localProjTable.ProjId);
            ProjHierarchyTemp.reread();    
        }
    }
    super();
}

public void executeQuery()
{
    if (showProjHierarchyInGridFeature)
    {
        Query query = this.query();

        // Set up temp table datasource
        QueryBuildDataSource hierarchyTempDS = SysQuery::findOrCreateDataSource(query, tableNum(ProjHierarchyTemp));
        SysQuery::findOrCreateRange(hierarchyTempDS, fieldnum(ProjHierarchyTemp, Visible)).value(SysQuery::value(FalseTrue::True));

        // Set up sorting table datasource
        QueryBuildDataSource sortDS = SysQuery::findOrCreateDataSource(query, tableNum(ProjTableSorting));

        // Set up relation: ProjTableSorting.RecId == ProjHierarchyTemp.RefSortRecId
        sortDS.clearLinks();
        sortDS.addLink(fieldNum(ProjHierarchyTemp, RefSortRecId), fieldNum(ProjTableSorting, RecId), hierarchyTempDS.name());

        // Set up ordering with ProjTableSorting
        query.clearOrderBy();

        for (int i = 1; i <= 10; i++)
        {
            sortDS.addOrderByField(fieldName2Id(tableNum(ProjTableSorting), 'HierarchyLevel' + int2Str(i)), SortOrder::Ascending);
        }

        QueryRun queryRun = new QueryRun(query);
        this.queryRun(queryRun);

        super();

        ProjTable localSelect = ProjTable::find(selectedProjId);
        if (localSelect)
        {
            ProjHierarchyTemp temp = ProjHierarchyTemp.findByReferenceV2(localSelect.RecId);
            if (temp)
            {
                element.args().lookupRecord(temp);
                ProjHierarchyTemp_ds.positionToRecord(temp);
            }
        }
    }
}

/// <summary>
/// Select a different project.
/// </summary>
public void selectionChanged()
{
    super();
    ProjHierarchyTemp temp = ProjHierarchyTemp_DS.cursor();
    changecompany(temp.DataAreaId)
    {
        selectedProjTable = ProjTable::findRecId(temp.RefRecId);
        selectedProjId = selectedProjTable.ProjId;
    }

    //positiontorecord is extremely slow when the temptable cursor is set to non root
    //The temp table needs to be set to non root when we directly need to open a suproject
    //Hence using lookuprecord and remove filter to move around in the details page
    if (DetailsPanel.isActivePage())
    {
        element.args().lookupRecord(selectedProjTable);
        ProjTable_ds.removeFilter();
    }
    else
    {
        //Here using positionToRecord as we want to select the project from list of projects (Title group tab) 
        ProjTable_ds.positionToRecord(selectedProjTable);
    }
}

void clicked()
{
    if (!element.validateSchedCalendarId())
    {
        throw error ("@PSA11087");
    }

    if (showProjHierarchyInGridFeature)
    {
        Args args = new Args();
        args.menuItemName(menuItemDisplayStr(ProjTableCreateSubproject));
        args.record(projTable);
        args.caller(element);
        args.name(formstr(ProjTableCreate));
        args.parmEnumType(enumNum(ProjTableCreateLevel));
        args.parmEnum(ProjTableCreateLevel::Subproject);
        args.openMode(OpenMode::New);

        FormRun projTableCreate = classFactory.formRunClass(args);
        projTableCreate.init();
        projTableCreate.run();
        projTableCreate.wait();

        if (projTableCreate.closedOk())
        {
            ProjTable createdProj = ProjTable::find(createprojId);
            ProjTable parentProj = ProjTable::find(createdProj.ParentId);

            ProjTableFormHierarchyGridHelper::addHierarchyTempTableRecord(ProjHierarchyTemp, createdProj);
            ProjTableFormHierarchyGridHelper::expandProject(parentProj, ProjTableSorting::findProjRecId(parentProj.RecId), ProjHierarchyTemp.getPhysicalTableName());

            selectedProjTable = createdProj;
            selectedProjId = createprojId;
            DetailsPanel.activatePage();
            element.args().lookupRecord(selectedProjTable);
            ProjTable_ds.removeFilter();
            projHierarchyTemp_ds.research();
            ProjHierarchyTemp_ds.positionToRecord(ProjHierarchyTemp.findByReferenceV2(createdProj.RecId));
        }
    }
    else
    {
        super();
    }
}

void clicked()
{
    container   selectedProjectAncestors,
                copiedProjectAncestors;

    ProjTable   copiedProjTable;

    super();

    if (copiedProjId)
    {
        copiedProjTable = ProjTable::find(copiedProjId);

        selectedProjectAncestors = ProjTable::ancestors(selectedProjTable.ProjId);
        copiedProjectAncestors   = ProjTable::ancestors(copiedProjId);

        // If ancestors of both selected and copied projects are not same, then build the tree.
        if (selectedProjectAncestors && copiedProjectAncestors &&
           (conPeek(selectedProjectAncestors, conLen(selectedProjectAncestors)) != conPeek(copiedProjectAncestors, conLen(copiedProjectAncestors))))
        {
            projectTreeShown = false; // This will force the tree to be built again.
        }

        // If the project was created using template then tree should be rebuilt.
        if (copiedFromTemplateProjId)
        {
            projectTreeShown = false; // This will force the tree to be built again.
        }

        if (!showProjHierarchyInGridFeature)
        {
            element.updateProjectTreeHierarchy(copiedProjId);
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;
        }
        else if (element.showProjectHierarchyGrid(copiedProjTable))
        {
            selectedProjTable = copiedProjTable;
            selectedProjId = copiedProjId;
            element.args().lookupRecord(selectedProjTable);
            ProjTable_ds.removeFilter();
            projHierarchyTemp_ds.research();
            ProjHierarchyTemp_ds.positionToRecord(ProjHierarchyTemp.findByReferenceV2(selectedProjTable.RecId));
        }
        else
        {
            projHierarchy.visible(false);
            ProjHierarchyGridTab.visible(false);
            projectHierarchyGridShown = false;

            ProjTable_ds.executeQuery();
            ProjTable_ds.positionToRecord(copiedProjTable);
        }
    }
}

