/// <summary>
/// Method to expand and collapse on clicking state
/// </summary>
public void clicked()
{
    selectedProjTable = ProjTable::findRecId(ProjHierarchyTemp.RefRecId);
    selectedProjId = selectedProjTable.ProjId;

    if (ProjHierarchyTemp.Expand == FalseTrue::False)
    {
        ProjTableFormHierarchyGridHelper::expandProject(
            selectedProjTable,
            ProjTableSorting,
            ProjHierarchyTemp.getPhysicalTableName());
    }
    else if (ProjHierarchyTemp.Expand == FalseTrue::True)
    {
        ProjTableFormHierarchyGridHelper::collapseProject(
            selectedProjTable,
            ProjTableSorting,
            ProjHierarchyTemp.getPhysicalTableName());
    }
    element.updateTempDataSourcesKeepCursor();
    super();
}

