/// <summary>
/// Book activity resource hours.
/// </summary>
public void clicked()
{
    #TimeConstants

    super();

    ProjTable project = ProjTable_ds.cursor();
    ActivityRecId activity = ActivityFacade::findActivityByProjectId(project.ProjId);
    resourceSchedulingController.activity(activity);

    utcdatetime teamStartDT = DateTimeUtil::newDateTime(ActivityFacade::getEarliestTeamMemberPlannedStartDate(activity), 0);
    utcdatetime teamEndDT = DateTimeUtil::newDateTime(ActivityFacade::getLatestTeamMemberPlannedEndDate(activity), #LastSecondOfTheDay);

    resourceSchedulingController.startDateTime(teamStartDT);
    resourceSchedulingController.endDateTime(teamEndDT);
    resourceSchedulingController.bookResources(selectedProjId);
}

public void clicked()
{
    ProjTable project = ProjTable_ds.cursor();
    ActivityRecId activity = ActivityFacade::findActivityByProjectId(project.ProjId);
    resourceSchedulingController.activity(activity);
    resourceSchedulingController.assignResource();
}

