public boolean modified()
{
    ProjLinePropertyId projLinePropertyIdOrig = (select projLinePropertySetup where projLinePropertySetup.ProjCode          == TableGroupAll::Table  &&
                                                                                    projLinePropertySetup.ProjRelation      == projTable.ProjId      &&
                                                                                    projLinePropertySetup.CategoryCode      == TableGroupAll::All    &&
                                                                                    projLinePropertySetup.CategoryRelation  == '').LinePropertyId;
    boolean ret = super();

    if (!projTable.editSubProj(0, this.valueStr(), projLinePropertyIdOrig, 0, true))
    {
        projTable.projLinePropertyId(true, projTable, this.valueStr());
    }

    return ret;
}

public void clicked()
{
    element.updateTransactionsExist(true);
    if (doTransactionsExist == NoYesUnchanged::Yes)
    {
        projTable.Header = false;
        projTable_ds.object(fieldNum(ProjTable, Header)).allowEdit(false);
        error("@SYS4010262");
    }
    else
    {
        super();
    }
}

public void lookup()
{
    element.lookupProjWIP(this, projTable.ProjGroupId);
}

/// <summary>
/// Handle estimate project Id selection.
/// </summary>
/// <returns>True when operation is successful. Otherwise, false.</returns>
public boolean modified()
{
    boolean ret = true;

    ProjId estimateProjectId = RevenueRec_WIPProject.text();

    ProjTable projTableLocal = ProjTable::find(estimateProjectId); // Retrieves the project for a given estimate project ID in ProjTable.

    if (!projTableLocal)
    {
        ret = checkFailed(strFmt(ProjTable::txtNotExist(), estimateProjectId));
    }

    if (ret && !ProjWIPTable::exist(estimateProjectId))
    {
        str dialogLabel;
        if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
        {
            dialogLabel = strFmt("@Proj:EstimateProjectNotExistsV2", estimateProjectId);
        }
        else
        {
            dialogLabel = strFmt("@Proj:EstimateProjectNotExists", estimateProjectId);
        }

        DialogButton dialogButton = Box::yesNo(dialogLabel, DialogButton::Yes);

        if (dialogButton == DialogButton::Yes)
        {
            ProjControlId controlId;
            ProjPeriodId periodId;

            if (element.enableMultipleContractLinesForProject)
            {
                ProjRevenueProfile projRevenueProfile = ProjRevRecHelper::getRevProfile(projTableLocal, ProjContractBillingMethod::FixedPrice);
                controlId = projRevenueProfile.CostTemplate;
                periodId = projRevenueProfile.PeriodId;
            }
            else
            {
                ProjGroup projGroup = ProjGroup::find(projTableLocal.ProjGroupId);
                controlId = projGroup.ControlId;
                periodId = projGroup.PeriodId;
            }

            ProjWIPTable projWIPTable;
            projWIPTable.ControlId = controlId;
            projWIPTable.PeriodId = periodId;
            projWIPTable.Description = projTableLocal.Name;
            projWIPTable.ProjId = estimateProjectId;

            if (projWIPTable.validateWrite())
            {
                projWIPTable.doInsert(); // Using doinsert() because the insert() is updating the estimate project Id in projTable for the selected WIP project records.
            }
        }
    }

    ret = ret && super();

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret && !ProjControl::exist(revenueRec_controlId.text()))
    {
        ret = checkFailed(strFmt("@SYS76877",revenueRec_controlId.text()));
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret && !ProjPeriodTable::exist(revenueRec_projPeriodId.text()))
    {
        ret = checkFailed(strFmt("@SYS76877",revenueRec_projPeriodId.text()));
    }

    return ret;
}

public void clicked()
{
    LogisticsLocationEntity location;
    super();
    projTable.editSubProj(fieldNum(ProjTable, DeliveryLocation), projTable.DeliveryLocation, 0);

    location            = LogisticsLocationEntity::constructFromLocationRecId(projTable.DeliveryLocation);
    projTable.DlvName   = location.getPostalAddress().displayLocationDescription();
    projTable.doUpdate();
    projTable.reread();
}

public void clicked()
{
    LogisticsLocationEntity location;
    super();
    projTable.editSubProj(fieldNum(ProjTable, DeliveryLocation), projTable.DeliveryLocation, 0);

    location            = LogisticsLocationEntity::constructFromLocationRecId(projTable.DeliveryLocation);
    projTable.DlvName   = location.getPostalAddress().displayLocationDescription();
    projTable.doUpdate();
    projTable.reread();
}

public void clicked()
{
    if (projTable.editSubProj(fieldNum(ProjTable, DeliveryLocation), 0, projTable.DeliveryLocation, 0))
    {
        projTable.DlvName = "";
        super();
    }
}
