public void close()
{
    super();

    templateProjId = oldProjId.text();
    wizardProjName = projCreateNameMethod.selection();

    xSysLastValue::saveLast(this);
}

public void closeOk()
{
    Object      callerForm;
    FormName    callerFormName;

    super();

    callerForm = sysWizard.getArgs().caller();
    callerFormName = callerForm.name();
    if (callerForm)
    {
        // Is the project wizard called from the agreement wizard?
        if (callerFormName == formStr(SMAAgreementWizard))
        {
            if (formHasMethod(callerForm, identifierStr(setProject)))
            {
                // Transfer the project id of the new project back to the agreement wizard
                callerForm.setProject(newProjIdEdit, projTable_ProjInvoiceProjId.text());
            }
        }

        // Is the project wizard is called from the project details form?
        if (callerFormName == formStr(ProjTable))
        {
            if (formHasMethod(callerForm, identifierStr(setCopiedProject)))
            {
                // Transfer the project id of the new project back to the project details form.
                callerForm.setCopiedProject(newProjIdEdit);
            }

            if (formHasMethod(callerForm, identifierStr(setCopiedFromTemplateProjId)))
            {
                // Transfer the project id of the new project back to the project details form.
                callerForm.setCopiedFromTemplateProjId(templateProjId);
            }
        }
    }
}

edit ProjId editNewprojId(boolean _set, ProjId _projId)
{
    boolean validate;
    ProjTable projTable;

    if (_set)
    {
        projTable.ParentId = parentProjId.text();
        validate = projTable.validateProjId(_projId);

        if (!validate)
        {
            newProjId.text(projTable.ProjId);
            Box::stop(strFmt("@SYS62416", parentProjId.text(), ProjTable::find(parentProjId.text()).Format), "@SYS53207", "");
        }
        else
        {
            this.setNewProjId(_projId);
            element.setNext();
        }
    }

    return newProjIdEdit;
}

edit ProjId editParentProjId(boolean set, ProjId projId)
{
    if (set)
    {
        this.setParentProjId(projId);
    }

    return parentProjIdEdit;
}

